// PREPARAR DATOS COMPLETOS PARA CREAR TAREA Y HORAS
let datos = $json;

// ✅ Normalizar si $json viene como array (muy común en n8n)
if (Array.isArray(datos) && datos.length > 0) {
  console.log('⚠️ $json viene como array, extrayendo primer elemento');
  datos = datos[0];
}

console.log('🔧 DATOS DESDE SWITCH ACCIONES (TAREA):', JSON.stringify(datos, null, 2));

// IMPORTANTE: Obtener datos desde el nodo que SÍ tiene la información
let datosFromAI;
try {
  datosFromAI = $('Preparar Datos AI Agent').first().json;
  console.log('✅ Datos obtenidos desde Preparar Datos AI Agent');
  console.log('📥 Datos AI Agent:', JSON.stringify(datosFromAI, null, 2));

  // Si los datos llegan como array, extraer el primer elemento
  if (Array.isArray(datosFromAI) && datosFromAI.length > 0) {
    console.log('⚠️ Datos AI Agent recibidos como array, extrayendo primer elemento');
    datosFromAI = datosFromAI[0];
  }

  datos = datosFromAI;
} catch (e) {
  console.error('❌ Error obteniendo datos desde Preparar Datos AI Agent:', e.message);
  console.log('📋 Usando datos del Switch');
}

console.log('🔧 === PREPARAR DATOS COMPLETOS TAREA ===');
console.log('📥 Datos recibidos:', JSON.stringify(datos, null, 2));

// Obtener datos del Router de Acciones anterior para asegurar información completa
let datosRouter = {};
try {
  datosRouter = $('Router de Acciones FIXED').first().json;

  // ✅ Normalizar si router viene como array
  if (Array.isArray(datosRouter) && datosRouter.length > 0) {
    console.log('⚠️ Router viene como array, extrayendo primer elemento');
    datosRouter = datosRouter[0];
  }

  console.log('✅ Datos del Router obtenidos exitosamente');
  console.log('🔍 Datos del Router:', JSON.stringify(datosRouter, null, 2));
} catch (e) {
  console.log('⚠️ No se pudieron obtener datos del Router de Acciones:', e.message);
  console.log('📋 Usando solo datos del Switch/AI');
}

// COMBINAR Y VALIDAR DATOS DE MÚLTIPLES FUENTES
const datosCompletos = {
  chat_id: datos.chat_id || datosRouter.chat_id,
  user_id: datos.user_id || datosRouter.user_id,
  user_name: datos.user_name || datosRouter.user_name,
  uid: datos.uid || datosRouter.uid,
  mensaje_original: datos.mensaje_original || datosRouter.mensaje_original,

  accion: datos.accion || 'crear_tarea_y_horas',

  // 👇 proyecto_id puede venir vacío; lo resolverás después en Odoo
  proyecto: datos.datos_pendientes?.proyecto || datos.proyecto || datosRouter.proyecto || datosRouter.datos_pendientes?.proyecto,
  proyecto_id: datos.datos_pendientes?.proyecto_id || datos.proyecto_id || datosRouter.proyecto_id || datosRouter.datos_pendientes?.proyecto_id,
  tarea: datos.datos_pendientes?.tarea || datos.tarea || datosRouter.tarea || datosRouter.datos_pendientes?.tarea,
  horas: datos.datos_pendientes?.horas ?? datos.horas ?? datosRouter.horas ?? datosRouter.datos_pendientes?.horas,
  descripcion: datos.datos_pendientes?.descripcion || datos.descripcion || datosRouter.descripcion || datosRouter.datos_pendientes?.descripcion,

  datos_pendientes: {
    proyecto: datos.datos_pendientes?.proyecto || datos.proyecto || datosRouter.proyecto || datosRouter.datos_pendientes?.proyecto,
    proyecto_id: datos.datos_pendientes?.proyecto_id || datos.proyecto_id || datosRouter.proyecto_id || datosRouter.datos_pendientes?.proyecto_id,
    tarea: datos.datos_pendientes?.tarea || datos.tarea || datosRouter.tarea || datosRouter.datos_pendientes?.tarea,
    horas: datos.datos_pendientes?.horas ?? datos.horas ?? datosRouter.horas ?? datosRouter.datos_pendientes?.horas,
    descripcion: datos.datos_pendientes?.descripcion || datos.descripcion || datosRouter.descripcion || datosRouter.datos_pendientes?.descripcion,
    uid: datos.uid || datosRouter.uid,
    tipo_creacion: 'solo_tarea_horas',
    ...datos.datos_pendientes
  }
};

console.log('🎯 DATOS COMPLETOS PREPARADOS (TAREA):');
console.log('   📋 Proyecto:', datosCompletos.proyecto);
console.log('   🆔 Proyecto ID:', datosCompletos.proyecto_id);
console.log('   ✅ Tarea:', datosCompletos.tarea);
console.log('   ⏰ Horas:', datosCompletos.horas);
console.log('   📝 Descripción:', datosCompletos.descripcion);

// ✅ VALIDACIÓN FINAL: NO exigir proyecto_id aquí
if (!datosCompletos.tarea || datosCompletos.horas == null || (!datosCompletos.proyecto && !datosCompletos.proyecto_id)) {
  console.error('❌ VALIDACIÓN FALLIDA - Faltan datos críticos después de preparación:');
  console.error('   - Proyecto:', datosCompletos.proyecto);
  console.error('   - Proyecto ID:', datosCompletos.proyecto_id);
  console.error('   - Tarea:', datosCompletos.tarea);
  console.error('   - Horas:', datosCompletos.horas);
  throw new Error(`Preparación de datos fallida. Proyecto: ${datosCompletos.proyecto}, Proyecto ID: ${datosCompletos.proyecto_id}, Tarea: ${datosCompletos.tarea}, Horas: ${datosCompletos.horas}`);
}

console.log('✅ VALIDACIÓN EXITOSA - Datos críticos presentes (proyecto_id puede resolverse luego)');
return datosCompletos;

