// PROYECTO Y TAREA EXISTEN - Registrar horas directamente
const datosVerificacion = $('Preparar Buscar Tarea').item.json.datos_verificacion;
const proyectoData = $('Preparar Buscar Tarea').item.json.proyecto_data;
const tareaData = $json.result[0];
const uid = $('Autenticar Odoo').item.json.result;

console.log('✅ PROYECTO Y TAREA EXISTEN - Registrando horas directamente');
console.log('📋 Proyecto:', proyectoData.name, 'ID:', proyectoData.id);
console.log('✅ Tarea:', tareaData.name, 'ID:', tareaData.id);
console.log('⏰ Horas a registrar:', datosVerificacion.horas);

// Crear el registro de horas directamente
const registrarHoras = {
  jsonrpc: "2.0",
  method: "call",
  params: {
    service: "object",
    method: "execute_kw",
    args: [
      "lsanchezgoshawk-bot-main-27977821",
      uid,
      "admin",
      "account.analytic.line",
      "create",
      [{
        name: datosVerificacion.descripcion,
        project_id: proyectoData.id,
        task_id: tareaData.id,
        unit_amount: datosVerificacion.horas,
        date: new Date().toISOString().split('T')[0]
      }]
    ]
  },
  id: Math.floor(Math.random() * 1000000)
};

return {
  payload: registrarHoras,
  datos_registro: {
    proyecto: proyectoData.name,
    tarea: tareaData.name,
    horas: datosVerificacion.horas,
    descripcion: datosVerificacion.descripcion,
    chat_id: datosVerificacion.chat_id,
    uid: uid
  }
};

