// PROYECTO EXISTE, TAREA NO EXISTE - Guardar en memoria
const datosVerificacion = $('Preparar Buscar Tarea').item.json.datos_verificacion;
const proyectoData = $('Preparar Buscar Tarea').item.json.proyecto_data;
const chatId = datosVerificacion.chat_id;
const contextoKey = `memoria_${chatId}`;
let memoria = global[contextoKey] || {};

console.log('✅ PROYECTO EXISTE, ❌ TAREA NO EXISTE');
console.log('📋 Proyecto:', proyectoData.name, 'ID:', proyectoData.id);
console.log('❌ Tarea no encontrada:', datosVerificacion.tarea);

// Guardar datos para crear solo tarea + horas
memoria.esperando_confirmacion = 'crear_tarea_solamente';
memoria.esperando_respuesta = true;
memoria.datos_pendientes = {
  proyecto: datosVerificacion.proyecto_nombre,
  proyecto_id: datosVerificacion.proyecto_id,
  tarea: datosVerificacion.tarea,
  horas: datosVerificacion.horas,
  descripcion: datosVerificacion.descripcion,
  accion_original: 'registrar_horas',
  tipo_creacion: 'solo_tarea_horas', // Solo crear tarea y horas
  uid: $('Autenticar Odoo').item.json.result
};

// Actualizar historial
const entradaHistorial = {
  timestamp: new Date().toISOString(),
  usuario: `Registrar ${datosVerificacion.horas}h en ${datosVerificacion.proyecto}/${datosVerificacion.tarea}`,
  bot: `Tarea "${datosVerificacion.tarea}" no existe en proyecto existente`,
  accion: 'tarea_no_existe_en_proyecto',
  chat_id: chatId
};

if (!memoria.historial) memoria.historial = [];
memoria.historial.push(entradaHistorial);

global[contextoKey] = memoria;

return {
  chat_id: chatId,
  proyecto: datosVerificacion.proyecto_nombre,
  proyecto_id: datosVerificacion.proyecto_id,
  tarea: datosVerificacion.tarea,
  horas: datosVerificacion.horas,
  descripcion: datosVerificacion.descripcion,
  tipo_respuesta: 'tarea_no_existe',
  memoria_actualizada: memoria
};

