// PROYECTO NO EXISTE - Guardar en memoria y preguntar al usuario
const datosVerificacion = $('Verificar Proyecto y Tarea Horas').item.json.datos_verificacion;
const chatId = datosVerificacion.chat_id;
const contextoKey = `memoria_${chatId}`;
let memoria = global[contextoKey] || {};

console.log('❌ PROYECTO NO EXISTE - Guardando en memoria:', datosVerificacion.proyecto);

// Guardar TODOS los datos del parte de horas en memoria
memoria.esperando_confirmacion = 'crear_proyecto_completo';
memoria.esperando_respuesta = true;
memoria.datos_pendientes = {
  proyecto: datosVerificacion.proyecto,
  tarea: datosVerificacion.tarea,
  horas: datosVerificacion.horas,
  descripcion: datosVerificacion.descripcion,
  accion_original: 'registrar_horas',
  tipo_creacion: 'proyecto_tarea_horas', // Indica que hay que crear todo
  uid: $('Autenticar Odoo').item.json.result
};

// Actualizar historial
const entradaHistorial = {
  timestamp: new Date().toISOString(),
  usuario: `Registrar ${datosVerificacion.horas}h en ${datosVerificacion.proyecto}/${datosVerificacion.tarea}`,
  bot: `Proyecto "${datosVerificacion.proyecto}" no existe`,
  accion: 'proyecto_no_existe_para_horas',
  chat_id: chatId
};

if (!memoria.historial) memoria.historial = [];
memoria.historial.push(entradaHistorial);

global[contextoKey] = memoria;

return {
  chat_id: chatId,
  proyecto: datosVerificacion.proyecto,
  tarea: datosVerificacion.tarea,
  horas: datosVerificacion.horas,
  descripcion: datosVerificacion.descripcion,
  tipo_respuesta: 'proyecto_no_existe',
  memoria_actualizada: memoria
};

// PROYECTO EXISTE - Ahora verificar si la tarea también existe
const datosVerificacion = $('Verificar Proyecto y Tarea Horas').item.json.datos_verificacion;
const proyectoData = $json.result[0];
const uid = $('Autenticar Odoo').item.json.result;

console.log('✅ PROYECTO EXISTE - Verificando tarea:', datosVerificacion.tarea);
console.log('📋 Proyecto encontrado:', proyectoData.name, 'ID:', proyectoData.id);

// Buscar tarea en el proyecto
const buscarTarea = {
  jsonrpc: "2.0",
  method: "call",
  params: {
    service: "object",
    method: "execute_kw",
    args: [
      "lsanchezgoshawk-bot-main-27977821",
      uid,
      "admin",
      "project.task",
      "search_read",
      [
        [
          ["name", "ilike", datosVerificacion.tarea],
          ["project_id", "=", proyectoData.id]
        ],
        ["id", "name", "project_id"]
      ]
    ]
  },
  id: Math.floor(Math.random() * 1000000)
};

return {
  payload_tarea: buscarTarea,
  datos_verificacion: {
    ...datosVerificacion,
    proyecto_id: proyectoData.id,
    proyecto_nombre: proyectoData.name,
    paso: 'verificar_tarea'
  },
  proyecto_data: proyectoData
};

// VERIFICACIÓN INTELIGENTE PARA REGISTRO DE HORAS
// Este nodo verifica si el proyecto Y la tarea existen para determinar qué crear

const datosHoras = $json;
const uid = $('Autenticar Odoo').item.json.result;
const nombreProyecto = datosHoras.proyecto;
const nombreTarea = datosHoras.tarea;
const horas = datosHoras.horas;
const descripcion = datosHoras.descripcion;
const chatId = datosHoras.chat_id;

console.log('🔍 === VERIFICACIÓN INTELIGENTE PROYECTO Y TAREA ===');
console.log('📋 Proyecto:', nombreProyecto);
console.log('✅ Tarea:', nombreTarea);
console.log('⏰ Horas:', horas);
console.log('📝 Descripción:', descripcion);

// PASO 1: Buscar proyecto
const buscarProyecto = {
  jsonrpc: "2.0",
  method: "call",
  params: {
    service: "object",
    method: "execute_kw",
    args: [
      "lsanchezgoshawk-bot-main-27977821",
      uid,
      "admin",
      "project.project",
      "search_read",
      [
        [["name", "ilike", nombreProyecto]],
        ["id", "name"]
      ]
    ]
  },
  id: Math.floor(Math.random() * 1000000)
};

return {
  payload_proyecto: buscarProyecto,
  datos_verificacion: {
    proyecto: nombreProyecto,
    tarea: nombreTarea,
    horas: horas,
    descripcion: descripcion,
    chat_id: chatId,
    uid: uid,
    paso: 'verificar_proyecto'
  }
};

// Log para datos inválidos que no se pueden guardar en PostgreSQL
console.log('❌ DATOS INVÁLIDOS - No se guardó en PostgreSQL:');
console.log('📄 Datos recibidos:', JSON.stringify($json, null, 2));
console.log('🚫 Razón:', $json.razon || 'No especificada');
console.log('📊 Chat ID original:', $json.chat_id_original);
console.log('🔢 Chat ID numérico:', $json.chat_id_numerico);

return { 
  mensaje: 'Datos no guardados en PostgreSQL por ser inválidos',
  detalles: $json 
};

