// NODO UNIFICADOR - PREPARAR DATOS POSTGRESQL CON BÚSQUEDA ACTIVA
const timestampActual = new Date().toISOString();

// Detectar si viene del flujo de evento incompleto
let respuestaBot = $json.text || $json.respuesta_usuario || 'Respuesta procesada';

// Si viene de evento incompleto, usar mensaje específico
if ($json.esperando_confirmacion === 'crear_evento_calendario' && $json.datos_pendientes) {
  respuestaBot = 'faltan datos';
} else if ($json.ultimo_mensaje_bot) {
  respuestaBot = $json.ultimo_mensaje_bot;
}

console.log('🔄 === PREPARAR DATOS POSTGRESQL - BÚSQUEDA ACTIVA ===');
console.log('📥 Datos recibidos directamente:', JSON.stringify($json, null, 2));

// FUNCIÓN PARA BUSCAR DATOS DEL USUARIO EN NODOS ANTERIORES
function buscarDatosUsuario() {
  const nodosABuscar = [
    'Router de Acciones FIXED',
    'Procesar Respuesta',
    'Preparar Datos AI Agent',
    'Obtener Chat ID',
    'Extraer Datos'
  ];
  for (const nombreNodo of nodosABuscar) {
    try {
      const datos = $(nombreNodo).first().json;
      if (datos && datos.chat_id && datos.chat_id !== 'unknown') {
        console.log(`✅ Datos encontrados en: ${nombreNodo}`);
        return {
          chat_id: datos.chat_id,
          user_id: datos.user_id || datos.chat_id,
          user_name: datos.user_name || 'Usuario',
          mensaje_original: datos.mensaje_usuario || datos.mensaje_original || 'mensaje no disponible',
          fuente: nombreNodo,
          es_fallback: datos.es_fallback === true
        };
      }
    } catch (e) {
      console.log(`⚠️ No se pudo acceder a ${nombreNodo}:`, e.message);
    }
  }
  return null;
}

// INTENTAR OBTENER DATOS DEL USUARIO
let datosUsuario = {
  chat_id: $json.chat_id,
  user_id: $json.user_id,
  user_name: $json.user_name,
  mensaje_original: $json.mensaje_usuario || $json.mensaje_original,
  es_fallback: $json.es_fallback === true
};

// Si no tenemos chat_id válido, buscar en nodos anteriores
if (!datosUsuario.chat_id || datosUsuario.chat_id === 'unknown') {
  console.log('🔍 Chat ID no válido, buscando en nodos anteriores...');
  const datosBuscados = buscarDatosUsuario();
  if (datosBuscados) {
    datosUsuario = datosBuscados;
    console.log(`✅ Datos recuperados desde: ${datosBuscados.fuente}`);
  } else {
    console.log('❌ No se encontraron datos válidos del usuario');
    datosUsuario = {
      chat_id: 0,
      user_id: 0,
      user_name: 'unknown',
      mensaje_original: 'mensaje no disponible',
      fuente: 'fallback',
      es_fallback: true
    };
  }
}

console.log('👤 DATOS FINALES DEL USUARIO:', {
  chat_id: datosUsuario.chat_id,
  user_id: datosUsuario.user_id,
  user_name: datosUsuario.user_name,
  fuente: datosUsuario.fuente || 'directo',
  es_fallback: datosUsuario.es_fallback === true
});

// CREAR REGISTRO PARA POSTGRESQL
const registroPostgreSQL = {
  chat_id: parseInt(datosUsuario.chat_id) || 0,
  user_id: parseInt(datosUsuario.user_id) || 0,
  user_name: datosUsuario.user_name === 'unknown' ? null : datosUsuario.user_name,
  ultima_accion: 'conversar',
  ultimo_mensaje_bot: respuestaBot,
  mensaje_usuario: datosUsuario.mensaje_original === 'mensaje no disponible' ? null : datosUsuario.mensaje_original,
  historial: JSON.stringify([{
    timestamp: timestampActual,
    usuario: datosUsuario.mensaje_original,
    bot: respuestaBot,
    accion: 'conversar'
  }]),
  historial_conversacion: JSON.stringify([{
    timestamp: timestampActual,
    contenido: datosUsuario.mensaje_original,
    tipo: 'usuario'
  }, {
    timestamp: timestampActual,
    contenido: respuestaBot,
    tipo: 'bot'
  }]),
  // Propagar al post-descontar
  es_fallback: datosUsuario.es_fallback === true
};

console.log('📤 REGISTRO FINAL PARA POSTGRESQL:', {
  chat_id: registroPostgreSQL.chat_id,
  user_id: registroPostgreSQL.user_id,
  user_name: registroPostgreSQL.user_name,
  es_fallback: registroPostgreSQL.es_fallback
});

return [registroPostgreSQL];

// Formatear respuesta de contacto creado para Telegram
const contactoCreado = $json.result;
const datosContacto = $('Preparar Crear Contacto').item.json.datos_contacto;
const baseUrl = 'https://lsanchezgoshawk-n8npruebas.odoo.com';
let texto = '';

if (!contactoCreado) {
  texto = '❌ Error al crear el contacto. Por favor, intenta de nuevo.';
} else {
