};

return {
  payload: crearProyecto,
  datos_flujo: {
    paso: 'crear_proyecto',
    proyecto: datosPendientes.proyecto,
    tarea: datosPendientes.tarea,
    horas: datosPendientes.horas,
    descripcion: datosPendientes.descripcion,
    chat_id: chatId,
    uid: uid
  },
  datos_pendientes: datosPendientes
};

// PREPARAR DATOS COMPLETOS PARA CREAR PROYECTO TAREA HORAS
// OBTENER DATOS DESDE EL NODO CORRECTO
let datos = $json;

console.log('🔧 DATOS DESDE SWITCH ACCIONES:', JSON.stringify(datos, null, 2));

// IMPORTANTE: Obtener datos desde el nodo que SÍ tiene la información
let datosFromAI;
try {
  datosFromAI = $('Preparar Datos AI Agent').first().json;
