  console.log('   - datos_pendientes.tarea:', datos.datos_pendientes.tarea);
  console.log('   - datos_pendientes.horas:', datos.datos_pendientes.horas);
}

// Obtener datos del Router de Acciones anterior para asegurar información completa
let datosRouter = {};
try {
