  // Acción específica
  accion: datos.accion || 'crear_proyecto_tarea_horas',

  // Datos del proyecto/tarea/horas - PRIORIDAD A datos_pendientes
  proyecto: datos.datos_pendientes?.proyecto || datos.proyecto || datosRouter.proyecto || datosRouter.datos_pendientes?.proyecto,
  tarea: datos.datos_pendientes?.tarea || datos.tarea || datosRouter.tarea || datosRouter.datos_pendientes?.tarea,
  horas: datos.datos_pendientes?.horas || datos.horas || datosRouter.horas || datosRouter.datos_pendientes?.horas,
  descripcion: datos.datos_pendientes?.descripcion || datos.descripcion || datosRouter.descripcion || datosRouter.datos_pendientes?.descripcion,

  // Datos pendientes completos
  datos_pendientes: {
    proyecto: datos.datos_pendientes?.proyecto || datos.proyecto || datosRouter.proyecto || datosRouter.datos_pendientes?.proyecto,
    tarea: datos.datos_pendientes?.tarea || datos.tarea || datosRouter.tarea || datosRouter.datos_pendientes?.tarea,
    horas: datos.datos_pendientes?.horas || datos.horas || datosRouter.horas || datosRouter.datos_pendientes?.horas,
    descripcion: datos.datos_pendientes?.descripcion || datos.descripcion || datosRouter.descripcion || datosRouter.datos_pendientes?.descripcion,
    uid: datos.uid || datosRouter.uid,
    tipo_creacion: 'proyecto_tarea_horas',
    ...datos.datos_pendientes
  }
};

console.log('🎯 DATOS COMPLETOS PREPARADOS:');
console.log('   📋 Proyecto:', datosCompletos.proyecto);
console.log('   ✅ Tarea:', datosCompletos.tarea);
