console.log('   🔑 UID:', datosCompletos.uid);
console.log('   💬 Chat ID:', datosCompletos.chat_id);
console.log('   🗂️ Datos pendientes completos:', JSON.stringify(datosCompletos.datos_pendientes, null, 2));

// VALIDACIÓN FINAL
if (!datosCompletos.proyecto || !datosCompletos.tarea || !datosCompletos.horas) {
