// Preparar registro automático de horas tras crear tarea
const datosHoras = $json;
const uid = $('Autenticar Odoo').item.json.result;
const fechaHoy = new Date().toISOString().split('T')[0];

const crearHoras = {
  jsonrpc: "2.0",
  method: "call",
  params: {
    service: "object",
    method: "execute_kw",
    args: [
      "lsanchezgoshawk-bot-main-27977821",
      uid,
      "admin",
      "account.analytic.line",
      "create",
      [{
        name: datosHoras.descripcion,
        unit_amount: parseFloat(datosHoras.horas),
        project_id: parseInt(datosHoras.proyecto_data.id),
        task_id: parseInt(datosHoras.tarea_creada_id),
        date: fechaHoy
      }]
    ]
  },
  id: Math.floor(Math.random() * 1000000)
};

return {
  payload: crearHoras,
  datos_registro: {
    proyecto_nombre: datosHoras.proyecto,
    tarea_nombre: datosHoras.tarea,
    horas: datosHoras.horas,
    descripcion: datosHoras.descripcion,
    fecha: fechaHoy,
    chat_id: datosHoras.chat_id
  }
};

// Verificar si después de crear la tarea hay que registrar horas pendientes
const datosLimpiar = $json;
const chatId = datosLimpiar.datos_tarea?.chat_id;
const memoria = datosLimpiar.memoria;

if (!chatId || !memoria) {
  console.log('No se encontró chatId o memoria');
  return { accion: 'terminar' };
}

// Si había datos pendientes de registro de horas, proceder a registrarlas
if (memoria.datos_pendientes && memoria.datos_pendientes.accion_original === 'registrar_horas') {
  const datosHoras = memoria.datos_pendientes;

  console.log('Registrando horas automáticamente:', datosHoras);

  // Limpiar memoria ahora que vamos a registrar las horas
  const contextoKey = `memoria_${chatId}`;
  let memoriaActualizada = global[contextoKey] || {};
  memoriaActualizada.esperando_confirmacion = null;
  memoriaActualizada.datos_pendientes = null;
  global[contextoKey] = memoriaActualizada;

  // Preparar datos para registrar horas con la tarea recién creada
  return {
    proyecto: datosHoras.proyecto,
    tarea: datosHoras.tarea,
    horas: datosHoras.horas,
    descripcion: datosHoras.descripcion,
    uid: datosLimpiar.uid,
    chat_id: chatId,
    accion: 'registrar_horas_automatico',
    proyecto_data: {
      id: datosLimpiar.datos_tarea.proyecto_id,
      name: datosHoras.proyecto
    },
    tarea_creada_id: datosLimpiar.tarea_creada_id
  };
} else {
  // No hay horas pendientes, terminar
  console.log('No hay horas pendientes para registrar');
  return { accion: 'terminar' };
}

// Preparar datos después de crear tarea exitosamente
const chatId = $('Preparar Crear Tarea').item.json.datos_tarea.chat_id;
const tareaCreada = $json.result; // ID de la tarea recién creada
const contextoKey = `memoria_${chatId}`;
let memoria = global[contextoKey] || {};

// Actualizar memoria con la nueva tarea creada
memoria.proyecto_anterior = $('Preparar Crear Tarea').item.json.datos_tarea.nombre_proyecto;
memoria.tarea_anterior = $('Preparar Crear Tarea').item.json.datos_tarea.nombre_tarea;
memoria.ultima_accion = 'crear_tarea';

// Conservar datos pendientes si existen para registro automático de horas
const tieneHorasPendientes = memoria.datos_pendientes && memoria.datos_pendientes.accion_original === 'registrar_horas';

if (!tieneHorasPendientes) {
  memoria.esperando_confirmacion = null;
  memoria.datos_pendientes = null;
}

global[contextoKey] = memoria;

return {
  ...($json),
  datos_tarea: $('Preparar Crear Tarea').item.json.datos_tarea,
  tarea_creada_id: tareaCreada,
  uid: $('Preparar Crear Tarea').item.json.datos_tarea.uid || $('Buscar Proyecto para Tarea').item.json.uid,
  memoria: memoria
};

