// Combinar toda la información del producto - MANEJO SEGURO DE NODOS

// Función para obtener datos de nodos de manera segura
function obtenerDatosSeguro(nombreNodo) {
  try {
    const nodo = $(nombreNodo);
    if (nodo && nodo.item && nodo.item.json) {
      return nodo.item.json;
    }
    return null;
  } catch (error) {
    console.log(`⚠️ Nodo "${nombreNodo}" no disponible:`, error.message);
    return null;
  }
}

// Obtener datos de forma segura
const datosBase = obtenerDatosSeguro('Preparar Consultas Adicionales');
const stockData = obtenerDatosSeguro('HTTP Stock Ubicaciones');
const ventasData = obtenerDatosSeguro('HTTP Ventas Producto');

if (!datosBase) {
  return {
    error: true,
    mensaje: '❌ Error interno: No se pudieron obtener los datos base del producto.',
    chat_id: 'unknown'
  };
}

if (datosBase.skip_queries) {
  return {
    error: true,
    mensaje: `❌ No se encontró ningún producto con el nombre "${datosBase.producto_buscado}".`,
    chat_id: datosBase.chat_id
  };
}

// Extraer datos de stock y ventas
const stockUbicaciones = (stockData && stockData.result) ? stockData.result : [];
const ventasProducto = (ventasData && ventasData.result) ? ventasData.result : [];
const productosInfo = datosBase.productos_info || [];

console.log('📊 COMBINANDO INFORMACIÓN COMPLETA:');
console.log('   - Productos:', productosInfo.length);
console.log('   - Stock ubicaciones:', stockUbicaciones.length);
console.log('   - Ventas históricas:', ventasProducto.length);

// Procesar cada producto encontrado
const productosCompletos = productosInfo.map(producto => {
  const productId = producto.id;

  // Stock por ubicaciones de este producto
  const stockDeEsteProducto = stockUbicaciones.filter(s => s.product_id && s.product_id[0] === productId);

  // Ventas de este producto
  const ventasDeEsteProducto = ventasProducto.filter(v => v.product_id && v.product_id[0] === productId);

  // Calcular estadísticas de ventas
  const totalVendido = ventasDeEsteProducto.reduce((sum, v) => sum + (v.product_uom_qty || 0), 0);
  const totalFacturado = ventasDeEsteProducto.reduce((sum, v) => sum + (v.price_total || 0), 0);

  // Agrupar stock por ubicación
  const stockPorUbicacion = stockDeEsteProducto.map(s => ({
    ubicacion: s.location_id ? s.location_id[1] : 'Ubicación desconocida',
    cantidad: s.quantity || 0,
    reservado: s.reserved_quantity || 0,
    disponible: s.available_quantity || 0
  }));

  return {
    id: productId,
    nombre: producto.name,
    codigo: producto.default_code || 'Sin código',
    precio_venta: producto.list_price || 0,
    precio_costo: producto.standard_price || 0,
    categoria: producto.categ_id ? producto.categ_id[1] : 'Sin categoría',
    unidad_medida: producto.uom_id ? producto.uom_id[1] : 'Unidad',

    // Stock
    stock_actual: producto.qty_available || 0,
    stock_pronosticado: producto.virtual_available || 0,
    stock_por_ubicacion: stockPorUbicacion,

    // Ventas (últimos 6 meses)
    total_vendido_6meses: totalVendido,
    total_facturado_6meses: totalFacturado.toFixed(2),
    numero_ordenes: ventasDeEsteProducto.length
  };
});

return {
  productos: productosCompletos,
  chat_id: datosBase.chat_id,
  producto_buscado: datosBase.producto_buscado
};

