// ✅ Telegram: partir en mensajes (límite ~4096). Usamos 3500 para ir seguros con Markdown.
const MAX = 3500;

function splitTextSafe(str, maxLen) {
  const parts = [];
  let remaining = str || '';

  while (remaining.length > maxLen) {
    // intenta cortar por salto de línea cerca del límite
    let cut = remaining.lastIndexOf('\n', maxLen);
    if (cut < 1000) cut = maxLen; // si no hay saltos razonables, corta a saco
    parts.push(remaining.slice(0, cut));
    remaining = remaining.slice(cut).trimStart();
  }
  if (remaining.length) parts.push(remaining);
  return parts;
}

