                             nombreProducto !== factura.name &&
                             !nombreProducto.match(/^\d+\.?\d*%?$/);

        console.log(`🔍 Línea "${nombreProducto}": cantidad=${cantidad}, precio=${precioTotal}, válida=${esLineaValida}`);
