const parametro = datosAI.parametro;

// Validar que tenemos uid
if (!uid) {
  throw new Error('No se pudo obtener el UID de autenticación');
}

let year, month;

if (tipoConsulta === 'mes_nombre' && parametro) {
  // Mapear nombres de meses a números
  const mesesMap = {
    'enero': 1, 'febrero': 2, 'marzo': 3, 'abril': 4, 'mayo': 5, 'junio': 6,
    'julio': 7, 'agosto': 8, 'septiembre': 9, 'octubre': 10, 'noviembre': 11, 'diciembre': 12
  };

  // Intentar detectar mes específico en el parámetro
  let mesDetectado = null;
  const mesTexto = parametro.toString().toLowerCase();
  console.log('🔍 DETECTANDO MES VENTAS PERDIDAS en:', mesTexto);

  // Buscar cualquier mes en el parámetro
  for (const [nombreMes, numeroMes] of Object.entries(mesesMap)) {
    if (mesTexto.includes(nombreMes)) {
      mesDetectado = numeroMes;
      console.log('✅ MES DETECTADO VENTAS PERDIDAS:', nombreMes, '=', numeroMes);
      break;
    }
  }

  if (mesDetectado) {
    // Usar mes detectado
    month = mesDetectado;
    year = new Date().getFullYear();
    console.log('📅 VENTAS PERDIDAS - USANDO MES ESPECÍFICO:', month, 'del año', year);
  } else {
    // Si no se detecta, usar mes actual
    const now = new Date();
    year = now.getFullYear();
    month = now.getMonth() + 1;
    console.log('📅 VENTAS PERDIDAS - USANDO MES ACTUAL:', month, 'del año', year);
  }
} else {
  // Mes actual por defecto
  const now = new Date();
  year = now.getFullYear();
  month = now.getMonth() + 1;
  console.log('📅 VENTAS PERDIDAS - USANDO MES ACTUAL:', month, 'del año', year);
}

// Construir fechas
const mes = month < 10 ? `0${month}` : `${month}`;
const primerDia = `${year}-${mes}-01`;
const siguienteMes = month === 12 ? 1 : month + 1;
const siguienteAnio = month === 12 ? year + 1 : year;
const mesSiguiente = siguienteMes < 10 ? `0${siguienteMes}` : `${siguienteMes}`;
const primerDiaSiguienteMes = `${siguienteAnio}-${mesSiguiente}-01`;

