// Analizar ventas perdidas y generar estadísticas
const respuestaOdoo = $json.result;
const datosConsulta = $('Preparar Consulta Ventas Perdidas').item.json;

console.log('📊 ANÁLISIS VENTAS PERDIDAS:', Array.isArray(respuestaOdoo) ? respuestaOdoo.length : 0, 'órdenes');

if (!Array.isArray(respuestaOdoo) || respuestaOdoo.length === 0) {
  return {
    texto: '📊 No se encontraron órdenes de venta en el período analizado.',
    chat_id: datosConsulta.chat_id
  };
}

// Clasificar órdenes - PENDIENTES Y PERDIDAS (incluye confirmadas no facturadas)
let ventasPendientes = 0; // Estado: draft, sent, sale no facturadas
let ventasPerdidas = 0;   // Estado: cancel
let montoPendiente = 0;
let montoPerdido = 0;

respuestaOdoo.forEach(orden => {
  const estado = orden.state;
  const estadoFacturacion = orden.invoice_status;
  const monto = parseFloat(orden.amount_total || 0);

  // PENDIENTES incluye: cotizaciones + confirmadas no facturadas
  if (estado === 'draft' || estado === 'sent') {
    // Cotizaciones pendientes de confirmar
    ventasPendientes++;
    montoPendiente += monto;
  } else if (estado === 'sale' || estado === 'done') {
    // Confirmadas pero verificar si están facturadas
    if (estadoFacturacion === 'invoiced') {
      // Ya facturadas = ignorar (proceso completado)
    } else {
      // Confirmadas pero NO facturadas = son relevantes como pendientes
      ventasPendientes++;
      montoPendiente += monto;
    }
  } else if (estado === 'cancel') {
    ventasPerdidas++;
    montoPerdido += monto;
  }
});

const totalRelevantes = ventasPendientes + ventasPerdidas;
const porcentajePendientes = totalRelevantes > 0 ? ((ventasPendientes / totalRelevantes) * 100).toFixed(1) : 0;
const porcentajePerdidas = totalRelevantes > 0 ? ((ventasPerdidas / totalRelevantes) * 100).toFixed(1) : 0;

// Generar reporte - SOLO PENDIENTES + PERDIDAS
let texto = `📊 **ANÁLISIS: VENTAS PENDIENTES Y PERDIDAS**\n\n`;
texto += `📈 **RESUMEN:**\n`;
texto += `• Órdenes relevantes: **${totalRelevantes}**\n\n`;
texto += `⏳ **PENDIENTES:** ${ventasPendientes} (${porcentajePendientes}%) - €${montoPendiente.toFixed(2)}\n`;
texto += `❌ **PERDIDAS:** ${ventasPerdidas} (${porcentajePerdidas}%) - €${montoPerdido.toFixed(2)}\n\n`;

if (totalRelevantes > 0) {
  const montoTotal = montoPendiente + montoPerdido;
  texto += `💰 **Total en riesgo:** €${montoTotal.toFixed(2)}\n`;

  if (ventasPendientes > 0) {
    texto += `🎯 **Oportunidad:** ${ventasPendientes} órdenes por €${montoPendiente.toFixed(2)} esperan confirmación\n`;
  }
}

return {
  texto,
  chat_id: datosConsulta.chat_id,
  estadisticas: {
    pendientes: ventasPendientes,
    perdidas: ventasPerdidas,
    total_relevantes: totalRelevantes,
    monto_pendiente: montoPendiente,
    monto_perdido: montoPerdido,
    porcentajes: { pendientes: porcentajePendientes, perdidas: porcentajePerdidas }
  }
};

