// Calcular fechas según el período
const ahora = new Date();
let fechaInicio, fechaFin;

switch(periodo) {
  case 'hoy':
    fechaInicio = new Date(ahora.getFullYear(), ahora.getMonth(), ahora.getDate());
    fechaInicio.setHours(0, 0, 0, 0);
    fechaFin = new Date(ahora.getFullYear(), ahora.getMonth(), ahora.getDate() + 1);
    fechaFin.setHours(0, 0, 0, 0);
    break;
  case 'ayer':
    fechaInicio = new Date(ahora.getFullYear(), ahora.getMonth(), ahora.getDate() - 1);
    fechaInicio.setHours(0, 0, 0, 0);
    fechaFin = new Date(ahora.getFullYear(), ahora.getMonth(), ahora.getDate());
    fechaFin.setHours(0, 0, 0, 0);
    break;
  case 'esta_semana':
    // Calcular el lunes de esta semana (getDay(): 0=domingo, 1=lunes, etc.)
    const diasDesdeInicioSemana = ahora.getDay() === 0 ? 6 : ahora.getDay() - 1; // Si es domingo, retroceder 6 días
    fechaInicio = new Date(ahora.getFullYear(), ahora.getMonth(), ahora.getDate() - diasDesdeInicioSemana);
    fechaInicio.setHours(0, 0, 0, 0); // Inicio del día
    fechaFin = new Date(ahora.getFullYear(), ahora.getMonth(), ahora.getDate() + 1);
    fechaFin.setHours(0, 0, 0, 0); // Inicio del día siguiente
    break;
  case 'semana_pasada':
    // Calcular el lunes de la semana pasada
    const diasDesdeInicioSemanaAnterior = ahora.getDay() === 0 ? 6 : ahora.getDay() - 1;
    const lunesSemanaAnterior = new Date(ahora.getFullYear(), ahora.getMonth(), ahora.getDate() - diasDesdeInicioSemanaAnterior - 7);
    fechaInicio = new Date(lunesSemanaAnterior);
    fechaInicio.setHours(0, 0, 0, 0);
    fechaFin = new Date(lunesSemanaAnterior.getFullYear(), lunesSemanaAnterior.getMonth(), lunesSemanaAnterior.getDate() + 7);
    fechaFin.setHours(0, 0, 0, 0);
    break;
  case 'este_mes':
    fechaInicio = new Date(ahora.getFullYear(), ahora.getMonth(), 1);
    fechaInicio.setHours(0, 0, 0, 0);
    fechaFin = new Date(ahora.getFullYear(), ahora.getMonth() + 1, 1);
    fechaFin.setHours(0, 0, 0, 0);
    break;
  case 'mes_pasado':
    fechaInicio = new Date(ahora.getFullYear(), ahora.getMonth() - 1, 1);
    fechaInicio.setHours(0, 0, 0, 0);
    fechaFin = new Date(ahora.getFullYear(), ahora.getMonth(), 1);
    fechaFin.setHours(0, 0, 0, 0);
    break;
  case 'mes_nombre':
    // Para consultas de mes específico por nombre (ej: "enero", "febrero", etc.)
    // Por defecto usar el mes actual si no se puede determinar el mes específico
    fechaInicio = new Date(ahora.getFullYear(), ahora.getMonth(), 1);
    fechaInicio.setHours(0, 0, 0, 0);
    fechaFin = new Date(ahora.getFullYear(), ahora.getMonth() + 1, 1);
    fechaFin.setHours(0, 0, 0, 0);
    break;
  default:
    throw new Error('Período no válido: ' + periodo);
}

