// Preparar consulta de eventos de usuario desde AI Agent — TZ Madrid + mañana/ayer/etc
const datosAI = $json;
const uid = $('Autenticar Odoo').item.json.result;
const nombreUsuario = datosAI.usuario;
const periodoEntrada = (datosAI.periodo || 'hoy').toString().toLowerCase().trim();
const fechaEspecifica = datosAI.fecha_especifica;

const mensajeOriginal = (
  datosAI.mensaje_ia ||
  datosAI.mensaje_original ||
  datosAI.mensaje ||
  ''
).toString().toLowerCase();

// Validaciones
if (!uid) throw new Error('No se pudo obtener el UID de autenticación');
if (!nombreUsuario) throw new Error('No se encontró el nombre del usuario');

// ===================== Helpers TZ (Europe/Madrid) =====================
const TZ = 'Europe/Madrid';
const pad = (n) => String(n).padStart(2, '0');

const ymdMadrid = (date) => {
  const parts = new Intl.DateTimeFormat('en-CA', {
    timeZone: TZ,
    year: 'numeric', month: '2-digit', day: '2-digit',
  }).formatToParts(date);
  const y = parts.find(p => p.type === 'year').value;
  const m = parts.find(p => p.type === 'month').value;
  const d = parts.find(p => p.type === 'day').value;
  return `${y}-${m}-${d}`;
};

const addDaysYMD = (ymd, days) => {
  const [Y, M, D] = ymd.split('-').map(Number);
  const base = new Date(Date.UTC(Y, M - 1, D));
  base.setUTCDate(base.getUTCDate() + days);
  return ymdMadrid(base);
};

const isYMD = (s) => /^\d{4}-\d{2}-\d{2}$/.test(String(s || ''));

// Semana Lunes-Domingo (España)
const weekRangeMadrid = (baseDate) => {
  const d = new Date(baseDate);
  // Pasamos a YMD Madrid primero para evitar líos UTC
  const baseYMD = ymdMadrid(d);
  const [Y, M, Dd] = baseYMD.split('-').map(Number);
  const local = new Date(Date.UTC(Y, M - 1, Dd)); // base "día" estable

  // getUTCDay: 0 dom..6 sáb; queremos lunes=0
  const day = local.getUTCDay();
  const diffToMonday = (day + 6) % 7;

  const monday = new Date(local);
  monday.setUTCDate(local.getUTCDate() - diffToMonday);

  const sunday = new Date(monday);
  sunday.setUTCDate(monday.getUTCDate() + 6);

  return { desde: ymdMadrid(monday), hasta: ymdMadrid(sunday) };
};

// Mes completo
const monthRange = (y, m1to12) => {
  const first = `${y}-${pad(m1to12)}-01`;
  const lastDay = new Date(Date.UTC(y, m1to12, 0)).getUTCDate(); // m1to12+1, día 0
  const last = `${y}-${pad(m1to12)}-${pad(lastDay)}`;
  return { desde: first, hasta: last };
};

// Base “hoy” usando timestamp real si viene
const ts = datosAI.timestamp ? new Date(datosAI.timestamp) : new Date();
const hoyYMD = ymdMadrid(ts);
const curY = Number(hoyYMD.slice(0, 4));

// ===================== Meses =====================
const meses = {
  'enero':1,'ene':1,
  'febrero':2,'feb':2,
  'marzo':3,'mar':3,
  'abril':4,'abr':4,
  'mayo':5,'may':5,
  'junio':6,'jun':6,
  'julio':7,'jul':7,
  'agosto':8,'ago':8,
  'septiembre':9,'setiembre':9,'septiebre':9,'sept':9,'sep':9,
  'octubre':10,'oct':10,
  'noviembre':11,'nov':11,
  'diciembre':12,'dic':12,
};

// Detectar mes en el mensaje (opcional)
const msgNorm = mensajeOriginal
  .normalize('NFD')
  .replace(/[\u0300-\u036f]/g, '')
  .replace(/\s+/g, ' ')
  .trim();

let mesMatch = null;
for (const [nombreMes, numeroMes] of Object.entries(meses)) {
  const regex = new RegExp(`\\b${nombreMes}\\b`, 'i');
  if (msgNorm.match(regex)) {
    mesMatch = numeroMes;
    break;
  }
}

// ===================== Calcular rango =====================
let fechaInicio = null;
let fechaFin = null;

// Si detectamos un mes en el mensaje, prioriza mes completo
if (mesMatch) {
  const r = monthRange(curY, mesMatch);
  fechaInicio = r.desde;
  fechaFin = r.hasta;
}

// Si no hay mes detectado, usar periodo
if (!fechaInicio) {
  switch (periodoEntrada) {
    case 'hoy':
      fechaInicio = hoyYMD;
      fechaFin = hoyYMD;
      break;

    case 'mañana':
    case 'manana':
      fechaInicio = addDaysYMD(hoyYMD, 1);
      fechaFin = fechaInicio;
      break;

    case 'ayer':
      fechaInicio = addDaysYMD(hoyYMD, -1);
      fechaFin = fechaInicio;
      break;

    case 'pasado_mañana':
    case 'pasado_manana':
      fechaInicio = addDaysYMD(hoyYMD, 2);
      fechaFin = fechaInicio;
      break;

    case 'esta_semana': {
      const r = weekRangeMadrid(ts);
      fechaInicio = r.desde;
      fechaFin = r.hasta;
      break;
    }

    case 'semana_pasada': {
      const lastWeek = new Date(ts);
      lastWeek.setDate(lastWeek.getDate() - 7);
      const r = weekRangeMadrid(lastWeek);
      fechaInicio = r.desde;
      fechaFin = r.hasta;
      break;
    }

    case 'este_mes': {
      const m = Number(hoyYMD.slice(5, 7));
      const r = monthRange(curY, m);
      fechaInicio = r.desde;
      fechaFin = r.hasta;
      break;
    }

    case 'mes_pasado': {
      let m = Number(hoyYMD.slice(5, 7)) - 1;
      let y = curY;
      if (m === 0) { m = 12; y = curY - 1; }
      const r = monthRange(y, m);
      fechaInicio = r.desde;
      fechaFin = r.hasta;
      break;
    }

    default:
      // Si el periodo es un mes directamente (ej "octubre")
      if (meses[periodoEntrada]) {
        const r = monthRange(curY, meses[periodoEntrada]);
        fechaInicio = r.desde;
        fechaFin = r.hasta;
      }
      // Fecha específica si viene bien formada
      else if (fechaEspecifica && isYMD(fechaEspecifica)) {
        fechaInicio = fechaEspecifica;
        fechaFin = fechaEspecifica;
      } else {
        // Default a hoy
        fechaInicio = hoyYMD;
        fechaFin = hoyYMD;
      }
  }
}

