  texto += `${periodo === 'hoy' ? '🎯 Día perfecto para enfocarse en tareas pendientes.' : '⚡ Período disponible para nuevos compromisos.'}`;
} else {
  texto += `📋 **${eventos.length} evento${eventos.length > 1 ? 's' : ''} programado${eventos.length > 1 ? 's' : ''}:**\n\n`;

  // Agrupar eventos por día
  const eventosPorDia = {};

  eventos.forEach(evento => {
    const fechaEvento = new Date(evento.start);
    const fechaStr = fechaEvento.toLocaleDateString('es-ES', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });

    if (!eventosPorDia[fechaStr]) {
      eventosPorDia[fechaStr] = [];
    }
    eventosPorDia[fechaStr].push(evento);
  });

  // Formatear cada día
  Object.keys(eventosPorDia).forEach((fecha, indexDia) => {
    const eventosDelDia = eventosPorDia[fecha];

    texto += `**📅 ${fecha.charAt(0).toUpperCase() + fecha.slice(1)}**\n`;

    eventosDelDia.forEach((evento, index) => {
      const fechaInicio = new Date(evento.start);
      const fechaFin = new Date(evento.stop);
      const horaInicio = fechaInicio.toLocaleTimeString('es-ES', { hour: '2-digit', minute: '2-digit' });
      const horaFin = fechaFin.toLocaleTimeString('es-ES', { hour: '2-digit', minute: '2-digit' });

      let iconoEvento = '🕐';
      if (evento.name.toLowerCase().includes('reunion')) iconoEvento = '👥';
      else if (evento.name.toLowerCase().includes('cita')) iconoEvento = '🤝';
      else if (evento.name.toLowerCase().includes('llamada')) iconoEvento = '📞';
      else if (evento.name.toLowerCase().includes('demo')) iconoEvento = '💻';
      else if (evento.name.toLowerCase().includes('formacion')) iconoEvento = '📚';

      texto += `   ${iconoEvento} **${horaInicio} - ${horaFin}** | ${evento.name}`;

      if (evento.location) {
        texto += ` 📍 ${evento.location}`;
      }

      if (evento.description && evento.description.trim() !== '') {
        const descripcionCorta = evento.description.length > 50 
          ? evento.description.substring(0, 50) + '...' 
          : evento.description;
        texto += `\n      💬 _${descripcionCorta}_`;
      }

      const duracionMinutos = Math.round(evento.duration * 60);
      const horas = Math.floor(duracionMinutos / 60);
      const minutos = duracionMinutos % 60;
      let duracionTexto = '';
      if (horas > 0) duracionTexto += `${horas}h`;
      if (minutos > 0) duracionTexto += `${minutos}m`;
      if (duracionTexto) texto += ` ⏱️ ${duracionTexto}`;

      texto += '\n\n';
    });
  });

  // Agregar resumen
  const totalDuracion = eventos.reduce((total, e) => total + e.duration, 0);
  const horasTotal = Math.floor(totalDuracion);
  const minutosTotal = Math.round((totalDuracion - horasTotal) * 60);

