}

let fechaDesde, fechaHasta;
const ahora = new Date();

// Calcular fechas según el tipo de consulta
if (tipoConsulta === 'mes_actual') {
  const year = ahora.getFullYear();
  const month = ahora.getMonth() + 1;
  const mes = month < 10 ? `0${month}` : `${month}`;
  fechaDesde = `${year}-${mes}-01`;
  fechaHasta = `${year}-${mes}-${new Date(year, month, 0).getDate()}`;
} 
else if (tipoConsulta === 'mes_pasado') {
  let year = ahora.getFullYear();
  let month = ahora.getMonth();
  if (month === 0) {
    month = 12;
    year = year - 1;
  }
  const mes = month < 10 ? `0${month}` : `${month}`;
  fechaDesde = `${year}-${mes}-01`;
  fechaHasta = `${year}-${mes}-${new Date(year, month, 0).getDate()}`;
}
else if (tipoConsulta === 'mes_nombre') {
  const meses = [
    "enero", "febrero", "marzo", "abril", "mayo", "junio",
    "julio", "agosto", "septiembre", "octubre", "noviembre", "diciembre"
  ];
  const mesIndex = meses.indexOf(parametro.toLowerCase());
  if (mesIndex !== -1) {
    let year = ahora.getFullYear();
    // Si el mes es futuro, usar año anterior
    if (mesIndex + 1 > (ahora.getMonth() + 1)) {
      year = year - 1;
    }
    const mes = (mesIndex + 1) < 10 ? `0${mesIndex + 1}` : `${mesIndex + 1}`;
    fechaDesde = `${year}-${mes}-01`;
    fechaHasta = `${year}-${mes}-${new Date(year, mesIndex + 1, 0).getDate()}`;
  }
}

else if (tipoConsulta === 'esta_semana') {
  // Semana ISO: lunes -> domingo (España)
  const d = new Date(ahora);
  const day = d.getDay(); // 0 dom, 1 lun, ... 6 sáb
  const diffToMonday = (day + 6) % 7; // lunes = 0
  const monday = new Date(d);
  monday.setDate(d.getDate() - diffToMonday);

  const sunday = new Date(monday);
  sunday.setDate(monday.getDate() + 6);

  const fmt = (x) => x.toISOString().slice(0, 10);
  fechaDesde = fmt(monday);
  fechaHasta = fmt(sunday);
}
else if (tipoConsulta === 'semana_pasada') {
  const d = new Date(ahora);
  const day = d.getDay();
  const diffToMonday = (day + 6) % 7;

  const mondayThisWeek = new Date(d);
  mondayThisWeek.setDate(d.getDate() - diffToMonday);

  const mondayLastWeek = new Date(mondayThisWeek);
  mondayLastWeek.setDate(mondayThisWeek.getDate() - 7);

  const sundayLastWeek = new Date(mondayLastWeek);
  sundayLastWeek.setDate(mondayLastWeek.getDate() + 6);

  const fmt = (x) => x.toISOString().slice(0, 10);
  fechaDesde = fmt(mondayLastWeek);
  fechaHasta = fmt(sundayLastWeek);
}


