    fechaHasta = new Date(ahora.getFullYear(), ahora.getMonth(), ahora.getDate() + 1).toISOString().split('T')[0];
    break;
  case 'ayer':
    const ayer = new Date(ahora.getTime() - 24*60*60*1000);
    fechaDesde = new Date(ayer.getFullYear(), ayer.getMonth(), ayer.getDate()).toISOString().split('T')[0];
    fechaHasta = new Date(ayer.getFullYear(), ayer.getMonth(), ayer.getDate() + 1).toISOString().split('T')[0];
    break;
  case 'esta_semana':
    const inicioSemana = new Date(ahora.getTime() - (ahora.getDay() * 24*60*60*1000));
    fechaDesde = new Date(inicioSemana.getFullYear(), inicioSemana.getMonth(), inicioSemana.getDate()).toISOString().split('T')[0];
    fechaHasta = ahora.toISOString().split('T')[0];
    break;
  case 'mes_pasado':
    const mesAnterior = new Date(ahora.getFullYear(), ahora.getMonth() - 1, 1);
    const finMesAnterior = new Date(ahora.getFullYear(), ahora.getMonth(), 0);
    fechaDesde = mesAnterior.toISOString().split('T')[0];
    fechaHasta = finMesAnterior.toISOString().split('T')[0];
    break;
  case 'mes_actual':
  default:
    fechaDesde = new Date(ahora.getFullYear(), ahora.getMonth(), 1).toISOString().split('T')[0];
    fechaHasta = ahora.toISOString().split('T')[0];
    break;
}

