  total_visitas: respuestaOdoo.reduce((sum, v) => sum + (v.visit_count || 1), 0),
  total_paginas_vistas: respuestaOdoo.reduce((sum, v) => sum + (v.page_count || 0), 0),
  promedio_paginas_por_visita: 0,
  tiempo_promedio_sesion: 0,
  paises: {},
  idiomas: {},
  zonas_horarias: {},
  visitantes_con_multiple_visitas: 0
};

// Calcular promedios
if (estadisticas.total_visitas > 0) {
  estadisticas.promedio_paginas_por_visita = (estadisticas.total_paginas_vistas / estadisticas.total_visitas).toFixed(1);
}

// Procesar países, idiomas y zonas horarias
respuestaOdoo.forEach(visitante => {
  // Países
  const pais = visitante.country_id ? visitante.country_id[1] : 'Desconocido';
  estadisticas.paises[pais] = (estadisticas.paises[pais] || 0) + 1;

  // Idiomas
  const idioma = visitante.lang_id ? visitante.lang_id[1] : 'Desconocido';
  estadisticas.idiomas[idioma] = (estadisticas.idiomas[idioma] || 0) + 1;

  // Zonas horarias
  const timezone = visitante.timezone || 'Desconocido';
  estadisticas.zonas_horarias[timezone] = (estadisticas.zonas_horarias[timezone] || 0) + 1;

  // Visitantes con múltiples visitas
  if ((visitante.visit_count || 1) > 1) {
    estadisticas.visitantes_con_multiple_visitas++;
  }

  // Calcular tiempo promedio de sesión (aproximado)
  if (visitante.time_since_last_action) {
    estadisticas.tiempo_promedio_sesion += visitante.time_since_last_action;
  }
});

// Finalizar cálculo de tiempo promedio
if (respuestaOdoo.length > 0) {
  estadisticas.tiempo_promedio_sesion = Math.round(estadisticas.tiempo_promedio_sesion / respuestaOdoo.length);
}

