console.log('📝 Descripción del AI:', descripcionAI);
console.log('📅 Fecha del AI:', fechaAI);
console.log('👤 empleado_nombre (user_name):', userName);
console.log('📧 odoo_user (login/email para desambiguar):', odooUser);

// 🔧 BACKUP: Si el AI Agent no detectó el importe, extraerlo del mensaje
if (importe === 0 || !importe) {
  console.log('⚠️ AI Agent no detectó importe, extrayendo del mensaje...');
  const mensajeCompleto = mensajeOriginal;

  // Patrones para detectar importes: 25€, 25 €, 25 euros, €25, etc.
  const patronesImporte = [
    /([0-9]+(?:[.,][0-9]{1,2})?)\s*€/i,
    /([0-9]+(?:[.,][0-9]{1,2})?)\s*euros?/i,
    /€\s*([0-9]+(?:[.,][0-9]{1,2})?)/i,
    /([0-9]+(?:[.,][0-9]{1,2})?)\s*eur/i
  ];

  for (const patron of patronesImporte) {
    const match = mensajeCompleto.match(patron);
    if (match) {
      const importeStr = match[1].replace(',', '.'); // Normalizar decimales
      importe = parseFloat(importeStr);
      console.log('✅ Importe extraído del mensaje:', importe, '€');
      break;
    }
  }

  if (importe === 0) {
    console.log('❌ No se pudo extraer importe del mensaje');
  }
}

// Procesar fecha
let fecha = new Date().toISOString().split('T')[0]; // Por defecto hoy
if (fechaAI === 'ayer' || /\bayer\b/i.test(mensajeOriginal)) {
  const ayer = new Date();
  ayer.setDate(ayer.getDate() - 1);
  fecha = ayer.toISOString().split('T')[0];
}

// ===== EMPLEADO =====
// Requisito: empleado_nombre debe ser SIEMPRE el user_name.
// Usaremos odoo_user por separado para desambiguar en la búsqueda (email/login).
const empleadoNombre = userName;

// === Imagen/recibo adjunto ===
let tieneImagen = false;
let imagenFileId = null;
let imgData = null;
try {
  // Obtener desde 'Extraer Datos' usando el nuevo campo img_data
  const datosOriginales = $('Extraer Datos').first().json;
  imgData = datosOriginales.img_data || null;
  tieneImagen = datosOriginales.tiene_imagen || false;
  imagenFileId = datosOriginales.imagen_file_id || null;

  console.log('=== INFO IMAGEN DESDE EXTRAER DATOS ===');
  console.log('Tiene imagen:', tieneImagen);
  console.log('File ID:', imagenFileId);
  console.log('Datos imagen completos (img_data):', imgData ? 'PRESENTE' : 'NO DISPONIBLE');

  if (imgData) {
    console.log('Detalles img_data:', JSON.stringify(imgData, null, 2));
    console.log('File ID desde img_data:', imgData.file_id);
    console.log('Tamaño archivo:', imgData.file_size);
    console.log('Dimensiones:', imgData.width + 'x' + imgData.height);
    console.log('Timestamp captura:', imgData.timestamp);
  }
} catch (e) {
  console.log('ERROR: No se pudo obtener info de imagen desde Extraer Datos:', e.message);
  // Fallback: intentar desde datos actuales si están disponibles
  tieneImagen = datos.tiene_imagen || false;
  imagenFileId = datos.imagen_file_id || null;
  imgData = null;
}

console.log('📊 DATOS PROCESADOS:');
console.log('💰 Importe:', importe);
console.log('📅 Fecha:', fecha);
console.log('📝 Descripción:', descripcionAI);
console.log('👤 Empleado (por nombre):', empleadoNombre);
console.log('📧 Empleado (odoo_user para desambiguar):', odooUser);
console.log('📸 Imagen adjunta:', tieneImagen ? 'SÍ' : 'NO');

// Validación mínima
if (!importe || importe <= 0) {
  throw new Error('❌ No se pudo extraer el importe del gasto. Importe recibido: ' + importe);
}

return {
  datos_gasto: {
    importe: importe,
    descripcion: descripcionAI,
    fecha: fecha,
    chat_id: chatId,
    user_name: userName,     // lo mantenemos
    odoo_user: odooUser,     // ✅ clave para desambiguar en la búsqueda
    user_id: userId,
    empleado_nombre: empleadoNombre, // ✅ SIEMPRE el user_name
    tiene_imagen: tieneImagen,
    imagen_file_id: imagenFileId,
    img_data: imgData        // campo completo de la imagen
  },
  uid: uid,
  mensaje_usuario: mensajeOriginal
};

