// Preparar creación de pedido de venta en Odoo
const datosDirectos = $json;
const uid = $('Autenticar Odoo').item.json.result;

// Obtener datos desde Router de Acciones FIXED (seguro)
const datosCompletos = $('Router de Acciones FIXED').first()?.json || {};

// Intentar obtener el perfil del usuario (primero del router, luego del preparador)
const perfil =
  datosCompletos.cliente_perfil ||
  $('Preparar Datos AI Agent').first()?.json?.cliente_perfil ||
  null;

console.log('🛒 === PREPARAR CREAR PEDIDO VENTA ===');
console.log('📊 Datos directos recibidos:', JSON.stringify(datosDirectos, null, 2));
console.log('📊 Datos completos del router:', JSON.stringify(datosCompletos, null, 2));
console.log('👤 Perfil detectado:', JSON.stringify(perfil, null, 2));

// Cliente: 1) el que venga en la intención  2) el nombre del perfil del usuario
const clienteNombre =
  datosCompletos.cliente ||
  datosDirectos.cliente ||
  (perfil?.name || null);

const productos = datosCompletos.productos || datosDirectos.productos || [];
const chatId = datosCompletos.chat_id || datosDirectos.chat_id;

console.log('👤 Cliente extraído:', clienteNombre);
console.log('📦 Productos extraídos:', JSON.stringify(productos, null, 2));
console.log('💬 Chat ID:', chatId);
console.log('🔑 UID:', uid);

// Validar que tenemos uid
if (!uid) {
  throw new Error('No se pudo obtener el UID de autenticación');
}

// Validar cliente (nombre o perfil)
if (!clienteNombre && !perfil) {
  console.log('❌ Error: No se encontró cliente ni perfil en los datos');
  console.log('📊 Datos disponibles:', { datosDirectos, datosCompletos, perfil });
  throw new Error('No se encontró el cliente (ni nombre ni perfil disponible)');
}

// Validar que tenemos productos
if (!productos || productos.length === 0) {
  throw new Error('No se encontraron productos para el pedido');
}

console.log('✅ Validación exitosa - Preparando datos para búsqueda/creación de cliente');

return {
  cliente_nombre: clienteNombre,    // puede venir de la intención o del perfil
  cliente_perfil: perfil,           // ← se arrastra para creación si no existe en Odoo
  productos: productos,
  chat_id: chatId,
  uid: uid,
  datos_originales: datosCompletos
};

// Buscar cliente SOLO por email (misma estructura, sin fallback y sin nombre/phone)
const datos = $json;
const uid = datos.uid || $('Autenticar Odoo').item.json.result;
if (!uid) throw new Error('No se pudo obtener UID');

// Email desde perfil (preferente). Si no existe, no podemos buscar por email.
const emailRaw = (datos.cliente_perfil?.email || '').toString().trim();
if (!emailRaw) {
  throw new Error('Falta cliente_perfil.email. No puedo buscar cliente SOLO por email.');
}

const email = emailRaw.toLowerCase();

console.log('📧 === BUSCAR CLIENTE SOLO POR EMAIL ===');
console.log('📧 Email a buscar:', email);

// Payload: search_read por email exacto case-insensitive
const payloadEmail = {
  jsonrpc: '2.0',
  method: 'call',
  params: {
    service: 'object',
    method: 'execute_kw',
    args: [
      'lsanchezgoshawk-bot-main-27977821',
      uid,
      'admin',
      'res.partner',
      'search_read',
      [[
        ['email', '=ilike', email],
        ['active', '=', true],
      ]],
      {
        fields: ['id', 'name', 'email', 'customer_rank', 'parent_id', 'type'],
        limit: 20,
      },
    ],
  },
  id: Math.floor(Math.random() * 1000000),
};

return {
  payload: payloadEmail,
  // Mantengo tu estructura
  payload_fallback: null,          // ya no se usa
  cliente_nombre: datos.cliente_nombre || null,  // se arrastra por compatibilidad, pero NO se usa
  email_busqueda: email,
  chat_id: datos.chat_id,
  uid,
  productos: datos.productos || [],
  cliente_perfil: datos.cliente_perfil || null,
  metodo_busqueda: 'email_search_read'
};

// Procesar resultado de search_read (cliente encontrado por email)
const datos = $('Buscar Cliente').item.json;
const resultado = Array.isArray($json.result) ? $json.result : [];

const emailBuscado = (datos.cliente_email || datos.cliente_perfil?.email || '').toString().trim().toLowerCase();
console.log('📋 === PROCESAR CLIENTE POR EMAIL (search_read) ===');
console.log('📧 Email buscado:', emailBuscado);
console.log('📊 Resultado:', JSON.stringify(resultado, null, 2));

let clienteId = null;
let clienteEncontrado = false;
let clienteDatos = null;

if (resultado.length > 0) {
  // Filtrar exacto por email (por seguridad)
  const exactos = resultado.filter(p =>
    (p.email || '').toString().trim().toLowerCase() === emailBuscado
  );

  const lista = exactos.length ? exactos : resultado;

  // Elegir "mejor": mayor customer_rank, si empate el más reciente
  const elegido = lista.reduce((best, cur) => {
    const b = Number(best.customer_rank || 0);
    const c = Number(cur.customer_rank || 0);
    if (c !== b) return c > b ? cur : best;
    return (cur.id || 0) > (best.id || 0) ? cur : best;
  }, lista[0]);

  clienteId = elegido.id;
  clienteEncontrado = true;
  clienteDatos = {
    id: elegido.id,
    name: elegido.name,
    email: elegido.email,
    customer_rank: elegido.customer_rank || 0,
    parent_id: elegido.parent_id || null,
    is_company: elegido.is_company ?? null,
    type: elegido.type || null
  };

  console.log('✅ Cliente seleccionado por email:', clienteDatos);
} else {
  console.log(`ℹ️ No se encontró cliente con email "${emailBuscado}"`);

  // Si no existe, prepara datos para crear cliente (usando el perfil si existe)
  const perfil = datos.cliente_perfil || {};
  clienteDatos = {
    name: perfil.name || datos.cliente_nombre || 'Cliente',
    email: perfil.email || emailBuscado || null,
    phone: perfil.phone || perfil.mobile || null,
    street: perfil.street || null,
    zip: perfil.zip || null,
    city: perfil.city || null,
    customer_rank: 1,
    is_company: false
  };

  clienteEncontrado = false;
}

// Mantener tu estructura de salida para que el resto del flujo no cambie
return {
  cliente_id: clienteId,
  cliente_encontrado: clienteEncontrado,
  cliente_datos: clienteDatos,
  crear_cliente: !clienteEncontrado,
  total_resultados: resultado.length,
  metodo_busqueda: 'email_search_read',
  ...datos
};

// === CREAR PEDIDO VENTA (tolerante a ramas no ejecutadas) ===

// Helper seguro: lee .json de otro nodo sin petar si no se ejecutó
function safeJson(nodeName) {
  try {
    const it = $items(nodeName, 0, 0);
    return (Array.isArray(it) && it[0] && it[0].json) ? it[0].json : null;
  } catch (e) { return null; }
}

// Datos inmediatos de la rama actual
const cur = $json || {};

// Posibles fuentes (algunas no existirán según la rama)
const desdeFusion = safeJson('Fusionar Correcciones con Original') || {};
const prepBusq    = safeJson('Preparar Búsqueda Productos') || {};
const httpFixed   = safeJson('HTTP Buscar Productos FIXED') || {};
const httpFixed1  = safeJson('HTTP Buscar Productos FIXED1') || {};
const auth        = safeJson('Autenticar Odoo') || {};

// UID / Cliente / Chat
const uid       = cur.uid || prepBusq.uid || desdeFusion.uid || auth.result;
const clienteId = cur.cliente_id ?? prepBusq.cliente_id ?? desdeFusion.cliente_id ?? null;
const clienteDt = cur.cliente_datos || prepBusq.cliente_datos || desdeFusion.cliente_datos || {};
const chatId    = cur.chat_id || prepBusq.chat_id || desdeFusion.chat_id;

// 1) Si venimos de la rama SIN correcciones, ya tenemos matching hecho:
//    "Preparar Productos" dejó lineas_preconfirmadas listas para crear el pedido.
let lineasPedido = [];
if (Array.isArray(cur.lineas_preconfirmadas) && cur.lineas_preconfirmadas.length > 0) {
  lineasPedido = cur.lineas_preconfirmadas.map(l => ({
    product_id: l.product_id,
    product_uom_qty: Number(l.product_uom_qty || 0),
    price_unit: Number(l.price_unit || 0),
    name: l.name,
  }));
}

// 2) Si no tenemos líneas aún (p. ej. venimos por la rama con correcciones),
//    armamos el pool y resolvemos contra productos solicitados/fusionados.
if (lineasPedido.length === 0) {
  const pool = Array.isArray(cur.result) ? cur.result
             : Array.isArray(httpFixed1?.result) ? httpFixed1.result
             : Array.isArray(httpFixed?.result)  ? httpFixed.result
             : [];

  // Productos priorizando los fusionados; si no, los que vengan en esta rama
  const productosSolicitados =
    (Array.isArray(desdeFusion.productos) && desdeFusion.productos.length > 0)
      ? desdeFusion.productos
      : (cur.productos || prepBusq.productos || []);

  if (!Array.isArray(productosSolicitados) || productosSolicitados.length === 0) {
    throw new Error('No se recibieron productos para el pedido');
  }

  // Normalización y matching
  const normalize = (s) => (s || '')
    .toString()
    .normalize('NFD').replace(/[\u0300-\u036f]/g,'')
    .toLowerCase()
    .replace(/(\d)\s+(kg|g|mg|l|ml|cl|cm|mm|m|u|uds?)/g, '$1$2')
    .replace(/\s+/g, ' ')
    .trim();

  const STOP = new Set(['con','de','y','para','en','la','el','los','las','por','del','a','al']);
  const tokens = (s) => {
    const n = normalize(s);
    if (!n) return [];
    return n.split(/[ \-_/]+/).filter(t => t && !STOP.has(t));
  };
  const jaccard = (a,b) => {
    const A = new Set(tokens(a)), B = new Set(tokens(b));
    const inter = [...A].filter(x=>B.has(x)).length;
    const uni = new Set([...A,...B]).size;
    return uni === 0 ? 0 : inter/uni;
  };

  const byNorm = new Map(pool.map(p => [normalize(p.name), p]));
  const ambiguos = [];

  for (const sol of productosSolicitados) {
    const nombre = (sol?.nombre || '').toString();
    const cant   = Number(sol?.cantidad || 1);
    const ns     = normalize(nombre);

    if (byNorm.has(ns)) {
      const p = byNorm.get(ns);
      lineasPedido.push({
        product_id: p.id,
        product_uom_qty: cant,
        price_unit: p.list_price,
        name: p.name,
      });
      continue;
    }

    // Fuzzy por tokens (suave)
    let best = null, bestScore = 0;
    for (const p of pool) {
      const s = jaccard(nombre, p.name);
      if (s > bestScore) { best = p; bestScore = s; }
    }
    if (best && bestScore >= 0.85) {
      lineasPedido.push({
        product_id: best.id,
        product_uom_qty: cant,
        price_unit: best.list_price,
        name: best.name,
      });
    } else {
      ambiguos.push({ solicitado: nombre, cantidad: cant });
    }
  }

  if (ambiguos.length > 0 && lineasPedido.length === 0) {
    // Si por alguna razón seguimos con ambigüedad aquí, pedimos corrección
    return {
      requiere_correccion: true,
      texto_correccion: '🔎 Algunos productos necesitan corrección. Envíalos como: Nombre x Cantidad, separados por comas.',
      productos_ambiguos: ambiguos,
      productos_solicitados: productosSolicitados,
      cliente_id: clienteId,
      cliente_datos: clienteDt,
      chat_id: chatId,
      uid,
    };
  }
}

// Validaciones finales
if (!uid) throw new Error('No se pudo obtener el UID de autenticación');
if (!clienteId) throw new Error('No se encontró el ID del cliente');
if (lineasPedido.length === 0) throw new Error('No hay líneas válidas para crear el pedido');

// Payload Odoo
const datosPedido = {
  partner_id: clienteId,
  state: 'draft',
  order_line: lineasPedido.map(l => [0,0,l]),
};

const jsonRpcPayload = {
  jsonrpc: '2.0',
  method: 'call',
  params: {
    service: 'object',
    method: 'execute_kw',
    args: [
      'lsanchezgoshawk-bot-main-27977821',
      uid,
      'admin',
      'sale.order',
      'create',
      [datosPedido]
    ]
  },
  id: Math.floor(Math.random() * 1e6),
};

return {
  payload: jsonRpcPayload,
  lineas_pedido: lineasPedido,
  productos_no_encontrados: [], // ya resueltos arriba
  datos_pedido: datosPedido,
  cliente_id: clienteId,
  cliente_datos: clienteDt,
  chat_id: chatId,
  uid
};

// Preparar memoria para corrección de pedido de venta (alimentado desde "Preparar Productos")
const d = $('Preparar Productos').item.json;

return {
  chat_id: Number(d.chat_id) || 0,
  esperando_confirmacion: 'corregir_pedido_venta',
  ultimo_mensaje_bot: d.texto_correccion || 'Indica los productos correctos en un solo mensaje (nombre x cantidad, separados por comas).',
  datos_pendientes: {
    accion_original: 'crear_pedido_venta',
    cliente: d.cliente_datos?.name || '',
    cliente_id: d.cliente_id || null,
    productos_originales: Array.isArray($('Preparar Búsqueda Productos').item.json.productos) ? $('Preparar Búsqueda Productos').item.json.productos : [],
    productos_ambiguos: d.productos_ambiguos || [],
    lineas_preconfirmadas: d.lineas_preconfirmadas || []
  }
};

