// Construir mensaje de confirmación o corrección para pedido de venta
const datos = $('Crear Pedido Venta').item.json;
const cliente = datos.cliente_datos?.name || 'Cliente';
const lineas = Array.isArray(datos.lineas_pedido) ? datos.lineas_pedido : [];

let esperando_confirmacion = 'crear_pedido_venta';
let texto = '';
let datosPendientes;

if (datos.requiere_correccion) {
  // Pedir corrección masiva de productos ambiguos o no encontrados
  texto = (datos.texto_correccion || '🔎 Necesito que corrijas algunos productos. Envía todos en un solo mensaje con formato: nombre x cantidad').toString();
  esperando_confirmacion = 'corregir_pedido_venta';
  datosPendientes = {
    accion_original: 'crear_pedido_venta',
    cliente: cliente,
    cliente_id: datos.cliente_id || null,
    productos_originales: Array.isArray($('Preparar Búsqueda Productos').item.json.productos) ? $('Preparar Búsqueda Productos').item.json.productos : (datos.productos || []),
    productos_ambiguos: datos.productos_ambiguos || [],
  };
} else {
  // Mensaje de confirmación normal
  texto = `📝 **Resumen del pedido de venta**\n\n`;
  texto += `👤 **Cliente:** ${cliente}\n`;
  texto += `📦 **Productos:**\n`;
  for (const l of lineas) {
    texto += `• ${l.name} - Cantidad: ${l.product_uom_qty} - Precio: €${l.price_unit}\n`;
  }
  if (Array.isArray(datos.productos_no_encontrados) && datos.productos_no_encontrados.length > 0) {
    texto += `\n⚠️ **No encontrados:** ${datos.productos_no_encontrados.join(', ')}\n`;
  }
  texto += `\n❓ **¿Confirmo este pedido de venta?** Responde *si* para continuar.`;

  datosPendientes = {
    accion_original: 'crear_pedido_venta',
    cliente: cliente,
    cliente_id: datos.cliente_id || null,
    productos: Array.isArray($('Preparar Búsqueda Productos').item.json.productos) ? $('Preparar Búsqueda Productos').item.json.productos : (datos.productos || []),
  };
}

return {
  chat_id: datos.chat_id,
  texto_confirmacion: texto,
  ultimo_mensaje_bot: texto,
  esperando_confirmacion,
  datos_pendientes: datosPendientes,
  payload: datos.payload,
  lineas_pedido: datos.lineas_pedido,
  productos_no_encontrados: datos.productos_no_encontrados,
  datos_pedido: datos.datos_pedido,
  cliente_id: datos.cliente_id,
  cliente_datos: datos.cliente_datos,
  uid: datos.uid
};

