const datosAI = $json;
const uid = $('Autenticar Odoo').item.json.result;
const chatId = datosAI.chat_id;

if (!uid) throw new Error('No se pudo obtener UID de Odoo');

// Leer memoria SIN usarla como input del nodo
const memRow = $('Leer Memoria Antes De Validar').first()?.json || {};
const memRaw = memRow.datos_pendientes;
const mem = memRaw ? (typeof memRaw === 'string' ? JSON.parse(memRaw) : memRaw) : {};

// ===================== Helpers TZ (Europe/Madrid) =====================
const TZ = 'Europe/Madrid';
const pad = (n) => String(n).padStart(2, '0');

const ymdMadrid = (date) => {
  const parts = new Intl.DateTimeFormat('en-CA', {
    timeZone: TZ,
    year: 'numeric', month: '2-digit', day: '2-digit',
  }).formatToParts(date);
  const y = parts.find(p => p.type === 'year').value;
  const m = parts.find(p => p.type === 'month').value;
  const d = parts.find(p => p.type === 'day').value;
  return `${y}-${m}-${d}`;
};

const addDaysYMD = (ymd, days) => {
  const [Y, M, D] = ymd.split('-').map(Number);
  const base = new Date(Date.UTC(Y, M - 1, D));
  base.setUTCDate(base.getUTCDate() + days);
  return ymdMadrid(base);
};

const isYMD = (s) => /^\d{4}-\d{2}-\d{2}$/.test(String(s || ''));

// Timestamp fiable (del mensaje actual o de memoria o ahora)
const ts =
  (datosAI.timestamp && new Date(datosAI.timestamp)) ||
  (mem.timestamp && new Date(mem.timestamp)) ||
  new Date();

const hoyYMD = ymdMadrid(ts);

const resolverFechaRelativa = (rel) => {
  const r = (rel || '').toString().toLowerCase().trim();
  if (r === 'hoy') return hoyYMD;
  if (r === 'mañana' || r === 'manana') return addDaysYMD(hoyYMD, 1);
  if (r === 'pasado_mañana' || r === 'pasado_manana') return addDaysYMD(hoyYMD, 2);
  return '';
};

// ===================== Normalizaciones =====================
const normalizarHora = (txt) => {
  if (!txt) return '';
  const m = String(txt).match(/(\d{1,2})[:h\.–-]?(\d{2})?/i);
  if (!m) return '';
  const hh = String(m[1]).padStart(2, '0');
  const mm = String(m[2] ?? '00').padStart(2, '0');
  return `${hh}:${mm}`;
};

const normalizarFechaAbsoluta = (txt) => {
  if (!txt) return '';
  const s = String(txt).trim().toLowerCase();

  // 1) Ya viene como YYYY-MM-DD
  if (/^\d{4}-\d{2}-\d{2}$/.test(s)) return s;

  // 2) Limpiar día de la semana y cortar partes de hora/frases accesorias
  const soloFecha = s
    .replace(/^(lunes|martes|miércoles|miercoles|jueves|viernes|sábado|sabado|domingo)\s*,?\s+/i, '')
    .replace(/\s+a las\s+.*$/i, '')
    .replace(/\s+de\s+\d{1,2}[:h\.–-]\d{2}.*/i, '')
    .replace(/\s+\d{1,2}[:h\.–-]\d{2}.*/i, '')
    .trim();

  const mesesLargos = ['enero','febrero','marzo','abril','mayo','junio','julio','agosto','septiembre','octubre','noviembre','diciembre'];
  const mesAbrevMap = { ene:'enero', feb:'febrero', mar:'marzo', abr:'abril', may:'mayo', jun:'junio', jul:'julio', ago:'agosto', sep:'septiembre', oct:'octubre', nov:'noviembre', dic:'diciembre' };
  const normalizarMes = (m) => {
    const base = m.normalize('NFD').replace(/[\u0300-\u036f]/g, '');
    if (mesesLargos.includes(base)) return base;
    const ab = base.slice(0,3);
    return mesAbrevMap[ab] || '';
  };

  const yearNow = new Date().getFullYear();

  // 3) “6 de octubre” o “6 de oct” con año opcional “de 2025”
  let m = soloFecha.match(/(\d{1,2})\s*de\s*([a-záéíóú]+)(?:\s*de\s*(\d{4}))?/i);
  if (m) {
    const d = String(m[1]).padStart(2, '0');
    const mesNorm = normalizarMes(m[2]);
    if (mesNorm) {
      const mm = String(mesesLargos.indexOf(mesNorm) + 1).padStart(2, '0');
      const y = m[3] ? m[3] : String(yearNow);
      return `${y}-${mm}-${d}`;
    }
  }

  // 4) “06/10[/2025]” o “06-10[-2025]”
  m = soloFecha.match(/^(\d{1,2})[\/\-](\d{1,2})(?:[\/\-](\d{2,4}))?$/);
  if (m) {
    const d  = String(m[1]).padStart(2, '0');
    const mo = String(m[2]).padStart(2, '0');
    const y  = m[3] ? (String(m[3]).length === 2 ? `20${m[3]}` : m[3]) : String(yearNow);
    return `${y}-${mo}-${d}`;
  }

  // 5) “6 oct[ 2025]”
  m = soloFecha.match(/(\d{1,2})\s+([a-záéíóú]+)(?:\s+(\d{4}))?/i);
  if (m) {
    const d = String(m[1]).padStart(2, '0');
    const mesNorm = normalizarMes(m[2]);
    if (mesNorm) {
      const mm = String(mesesLargos.indexOf(mesNorm) + 1).padStart(2, '0');
      const y  = m[3] ? m[3] : String(yearNow);
      return `${y}-${mm}-${d}`;
    }
  }

  return '';
};
// ===================== Fin normalizaciones =====================

// ⬇️ NUEVO: leer fecha_relativa (del AI o de memoria)
const fechaRelNueva = (datosAI.fecha_relativa || '').toString().trim();
const fechaRelMem = (mem.fecha_relativa || '').toString().trim();
const fechaRelativa = fechaRelNueva || fechaRelMem || '';

// Nuevos del mensaje
const tituloNuevo      = (datosAI.titulo || '').trim();

// fecha absoluta (si viene)
const fechaAbsNueva = normalizarFechaAbsoluta(datosAI.fecha);

// si hay fecha_relativa, la convertimos a YYYY-MM-DD real
const fechaFromRel = resolverFechaRelativa(fechaRelativa);

// FECHA final: prioriza fecha relativa resuelta > fecha absoluta nueva > memoria
const fecha = fechaFromRel || fechaAbsNueva || normalizarFechaAbsoluta(mem.fecha) || '';

const horaInicioNueva  = normalizarHora(datosAI.hora_inicio);
const horaFinNueva     = normalizarHora(datosAI.hora_fin);
const descripcionNueva = (datosAI.descripcion || '').trim();

// Participantes entrantes (lista o usuario)
const participantesEntrada = Array.isArray(datosAI.participantes)
  ? datosAI.participantes
  : (datosAI.usuario ? [String(datosAI.usuario).trim()] : []);

// Fusionar con memoria (no perder previos)
const participantesMem = Array.isArray(mem.participantes) ? mem.participantes : [];
const participantes = Array.from(new Set(
  [...participantesMem, ...participantesEntrada]
    .map(p => String(p || '').trim())
    .filter(Boolean)
));

const titulo      = tituloNuevo      || mem.titulo      || '';
const hora_inicio = horaInicioNueva  || mem.hora_inicio || '';
const hora_fin    = horaFinNueva     || mem.hora_fin    || '';
const descripcion = descripcionNueva || mem.descripcion || '';

// ✅ Si falta hora_fin, deja vacío y lo resolverás luego (en Preparar Datos ya lo toleras)
let datosCompletos = true;
const errores = [];
if (!titulo)      { datosCompletos = false; errores.push('título del evento'); }
if (!fecha)       { datosCompletos = false; errores.push('fecha'); }
if (!hora_inicio) { datosCompletos = false; errores.push('hora de inicio'); }
if (participantes.length === 0) { datosCompletos = false; errores.push('participantes'); }

const resultado = {
  datos_completos: datosCompletos,
  titulo,
  fecha,                 // ✅ ya es YYYY-MM-DD real incluso si el usuario dijo "mañana"
  fecha_relativa: fechaRelativa || null, // guardamos para trazabilidad/memoria
  hora_inicio,
  hora_fin,
  participantes,
  descripcion,
  chat_id: chatId,
  uid,
  timestamp: datosAI.timestamp || mem.timestamp || new Date().toISOString(),
  debug_fecha: {
    hoy_madrid: hoyYMD,
    fecha_relativa: fechaRelativa || null,
    fecha_abs_nueva: datosAI.fecha || null,
    fecha_resuelta: fecha || null
  },
  datos_originales: datosAI,
};

if (!datosCompletos) {
  const faltantes = participantes.length > 0
    ? errores.filter(f => f.toLowerCase() !== 'participantes')
    : errores;

  const datosPendientes = {
    accion_original: 'crear_evento_calendario',
    titulo,
    fecha,                 // ✅ guardamos la fecha ya resuelta
    fecha_relativa: fechaRelativa || '',
    hora_inicio,
    hora_fin,
    participantes,
    descripcion,
    datos_faltantes: faltantes,
    timestamp: resultado.timestamp,
  };

  const piezas = [];
  if (titulo)       piezas.push(`📝 **Título:** ${titulo}`);
  if (fechaRelativa) piezas.push(`📆 **Fecha:** ${fechaRelativa} (${fecha})`);
  else if (fecha)   piezas.push(`📆 **Fecha:** ${fecha}`);
  if (hora_inicio)  piezas.push(`🕐 **Hora:** ${hora_inicio}${hora_fin && hora_fin !== hora_inicio ? ` - ${hora_fin}` : ''}`);
  if (participantes.length > 0) piezas.push(`👥 **Participantes:** ${participantes.join(', ')}`);
  if (descripcion)  piezas.push(`📄 **Descripción:** ${descripcion}`);

  const mensaje = `📅 **Creando evento de calendario**\n\n${piezas.length ? piezas.join('\n') + '\n\n' : ''}❓ **Me faltan estos datos:**\n${faltantes.join(', ')}\n\n💬 Por favor, proporciona la información faltante y yo completaré el evento automáticamente.`;

  resultado.datos_pendientes = datosPendientes;
  resultado.esperando_confirmacion = 'crear_evento_calendario';
  resultado.ultimo_mensaje_bot = mensaje;
  resultado.mensaje_usuario = mensaje;
}

return resultado;

// Buscar participantes del evento en Odoo
const datos = $json;
const uid = datos.uid;
const participantesEntrada = Array.isArray(datos.participantes) ? datos.participantes : [];

console.log('👥 === BUSCAR PARTICIPANTES EN ODOO ===');
console.log('📋 Participantes a buscar (raw):', participantesEntrada);

// Normalizar: quitar vacíos/espacios, case-insensitive y sin duplicados
const participantes = Array.from(
  new Set(
    participantesEntrada
      .map((p) => (p ?? '').toString().trim())
      .filter((p) => p.length > 0)
      .map((p) => p.toLowerCase())
  )
);

console.log('📋 Participantes a buscar (normalizados):', participantes);

// Hojas del dominio
const hojas = participantes.map((p) => ['name', 'ilike', p]);

// Construir dominio OR PLANO (sin anidar):
// - 0 hojas: [['id','=',-1]] (sin resultados)
// - 1 hoja: [ ['name','ilike','laura'] ]
// - 2 hojas: [ '|', ['name','ilike','a'], ['name','ilike','b'] ]
// - 3 hojas: [ '|','|', ['name','ilike','a'], ['name','ilike','b'], ['name','ilike','c'] ]
let domain;
if (hojas.length === 0) {
  domain = [['id', '=', -1]];
} else if (hojas.length === 1) {
  domain = [hojas[0]];
} else {
  const pipes = Array(hojas.length - 1).fill('|');
  domain = [...pipes, ...hojas];
}

console.log('🔍 Dominio OR PLANO (final):', JSON.stringify(domain, null, 2));

// En execute_kw, ARGS = [domain]
const jsonRpcPayload = {
  jsonrpc: '2.0',
  method: 'call',
  params: {
    service: 'object',
    method: 'execute_kw',
    args: [
      'lsanchezgoshawk-bot-main-27977821',
      uid,
      'admin',
      'res.partner',
      'search_read',
      [domain],
      {
        fields: ['id', 'name', 'email'],
        limit: 50,
      },
    ],
  },
  id: Math.floor(Math.random() * 1000000),
};

console.log('🚀 Payload participantes generado:', JSON.stringify(jsonRpcPayload, null, 2));

return {
  payload: jsonRpcPayload,
  participantes_solicitados: participantesEntrada,
  titulo: datos.titulo,
  fecha: datos.fecha,
  hora_inicio: datos.hora_inicio,
  hora_fin: datos.hora_fin,
  descripcion: datos.descripcion,
  chat_id: datos.chat_id,
  uid: datos.uid,
  datos_originales: datos.datos_originales,
};

// Formatear respuesta del evento creado (con enlace a Odoo)
const resultado = $json; // respuesta del HTTP Crear Evento
const datosEvento = $('Preparar Datos').item.json;

// ⚙️ Cambia esto si tu URL base es distinta
const baseUrl = 'https://lsanchezgoshawk-n8npruebas.odoo.com';

console.log('📅 === FORMATEAR RESPUESTA EVENTO ===');
console.log('📋 Resultado Odoo:', JSON.stringify(resultado, null, 2));
console.log('📋 Datos evento:', JSON.stringify(datosEvento, null, 2));

let mensaje;
let enlaceOdoo = null;

if (resultado.error) {
  // Error al crear el evento
  console.error('❌ Error al crear evento:', resultado.error);
  mensaje = `❌ **Error al crear el evento**\n\n🚫 ${resultado.error.data?.message || resultado.error.message || 'Error desconocido'}\n\nPor favor, verifica los datos e intenta de nuevo.`;
} else if (resultado.result) {
  // Evento creado exitosamente
  const eventoId = resultado.result;
  enlaceOdoo = `${baseUrl}/web#id=${eventoId}&model=calendar.event&view_type=form`;

  const participantesTexto = Array.isArray(datosEvento.participantes_encontrados) && datosEvento.participantes_encontrados.length > 0
    ? `👥 **Participantes:** ${datosEvento.participantes_encontrados.length} persona(s) invitada(s)`
    : '';

  const participantesNoEncontrados = Array.isArray(datosEvento.participantes_no_encontrados) && datosEvento.participantes_no_encontrados.length > 0
    ? `\n⚠️ **Nota:** No se encontraron: ${datosEvento.participantes_no_encontrados.join(', ')}`
    : '';

  mensaje =
    `✅ **¡Evento creado exitosamente!**\n\n` +
    `📅 **Evento:** ${datosEvento.titulo}\n` +
    `📆 **Fecha:** ${datosEvento.fecha}\n` +
    `🕐 **Hora:** ${datosEvento.hora_inicio}${datosEvento.hora_fin && datosEvento.hora_fin !== datosEvento.hora_inicio ? ` - ${datosEvento.hora_fin}` : ''}\n` +
    `${participantesTexto}${participantesNoEncontrados}\n` +
    `🆔 **ID:** ${eventoId}\n\n` +
    `🔗 **[Abrir en Odoo](${enlaceOdoo})**\n\n` +
    `¿Necesitas ayuda con algo más? 😊`;
} else {
  // Respuesta inesperada
  mensaje = `⚠️ **Respuesta inesperada**\n\nSe procesó la solicitud pero la respuesta no es la esperada. Por favor, verifica el calendario en Odoo.`;
}

return {
  chat_id: datosEvento.chat_id,
  mensaje,
  evento_id: resultado.result || null,
  enlace_odoo: enlaceOdoo,
  success: !resultado.error
};

// Extraer datos del mensaje para limitar módulos
const mensaje = ($json.mensaje_usuario || $json.mensaje_original || '').toString().trim();
const chatId = $json.chat_id;
const userId = $json.user_id;
const userName = $json.user_name;

// 1) Priorizar datos estructurados si vienen en el payload
let usuarioObjetivo = ($json.usuario_objetivo || '').toString().trim();
let modulosEntrada = Array.isArray($json.modulos) ? $json.modulos : [];

// Normalizador de módulos (array o string → array limpia)
function normalizarModulos(mods) {
  if (!mods) return [];
  if (Array.isArray(mods)) return mods.map(m => m?.toString().trim()).filter(Boolean);
  // dividir por coma o “ y ”
  return mods
    .toString()
    .split(/,| y /i)
    .map(s => s.trim())
    .filter(Boolean);
}

// Si faltan estructurados, intentar parsear el mensaje
if (!usuarioObjetivo || modulosEntrada.length === 0) {
  // Patrones típicos:
  //  a) "limitar el modulo <mods> a <user>"
  //  b) "limitar el módulo <mods> para <user>"
  //  c) "limitar modulos <mods> a <user>"
  //  d) "limitar modulos a <user> <mods>"
  // Usamos Unicode y nombres con espacios
  const patterns = [
    // singular: modulo ... a|para user
    /limitar\s+(?:el\s+)?m[oó]dulo\s+(?<mods>[\p{L}\d\s,_-]+?)\s+(?:a|para)\s+(?:usuario\s+)?(?<user>[\p{L}\d\s'.-]+)$/iu,
    // plural: modulos ... a|para user
    /limitar\s+m[oó]dulos?\s+(?<mods>[\p{L}\d\s,_-]+?)\s+(?:a|para)\s+(?:usuario\s+)?(?<user>[\p{L}\d\s'.-]+)$/iu,
    // plural invertido: limitar modulos a user mods
    /limitar\s+m[oó]dulos?\s+(?:a|para)\s+(?:usuario\s+)?(?<user>[\p{L}\d\s'.-]+)\s+(?<mods>[\p{L}\d\s,_-]+)$/iu,
  ];

  for (const re of patterns) {
    const m = mensaje.match(re);
    if (m?.groups) {
      const modsStr = (m.groups.mods || '').trim();
      const userStr = (m.groups.user || '').trim();
      if (!usuarioObjetivo && userStr) usuarioObjetivo = userStr;
      if (modulosEntrada.length === 0 && modsStr) modulosEntrada = normalizarModulos(modsStr);
      break;
    }
  }
}

// Limpieza final
usuarioObjetivo = usuarioObjetivo || '';
const modulos = normalizarModulos(modulosEntrada).map(m => m.toLowerCase());

// Validaciones
if (!usuarioObjetivo) {
  throw new Error('No se pudo extraer el usuario objetivo.');
}
if (modulos.length === 0) {
  throw new Error('No se pudieron extraer los módulos a limitar.');
}

return {
  chat_id: chatId,
  user_id: userId,
  user_name: userName,
  usuario_ejecutor: userId,
  usuario_objetivo: usuarioObjetivo,
  modulos: modulos,
  mensaje_original: mensaje
};

// Validación simple de auto-limitación
const datosExtraccion = $('Extraer Datos Limitar Módulos').item.json;
const datosBusqueda = $('Buscar Usuario Objetivo').item.json;

console.log('🔒 === VALIDAR AUTO-LIMITACIÓN ===');
console.log('📊 Datos extracción:', JSON.stringify(datosExtraccion, null, 2));
console.log('🔍 Datos búsqueda:', JSON.stringify(datosBusqueda, null, 2));

const adminUserId = datosExtraccion.user_id;
const modulos = datosExtraccion.modulos;

// El resultado puede ser un objeto directo o un array
let usuarioObjetivoId, usuarioObjetivoName;

if (Array.isArray(datosBusqueda)) {
  // Si es array, tomar el primer elemento
  usuarioObjetivoId = datosBusqueda[0]?.user_id;
  usuarioObjetivoName = datosBusqueda[0]?.user_name;
} else {
  // Si es objeto directo
  usuarioObjetivoId = datosBusqueda.user_id;
  usuarioObjetivoName = datosBusqueda.user_name;
}

console.log('👤 Admin ID:', adminUserId);
console.log('🎯 Usuario objetivo ID:', usuarioObjetivoId);
console.log('👤 Usuario objetivo nombre:', usuarioObjetivoName);

if (!usuarioObjetivoId) {
  throw new Error('❌ No se pudo obtener el ID del usuario objetivo');
}

// Verificar auto-limitación
if (adminUserId.toString() === usuarioObjetivoId.toString()) {
  throw new Error('❌ No puedes limitar tus propios permisos por seguridad.');
}

console.log('✅ Validación exitosa - Procediendo con la limitación');

// Pasar todos los datos necesarios
return {
  admin_user_id: adminUserId,
  usuario_objetivo_id: usuarioObjetivoId,
  usuario_objetivo_name: usuarioObjetivoName,
  modulos: modulos,
  modulos_string: modulos.join(',')
};

