// PROCESAR DATOS DE AUSENCIA DEL AI AGENT (con odoo_user para desambiguar)
const datos = $json;

const chatId = datos.chat_id;
const userName = (datos.user_name || 'Usuario').toString().trim();
const userId = datos.user_id;
const uid = $('Autenticar Odoo').item.json.result;

const mensajeOriginal = (datos.mensaje_original || '').trim();
const descripcionEntrada = (datos.descripcion || '').trim();

// 🔹 Nombre de empleado que viene del switch/AI (si lo hay)
const empleadoEntrada = (datos.empleado || '').toString().trim();

// 🔹 Determinar a quién se le registra la ausencia:
//    - Si el agente ha extraído "empleado" → usarlo
//    - Si no, usar el nombre del usuario de Telegram
const empleadoNombre = empleadoEntrada || userName;

// 🔹 Saber si se ha especificado un empleado distinto del usuario
const esEmpleadoDistintoUsuario =
  empleadoEntrada &&
  empleadoEntrada.toLowerCase() !== userName.toLowerCase();

let tipo = (datos.tipo || datos.tipo_ausencia || '').toString().trim();
let fechaInicio = (datos.fecha_inicio || '').toString().trim();
let fechaFin = (datos.fecha_fin || fechaInicio || '').toString().trim();
let descripcion = descripcionEntrada || 'Solicitud de ausencia';

// 🔹 Resolver odoo_user con fallbacks (login/email del empleado DEL PERFIL)
let odooUser = (
  datos.odoo_user
  || datos.cliente_perfil?.login
  || datos.cliente_perfil?.email
  || datos.user_mail
  || null
);
odooUser = odooUser ? odooUser.toString().trim() : null;

// 🔹 Si la ausencia es para OTRA PERSONA (ej: "para abigail peterson"),
//    NO usamos el email del usuario de Telegram para desambiguar,
//    porque eso forzaría a escoger siempre a Laura.
if (esEmpleadoDistintoUsuario) {
  odooUser = null;
}

console.log('🔧 AUSENCIA → empleado ENTRADA:', empleadoEntrada || '(no especificado)');
console.log('🔧 AUSENCIA → empleado USADO:', empleadoNombre);
console.log('🔧 AUSENCIA → user_name (Telegram):', userName);
console.log('🔧 AUSENCIA → odoo_user (login/email):', odooUser);

// Utilidades datetime Odoo
function toOdooDateTimeUTC(d) {
  const pad = (n) => String(n).padStart(2, '0');
  const z = new Date(d);
  return `${z.getUTCFullYear()}-${pad(z.getUTCMonth() + 1)}-${pad(z.getUTCDate())} ${pad(z.getUTCHours())}:${pad(z.getUTCMinutes())}:${pad(z.getUTCSeconds())}`;
}

// Imagen
let tieneImagen = false;
let imagenFileId = null;
let imgData = null;
try {
  const datosOriginales = $('Extraer Datos').first().json;
  imgData = datosOriginales.img_data || null;
  if (imgData && Array.isArray(imgData.photo) && imgData.photo.length) {
    imagenFileId = imgData.photo[imgData.photo.length - 1].file_id;
  } else if (imgData && imgData.document && imgData.document.file_id) {
    imagenFileId = imgData.document.file_id;
  } else if (datosOriginales.imagen_file_id) {
    imagenFileId = datosOriginales.imagen_file_id;
  }
  tieneImagen = Boolean(imagenFileId);
} catch (_) {
  imagenFileId = datos.imagen_file_id || null;
  tieneImagen = Boolean(imagenFileId);
  imgData = null;
}

// Inferir tipo si no viene
const msgLower = (mensajeOriginal + ' ' + descripcion).toLowerCase();
if (!tipo) {
  if (/(m[eé]dic|doctor|cita m[eé]dica)/i.test(msgLower)) tipo = 'Permiso Médico';
  else if (/vacaciones?/i.test(msgLower)) tipo = 'Vacaciones';
  else if (/permiso/i.test(msgLower)) tipo = 'Permiso';
  else if (/baja|enfermedad/i.test(msgLower)) tipo = 'Baja';
  else tipo = 'Permiso';
}

// Parsear rango horario SOLO desde texto
function parseHora(h) {
  const mm = String(h).match(/^(\d{1,2})(?::|\.|h)?(\d{2})?$/);
  if (!mm) return null;
  const hh = Math.min(23, parseInt(mm[1], 10));
  const mi = Math.min(59, mm[2] ? parseInt(mm[2], 10) : 0);
  return { hh, mi };
}

const textoHoras = `${descripcion} ${mensajeOriginal}`;
let horas = 0;
let rangoInicio = null;
let rangoFin = null;

// “12-13”, “12 a 13”, “12:00 a 13:00”
const mRango = textoHoras.match(/(\d{1,2}(?::\d{2})?)\s*(?:a|hasta|-)\s*(\d{1,2}(?::\d{2})?)/i);
if (mRango) {
  const h1 = parseHora(mRango[1]);
  const h2 = parseHora(mRango[2]);
  if (h1 && h2) {
    rangoInicio = h1; rangoFin = h2;
    const diff = (h2.hh + h2.mi/60) - (h1.hh + h1.mi/60);
    horas = diff > 0 ? diff : 0;
  }
}
if (!mRango) {
  const mHoras = textoHoras.match(/(\d+(?:[.,]\d+)?)\s*h(?:oras?)?/i) || textoHoras.match(/(\d+(?:[.,]\d+)?)\s*horas?/i);
  if (mHoras) {
    horas = parseFloat(mHoras[1].replace(',', '.'));
  } else if (/hora y media/i.test(textoHoras)) {
    horas = 1.5;
  } else if (/media hora/i.test(textoHoras)) {
    horas = 0.5;
  } else if (/\buna hora\b/i.test(textoHoras)) {
    horas = 1;
  }
}

// Construir date_from/date_to
let usaDateTime = false;
let date_from = null;
let date_to = null;

if (rangoInicio && rangoFin && fechaInicio) {
  usaDateTime = true;
  const base = new Date(`${fechaInicio}T00:00:00`);
  const ini = new Date(base);  ini.setHours(rangoInicio.hh, rangoInicio.mi, 0, 0);
  const fin = new Date(base);  fin.setHours(rangoFin.hh, rangoFin.mi, 0, 0);
  date_from = toOdooDateTimeUTC(ini);
  date_to   = toOdooDateTimeUTC(fin);
} else if (horas > 0 && fechaInicio) {
  usaDateTime = true;
  const ini = new Date(`${fechaInicio}T09:00:00`); // por defecto 09:00
  const fin = new Date(ini.getTime() + horas * 3600000);
  date_from = toOdooDateTimeUTC(ini);
  date_to   = toOdooDateTimeUTC(fin);
}

// Validaciones mínimas
const errores = [];
if (!tipo) errores.push('Tipo de ausencia');
if (!fechaInicio) errores.push('Fecha inicio');
if (!fechaFin) errores.push('Fecha fin');
if (errores.length) throw new Error('Faltan campos obligatorios: ' + errores.join(', '));

// 🔹 Aquí ya usamos empleadoNombre calculado arriba
return {
  datos_ausencia: {
    tipo,
    fecha_inicio: fechaInicio,
    fecha_fin: fechaFin,
    date_from: usaDateTime ? date_from : null,
    date_to: usaDateTime ? date_to : null,
    horas: horas || null,
    descripcion,
    chat_id: chatId,
    user_name: userName,          // quién lo pide por Telegram
    odoo_user: odooUser,          // solo cuando es para él/ella
    user_id: userId,
    empleado_nombre: empleadoNombre, // para quién es la ausencia (Laura o Abigail)
    empleado_origen: empleadoEntrada || null, // opcional, por si quieres loguearlo
    tiene_imagen: tieneImagen,
    imagen_file_id: imagenFileId,
    img_data: imgData
  },
  uid,
  mensaje_usuario: mensajeOriginal
};

