// PREPARAR CONSULTA DE TICKETS DEL SERVICIO DE ASISTENCIA
const datosUsuario = $json;
const chatId = datosUsuario.chat_id;
const uid = datosUsuario.uid;

console.log('🎫 === PREPARAR CONSULTA TICKETS ===');
console.log('💬 Chat ID:', chatId);
console.log('🔑 UID:', uid);

// -----------------------------
// 1) Normalizar intención/inputs
// -----------------------------
const rawMsg = (typeof datosUsuario.mensaje_original === 'string' ? datosUsuario.mensaje_original : '');
const msg = rawMsg.toLowerCase().replace(/\s+/g, ' ').trim();

let intencion = datosUsuario.intencion || 'resumen';
let ticketId = datosUsuario.ticket_id ?? null;
let ticketNombre = (typeof datosUsuario.ticket_nombre === 'string' ? datosUsuario.ticket_nombre : '').trim();
let ticketExact = !!datosUsuario.ticket_exact;

// 🚩 PRIORIDAD: si llega nombre_ticket en el input, forzamos estado_ticket
const nombreTicketInput = (typeof datosUsuario.nombre_ticket === 'string' ? datosUsuario.nombre_ticket : '').trim();
if (nombreTicketInput) {
  intencion = 'estado_ticket';
  ticketNombre = nombreTicketInput;
  // Si viene textual sin comillas, lo tratamos como parcial; si quieres exacto, pon ticketExact = true
  ticketExact = false;
}

// Si no vino nombre_ticket pero el mensaje dice "ticket <texto>", intentamos extraer:
if (!ticketNombre && /\bticket(?!s)\b/.test(msg)) {
  // entre comillas → exacto
  const quoted = msg.match(/\bticket(?!s)\b\s+["“]([^"”]+)["”]/i);
  if (quoted && quoted[1]) {
    intencion = 'estado_ticket';
    ticketNombre = quoted[1].trim();
    ticketExact = true;
  } else {
    // sin comillas → coge lo que sigue a "ticket "
    const after = msg.match(/\bticket(?!s)\b\s+(.+?)$/i);
    if (after && after[1]) {
      intencion = 'estado_ticket';
      ticketNombre = after[1].trim();
      ticketExact = false;
    }
  }
}

// Forzar "listar_abiertos" si nos lo dicen explícitamente
if (datosUsuario.tipo_consulta === 'abiertos' || (msg.includes('listado') && msg.includes('abiertos'))) {
  intencion = 'listar_abiertos';
  ticketId = null;
  ticketNombre = null;
  ticketExact = false;
}

// Detectar "cerrados" (listado)
const pideCerrados = /(cerrad[oa]s?)/.test(msg) || datosUsuario.tipo_consulta === 'cerrados';
const ultimos7 = /(últimos?\s*7\s*días?|7\s*días|ultima\s*semana|última\s*semana)/.test(msg);
let cerradosPeriodo = datosUsuario.cerrados_periodo || (ultimos7 ? 'ultimos_7' : 'todos');

if (pideCerrados) {
  intencion = 'listar_cerrados';
  cerradosPeriodo = ultimos7 ? 'ultimos_7' : cerradosPeriodo;
  ticketId = null;
  ticketNombre = null;
  ticketExact = false;
}

// Si la intención es listar_abiertos o listar_cerrados, no arrastrar nombres
if (intencion === 'listar_abiertos' || intencion === 'listar_cerrados') {
  ticketId = null;
  ticketNombre = null;
  ticketExact = false;
}

// Si la intención es estado_ticket, limpia nombres genéricos
if (intencion === 'estado_ticket') {
  const tooGeneric = ['abierto','abiertos','cerrado','cerrados','tickets','listado','s abiertos','los abiertos'];
  if (!ticketId && (!ticketNombre || tooGeneric.some(w => ticketNombre.toLowerCase().includes(w)))) {
    console.log('ℹ️ Nombre genérico; cambiamos a listar_abiertos');
    intencion = 'listar_abiertos';
    ticketNombre = null;
    ticketExact = false;
  }
}

console.log('🧭 INTENCION:', intencion, '| ticket_id:', ticketId, '| ticket_nombre:', ticketNombre, '| exact:', ticketExact, '| cerrados_periodo:', cerradosPeriodo);

// -----------------------------
// 2) Fechas (local) YYYY-MM-DD
// -----------------------------
function formatDateLocal(d) {
  const year = d.getFullYear();
  const month = String(d.getMonth() + 1).padStart(2, '0');
  const day = String(d.getDate()).padStart(2, '0');
  return `${year}-${month}-${day}`;
}
const hoy = new Date();
const hace7Dias = new Date();
hace7Dias.setDate(hoy.getDate() - 7);
const fechaHoy = formatDateLocal(hoy);
const fechaHace7Dias = formatDateLocal(hace7Dias);

// -----------------------------
// 3) Dominios base + filtro por equipo (opcional)
// -----------------------------
const teamId = datosUsuario.team_id || null;
const addTeamFilter = (domain) => {
  const d = Array.isArray(domain) ? [...domain] : [];
  if (teamId) d.push(['team_id', '=', teamId]);
  return d;
};

// Abiertos = etapas NO plegadas
const dominioAbiertos = addTeamFilter([['stage_id.fold', '=', false]]);

// Cerrados base = etapa plegada
const dominioCerradosBase = addTeamFilter([['stage_id.fold', '=', true]]);

// Cerrados últimos 7 días
const dominioCerradosUlt7 = addTeamFilter([
  ['stage_id.fold', '=', true],
  ['close_date', '>=', fechaHace7Dias],
  ['close_date', '<=', fechaHoy],
]);

// Abiertos creados en últimos 7 días
const dominioAbiertosUlt7 = addTeamFilter([
  ['create_date', '>=', fechaHace7Dias],
  ['create_date', '<=', fechaHoy],
  ['stage_id.fold', '=', false],
]);

// --- helper: limpia el nombre
function cleanName(s) {
  return (s || '').toString().replace(/\s+/g, ' ').trim();
}
const nameClean = cleanName(ticketNombre);

// Construye un dominio OR robusto por nombre
function buildNameDomain(name, exact) {
  if (!name) return [['id', '=', -1]];
  if (exact) return [['name', '=', name]]; // exacto
  const collapsed = name.replace(/\s+/g, '%'); // 'Wood Treatment' -> 'Wood%Treatment'
  return ['|','|',
    ['name', '=', name],
    ['name', 'ilike', name],
    ['name', 'ilike', collapsed]
  ];
}

// Dominio ticket concreto (id tiene prioridad)
let dominioTicketConcreto;
if (ticketId) {
  dominioTicketConcreto = addTeamFilter([['id', '=', Number(ticketId)]]);
} else if (nameClean) {
  const nameDomain = buildNameDomain(nameClean, ticketExact);
  // AND(team) con OR(name)
  dominioTicketConcreto = teamId ? [['team_id', '=', teamId], nameDomain] : nameDomain;
} else {
  dominioTicketConcreto = addTeamFilter([['id', '=', -1]]);
}

// -----------------------------
// 4) Parámetros Odoo
// -----------------------------
const DB = 'lsanchezgoshawk-bot-main-27977821';
const PASSWORD = 'admin'; // tu token/clave

function rpcCount(model, domain) {
  return {
    jsonrpc: '2.0',
    method: 'call',
    params: {
      service: 'object',
      method: 'execute_kw',
      args: [DB, uid, PASSWORD, model, 'search_count', [domain]],
    },
    id: Math.floor(Math.random() * 1_000_000),
  };
}
function rpcRead(model, domain, fields, extra = {}) {
  return {
    jsonrpc: '2.0',
    method: 'call',
    params: {
      service: 'object',
      method: 'execute_kw',
      args: [DB, uid, PASSWORD, model, 'search_read', [domain], { fields, ...extra }],
    },
    id: Math.floor(Math.random() * 1_000_000),
  };
}

// -----------------------------
// 5) Consultas
// -----------------------------
const consultaTicketsAbiertos = rpcCount('helpdesk.ticket', dominioAbiertos);
const consultaTicketsCerradosUltimos7Dias = rpcCount('helpdesk.ticket', dominioCerradosUlt7);
const consultaTicketsAbiertosUltimos7Dias = rpcCount('helpdesk.ticket', dominioAbiertosUlt7);

// Todos (para desglose)
const domainTodos = teamId ? addTeamFilter([]) : [];
const consultaTodosTicketsConEstados = rpcRead(
  'helpdesk.ticket',
  domainTodos,
  ['id', 'name', 'stage_id', 'create_date', 'close_date'],
  { limit: 1000 }
);

// Listado de tickets abiertos
const consultaListadoAbiertos = rpcRead(
  'helpdesk.ticket',
  dominioAbiertos,
  ['id', 'name', 'stage_id', 'partner_id', 'user_id', 'priority', 'create_date', 'close_date'],
  { limit: 50, order: 'create_date desc' }
);

// Listado de tickets cerrados
const dominioCerradosListar = (cerradosPeriodo === 'ultimos_7') ? dominioCerradosUlt7 : dominioCerradosBase;
const consultaListadoCerrados = rpcRead(
  'helpdesk.ticket',
  dominioCerradosListar,
  ['id', 'name', 'stage_id', 'partner_id', 'user_id', 'priority', 'create_date', 'close_date'],
  { limit: 50, order: 'close_date desc, create_date desc' }
);

// Estado de un ticket concreto
const consultaEstadoTicket = rpcRead(
  'helpdesk.ticket',
  dominioTicketConcreto,
  ['id','name','stage_id','partner_id','user_id','priority','create_date','close_date'],
  { limit: ticketExact ? 1 : 5, order: 'create_date desc' }
);

console.log('🧭 Final INTENCION:', intencion);
console.log('🧪 Dominio estado_ticket:', JSON.stringify(dominioTicketConcreto));
console.log('🧪 Payload consulta_estado_ticket:', JSON.stringify(consultaEstadoTicket, null, 2));
console.log('🚀 Consultas preparadas para tickets');

// -----------------------------
// 6) Devolver todo para los HTTP
// -----------------------------
return {
  chat_id: chatId,
  uid,
  intencion,
  team_id: teamId,
  ticket_id: ticketId || null,
  ticket_nombre: nameClean || null,
  ticket_exact: ticketExact,
  cerrados_periodo: cerradosPeriodo,
  fecha_hoy: fechaHoy,
  fecha_hace_7_dias: fechaHace7Dias,

  // Resumen
  consulta_abiertos: consultaTicketsAbiertos,
  consulta_cerrados_7_dias: consultaTicketsCerradosUltimos7Dias,
  consulta_abiertos_7_dias: consultaTicketsAbiertosUltimos7Dias,
  consulta_todos_estados: consultaTodosTicketsConEstados,

  // Listados
  consulta_listado_abiertos: consultaListadoAbiertos,
  consulta_listado_cerrados: consultaListadoCerrados,

  // Estado concreto
  consulta_estado_ticket: consultaEstadoTicket,
};

// CONSOLIDAR RESULTADOS DE CONSULTAS DE TICKETS
const prep = $('Preparar Consulta Tickets').item.json;

const resAbiertosCount = $('HTTP Tickets Abiertos').item.json.result || 0;
const resCerrados7Count = $('HTTP Tickets Cerrados 7 Días').item.json.result || 0;
const resAbiertos7Count = $('HTTP Tickets Abiertos 7 Días').item.json.result || 0;
const resTodos = $('HTTP Tickets Todos Estados').item.json.result || [];

const resListadoAbiertos = $('HTTP Listado Abiertos').item.json.result || [];
let resListadoCerrados = [];
try { resListadoCerrados = $('HTTP Listado Cerrados').item.json.result || []; } catch(e) { /* si aún no lo conectaste */ }

let resEstadoTicket = [];
try { resEstadoTicket = $('HTTP Estado Ticket').item.json.result || []; } catch(e) { /* idem */ }

function fmtM2O(val) { return Array.isArray(val) ? val[1] : (val || '—'); }

const intent = prep.intencion || 'resumen';

// ====== INTENCION: LISTAR ABIERTOS ======
if (intent === 'listar_abiertos') {
  if (!resListadoAbiertos.length) {
    return { chat_id: prep.chat_id, texto: '📂 No hay tickets abiertos en este momento.' };
  }
  const max = 25;
  const lines = resListadoAbiertos.slice(0, max).map(t =>
    `• #${t.id} — ${t.name}  |  Estado: ${fmtM2O(t.stage_id)}`
  );
  const extra = resListadoAbiertos.length > max ? `\n… y ${resListadoAbiertos.length - max} más.` : '';
  return {
    chat_id: prep.chat_id,
    texto: `🟢 **Tickets abiertos (${resListadoAbiertos.length})**\n\n${lines.join('\n')}${extra}`
  };
}

// ====== INTENCION: LISTAR CERRADOS ======
if (intent === 'listar_cerrados') {
  const periodo = prep.cerrados_periodo === 'ultimos_7' ? ` (últimos 7 días)` : '';
  if (!resListadoCerrados.length) {
    return { chat_id: prep.chat_id, texto: `📁 No hay tickets cerrados${periodo}.` };
  }
  const max = 25;
  const lines = resListadoCerrados.slice(0, max).map(t =>
    `• #${t.id} — ${t.name}  |  Estado: ${fmtM2O(t.stage_id)}  |  Cerrado: ${t.close_date || '—'}`
  );
  const extra = resListadoCerrados.length > max ? `\n… y ${resListadoCerrados.length - max} más.` : '';
  return {
    chat_id: prep.chat_id,
    texto: `🔒 **Tickets cerrados${periodo} (${resListadoCerrados.length})**\n\n${lines.join('\n')}${extra}`
  };
}

// ====== INTENCION: ESTADO TICKET ======
if (intent === 'estado_ticket') {
  const id = prep.ticket_id;
  const name = prep.ticket_nombre;
  if (!id && !name) {
    return {
      chat_id: prep.chat_id,
      texto: 'ℹ️ Para ver el estado de un ticket, dime el **número** (p. ej., `ticket 24`) o el **nombre exacto entre comillas** (p. ej., `ticket "Install High-speed Internet"`).'
    };
  }
  if (!resEstadoTicket.length) {
    const ref = id ? `#${id}` : `"${name}"`;
    return { chat_id: prep.chat_id, texto: `❓ No encontré el ticket ${ref}.` };
  }
  if (resEstadoTicket.length > 1 && !prep.ticket_exact) {
    const max = 5;
    const lines = resEstadoTicket.slice(0, max).map(t =>
      `• #${t.id} — ${t.name}  |  Estado: ${fmtM2O(t.stage_id)}`
    );
    const extra = resEstadoTicket.length > max ? `\n… y ${resEstadoTicket.length - max} más.` : '';
    return {
      chat_id: prep.chat_id,
      texto: `🔎 Encontré varias coincidencias:\n\n${lines.join('\n')}${extra}\n\nEscribe el **número** de ticket o el **nombre exacto entre comillas**.`
    };
  }
  const t = resEstadoTicket[0];
  const detalle =
    `🧾 **Ticket #${t.id} — ${t.name}**\n` +
    `• Estado: ${fmtM2O(t.stage_id)}\n` +
    `• Cliente: ${fmtM2O(t.partner_id)}\n` +
    `• Asignado a: ${fmtM2O(t.user_id)}\n` +
    `• Prioridad: ${t.priority || '—'}\n` +
    `• Creado: ${t.create_date || '—'}\n` +
    `• Cerrado: ${t.close_date || '—'}`;
  return { chat_id: prep.chat_id, texto: detalle };
}

// ====== RESUMEN (por defecto) ======
const estados = {};
resTodos.forEach(t => {
  const s = fmtM2O(t.stage_id);
  estados[s] = (estados[s] || 0) + 1;
});
const total = resTodos.length;

let texto = `🎫 **ESTADO DEL SERVICIO DE ASISTENCIA**\n\n` +
  `📊 **RESUMEN GENERAL:**\n` +
  `• **Tickets abiertos:** ${resAbiertosCount}\n` +
  `• **Tickets cerrados (últimos 7 días):** ${resCerrados7Count}\n` +
  `• **Tickets abiertos (últimos 7 días):** ${resAbiertos7Count}\n` +
  `• **Total de tickets en sistema:** ${total}\n\n`;

if (Object.keys(estados).length) {
  texto += `🏷️ **DESGLOSE POR ESTADOS:**\n`;
  Object.entries(estados)
    .sort((a,b) => b[1]-a[1])
    .forEach(([estado, n]) => {
      const p = total ? ((n/total)*100).toFixed(1) : '0.0';
      texto += `  • **${estado}:** ${n} (${p}%)\n`;
    });
}
texto += `\n📅 Período: ${prep.fecha_hace_7_dias} → ${prep.fecha_hoy}`;

return { chat_id: prep.chat_id, texto };

// BUSCAR TICKET ESPECÍFICO PARA AÑADIR MENSAJE
const datosUsuario = $json;
const chatId = datosUsuario.chat_id;
const uid = datosUsuario.uid;
const ticketId = datosUsuario.ticket_id || datosUsuario.ticket || datosUsuario.ticket_nombre || datosUsuario.ticket_id_o_nombre || datosUsuario.ticket_identificador;
const mensaje = datosUsuario.mensaje;

console.log('🎫 === BUSCAR TICKET PARA MENSAJE ===');
console.log('💬 Chat ID:', chatId);
console.log('🔑 UID:', uid);
console.log('🎫 Ticket ID/Nombre:', ticketId);
console.log('💬 Mensaje a añadir:', mensaje);

if (!ticketId) {
  console.error('❌ No se proporcionó ID o nombre del ticket');
  return {
    error: true,
    mensaje_error: 'No se especificó el ticket al que añadir el mensaje',
    chat_id: chatId
  };
}

if (!mensaje) {
  console.error('❌ No se proporcionó mensaje para añadir');
  return {
    error: true,
    mensaje_error: 'No se especificó el mensaje a añadir al ticket',
    chat_id: chatId
  };
}

// Preparar consulta para buscar el ticket
// Buscar por ID numérico o por nombre
let filtrosBusqueda;
if (/^\d+$/.test(ticketId)) {
  // Es un ID numérico
  filtrosBusqueda = [["id", "=", parseInt(ticketId)]];
} else {
  // Es un nombre, buscar por nombre o referencia
  filtrosBusqueda = [
    "|",
    ["name", "ilike", ticketId],
    ["ticket_ref", "ilike", ticketId]
  ];
}

const consultaBuscarTicket = {
  jsonrpc: "2.0",
  method: "call",
  params: {
    service: "object",
    method: "execute_kw",
    args: [
      "lsanchezgoshawk-bot-main-27977821",
      uid,
      "admin",
      "helpdesk.ticket",
      "search_read",
      [filtrosBusqueda],
      {
        fields: ["id", "name", "ticket_ref", "stage_id", "partner_id", "create_date"],
        limit: 5
      }
    ]
  },
  id: Math.floor(Math.random() * 1000000)
};

console.log('🔍 Consulta preparada para buscar ticket:', JSON.stringify(consultaBuscarTicket, null, 2));

return {
  chat_id: chatId,
  uid: uid,
  ticket_buscado: ticketId,
  mensaje_anadir: mensaje,
  consulta_buscar_ticket: consultaBuscarTicket
};

// VALIDAR TICKET ENCONTRADO Y PREPARAR MENSAJE
const datosBusqueda = $('Buscar Ticket para Mensaje').item.json;
const resultadoBusqueda = $json.result || [];

console.log('🎫 === VALIDAR TICKET ENCONTRADO ===');
console.log('🔍 Tickets encontrados:', resultadoBusqueda.length);
console.log('📋 Resultados:', JSON.stringify(resultadoBusqueda, null, 2));

// OBTENER DATOS DEL USUARIO DE ODOO DESDE CONSULTAR USUARIO TELEGRAM
let odooUser = null;
try {
  const datosUsuarioTelegram = $('Consultar Usuario Telegram').item.json;
  odooUser = datosUsuarioTelegram.odoo_user;
  console.log('👤 Usuario Odoo obtenido:', odooUser);
} catch (error) {
  console.log('⚠️ No se pudo obtener usuario Odoo:', error.message);
}

if (resultadoBusqueda.length === 0) {
  console.log('❌ Ticket no encontrado');
  return {
    error: true,
    ticket_encontrado: false,
    mensaje_error: `No se encontró ningún ticket con el identificador "${datosBusqueda.ticket_buscado}". Verifica que el ID o nombre del ticket sea correcto.`,
    chat_id: datosBusqueda.chat_id
  };
}

if (resultadoBusqueda.length > 1) {
  console.log('⚠️ Múltiples tickets encontrados');
  let listaTickets = resultadoBusqueda.map((ticket, index) => 
    `${index + 1}. **${ticket.name}** (ID: ${ticket.id})${ticket.ticket_ref ? ` - Ref: ${ticket.ticket_ref}` : ''}`
  ).join('\n');

  return {
    error: true,
    tickets_multiples: true,
    mensaje_error: `Se encontraron múltiples tickets. Por favor, especifica cuál:\n\n${listaTickets}\n\nUsa el ID exacto del ticket que deseas.`,
    chat_id: datosBusqueda.chat_id,
    tickets_encontrados: resultadoBusqueda
  };
}

// Ticket único encontrado
const ticket = resultadoBusqueda[0];
console.log('✅ Ticket único encontrado:', ticket);

// CONSTRUIR MENSAJE CON FORMATO: "mensaje -- usuario_odoo"
let mensajeCompleto = datosBusqueda.mensaje_anadir;
if (odooUser && odooUser.trim()) {
  mensajeCompleto = `${datosBusqueda.mensaje_anadir} -- ${odooUser}`;
  console.log('✅ Mensaje formateado con usuario Odoo:', mensajeCompleto);
} else {
  console.log('⚠️ Sin usuario Odoo, usando mensaje original');
}

// Preparar mensaje para el chatter
const mensajeChatter = {
  jsonrpc: "2.0",
  method: "call",
  params: {
    service: "object",
    method: "execute_kw",
    args: [
      "lsanchezgoshawk-bot-main-27977821",
      datosBusqueda.uid,
      "admin",
      "mail.message",
      "create",
      [{
        res_id: ticket.id,
        model: "helpdesk.ticket",
        message_type: "comment",
        body: `<p>${mensajeCompleto}</p>`,
        subtype_id: 1  // mt_comment
      }]
    ]
  },
  id: Math.floor(Math.random() * 1000000)
};

console.log('💬 Mensaje preparado para chatter:', JSON.stringify(mensajeChatter, null, 2));

return {
  ticket_encontrado: true,
  ticket: ticket,
  chat_id: datosBusqueda.chat_id,
  uid: datosBusqueda.uid,
  mensaje_original: datosBusqueda.mensaje_anadir,
  mensaje_completo: mensajeCompleto,
  odoo_user: odooUser,
  consulta_mensaje_chatter: mensajeChatter
};

