parameters.jsCode:
// CONSULTAR FABRICACIONES CON FILTROS POR PERÍODO
const datosUsuario = $json;
const chatId = datosUsuario.chat_id;
const uid = datosUsuario.uid;
const mensajeUsuario = datosUsuario.mensaje_original || datosUsuario.mensaje_usuario || '';

console.log('🏭 === CONSULTAR FABRICACIONES ===');
console.log('💬 Chat ID:', chatId);
console.log('🔑 UID:', uid);
console.log('📝 Mensaje usuario:', mensajeUsuario);

// DETECTAR FILTROS DE PERÍODO EN EL MENSAJE
let filtroFecha = [];
let descripcionPeriodo = 'todas las fabricaciones';
const ahora = new Date();
const hoy = new Date(ahora.getFullYear(), ahora.getMonth(), ahora.getDate());

// Detectar períodos específicos
if (mensajeUsuario.includes('hoy') || mensajeUsuario.includes('día')) {
  const fechaHoy = hoy.toISOString().split('T')[0];
  filtroFecha = [['create_date', '>=', fechaHoy + ' 00:00:00'], ['create_date', '<=', fechaHoy + ' 23:59:59']];
  descripcionPeriodo = 'de hoy';
} else if (mensajeUsuario.includes('ayer')) {
  const ayer = new Date(hoy);
  ayer.setDate(hoy.getDate() - 1);
  const fechaAyer = ayer.toISOString().split('T')[0];
  filtroFecha = [['create_date', '>=', fechaAyer + ' 00:00:00'], ['create_date', '<=', fechaAyer + ' 23:59:59']];
  descripcionPeriodo = 'de ayer';
} else if (mensajeUsuario.includes('esta semana') || mensajeUsuario.includes('semana actual')) {
  const inicioSemana = new Date(hoy);
  inicioSemana.setDate(hoy.getDate() - hoy.getDay());
  const finSemana = new Date(inicioSemana);
  finSemana.setDate(inicioSemana.getDate() + 6);
  filtroFecha = [['create_date', '>=', inicioSemana.toISOString().split('T')[0] + ' 00:00:00'], ['create_date', '<=', finSemana.toISOString().split('T')[0] + ' 23:59:59']];
  descripcionPeriodo = 'de esta semana';
} else if (mensajeUsuario.includes('este mes') || mensajeUsuario.includes('mes actual')) {
  const inicioMes = new Date(ahora.getFullYear(), ahora.getMonth(), 1);
  const finMes = new Date(ahora.getFullYear(), ahora.getMonth() + 1, 0);
  filtroFecha = [['create_date', '>=', inicioMes.toISOString().split('T')[0] + ' 00:00:00'], ['create_date', '<=', finMes.toISOString().split('T')[0] + ' 23:59:59']];
  descripcionPeriodo = 'de este mes';
} else if (mensajeUsuario.includes('mes pasado') || mensajeUsuario.includes('último mes')) {
  const inicioMesPasado = new Date(ahora.getFullYear(), ahora.getMonth() - 1, 1);
  const finMesPasado = new Date(ahora.getFullYear(), ahora.getMonth(), 0);
  filtroFecha = [['create_date', '>=', inicioMesPasado.toISOString().split('T')[0] + ' 00:00:00'], ['create_date', '<=', finMesPasado.toISOString().split('T')[0] + ' 23:59:59']];
  descripcionPeriodo = 'del mes pasado';
}

// DETECTAR FILTROS DE ESTADO
let filtroEstado = [];
let descripcionEstado = '';
if (mensajeUsuario.includes('pendiente') || mensajeUsuario.includes('por hacer')) {
  filtroEstado = [['state', 'in', ['draft', 'confirmed', 'progress']]];
  descripcionEstado = ' pendientes';
} else if (mensajeUsuario.includes('en progreso') || mensajeUsuario.includes('en curso')) {
  filtroEstado = [['state', '=', 'progress']];
  descripcionEstado = ' en progreso';
} else if (mensajeUsuario.includes('terminada') || mensajeUsuario.includes('completada') || mensajeUsuario.includes('hecha')) {
  filtroEstado = [['state', '=', 'done']];
  descripcionEstado = ' terminadas';
} else if (mensajeUsuario.includes('cancelada') || mensajeUsuario.includes('cancelado')) {
  filtroEstado = [['state', '=', 'cancel']];
  descripcionEstado = ' canceladas';
}

// COMBINAR FILTROS
let filtrosCombinados = [];
if (filtroFecha.length > 0 && filtroEstado.length > 0) {
  filtrosCombinados = [...filtroFecha, ...filtroEstado];
} else if (filtroFecha.length > 0) {
  filtrosCombinados = filtroFecha;
} else if (filtroEstado.length > 0) {
  filtrosCombinados = filtroEstado;
} else {
  // Sin filtros específicos, mostrar las últimas 10
  filtrosCombinados = [];
}

console.log('🔍 Filtros aplicados:', JSON.stringify(filtrosCombinados, null, 2));
console.log('📅 Descripción período:', descripcionPeriodo);
console.log('📊 Descripción estado:', descripcionEstado);

// CONSULTA A ODOO PARA ÓRDENES DE FABRICACIÓN
const consultaFabricaciones = {
  jsonrpc: "2.0",
  method: "call",
  params: {
    service: "object",
    method: "execute_kw",
    args: [
      "lsanchezgoshawk-bot-main-27977821",
      uid,
      "admin",
      "mrp.production",
      "search_read",
      [filtrosCombinados],
      {
        fields: ["id", "name", "product_id", "product_qty", "product_uom_id", "state", "date_planned_start", "date_planned_finished", "date_finished", "user_id", "company_id", "priority"],
        limit: 20,
        order: "date_planned_start desc"
      }
    ]
  },
  id: Math.floor(Math.random() * 1000000)
};

console.log('🔍 Consulta preparada para fabricaciones:', JSON.stringify(consultaFabricaciones, null, 2));

return {
  chat_id: chatId,
  uid: uid,
  descripcion_periodo: descripcionPeriodo,
  descripcion_estado: descripcionEstado,
  mensaje_usuario: mensajeUsuario,
  consulta_fabricaciones: consultaFabricaciones
};

// PROCESAR RESULTADOS DE FABRICACIONES
const datosBusqueda = $('Consultar Fabricaciones').item.json;
const resultadoFabricaciones = $json.result || [];

console.log('🏭 === PROCESAR FABRICACIONES ===');
console.log('📊 Fabricaciones encontradas:', resultadoFabricaciones.length);
console.log('📋 Resultados:', JSON.stringify(resultadoFabricaciones, null, 2));

if (resultadoFabricaciones.length === 0) {
  console.log('❌ No se encontraron fabricaciones');
  return {
    chat_id: datosBusqueda.chat_id,
    mensaje_telegram: `❌ No se encontraron órdenes de fabricación${datosBusqueda.descripcion_estado} ${datosBusqueda.descripcion_periodo}.\n\n💡 **Sugerencias:**\n• Prueba con un período más amplio\n• Verifica si hay fabricaciones en el sistema\n• Usa términos como "fabricaciones de este mes" o "producción en progreso"`
  };
}

// MAPEAR ESTADOS A EMOJIS Y TEXTO
const estadosMap = {
  'draft': '📝 Borrador',
  'confirmed': '✅ Confirmada',
  'progress': '🔄 En Progreso',
  'to_close': '⏳ Por Cerrar',
  'done': '✅ Terminada',
  'cancel': '❌ Cancelada'
};

// FORMATEAR RESULTADOS
let mensaje = `🏭 **Órdenes de Fabricación${datosBusqueda.descripcion_estado} ${datosBusqueda.descripcion_periodo}**\n\n`;
mensaje += `📊 **Total encontradas:** ${resultadoFabricaciones.length}\n\n`;

// ESTADÍSTICAS POR ESTADO
const estadisticas = {};
resultadoFabricaciones.forEach(fab => {
  const estado = fab.state || 'unknown';
  estadisticas[estado] = (estadisticas[estado] || 0) + 1;
});

mensaje += `📈 **Resumen por estado:**\n`;
Object.entries(estadisticas).forEach(([estado, cantidad]) => {
  const estadoTexto = estadosMap[estado] || `❓ ${estado}`;
  mensaje += `${estadoTexto}: ${cantidad}\n`;
});
mensaje += `\n`;

// LISTADO DETALLADO (máximo 10 para no saturar)
const fabricacionesMostrar = resultadoFabricaciones.slice(0, 10);
mensaje += `📋 **Detalle de fabricaciones:**\n`;

fabricacionesMostrar.forEach((fab, index) => {
  const estadoEmoji = estadosMap[fab.state] || `❓ ${fab.state}`;
  const producto = fab.product_id ? fab.product_id[1] : 'Producto no especificado';
  const cantidad = fab.product_qty || 0;
  const unidad = fab.product_uom_id ? fab.product_uom_id[1] : 'uds';
  const fechaInicio = fab.date_planned_start ? new Date(fab.date_planned_start).toLocaleDateString('es-ES') : 'No programada';
  const fechaFin = fab.date_planned_finished ? new Date(fab.date_planned_finished).toLocaleDateString('es-ES') : 'No programada';

  mensaje += `\n${index + 1}. **${fab.name || 'Sin nombre'}**\n`;
  mensaje += `   📦 Producto: ${producto}\n`;
  mensaje += `   📊 Cantidad: ${cantidad} ${unidad}\n`;
  mensaje += `   ${estadoEmoji}\n`;
  mensaje += `   📅 Inicio: ${fechaInicio}\n`;
  mensaje += `   🏁 Fin: ${fechaFin}\n`;
});

if (resultadoFabricaciones.length > 10) {
  mensaje += `\n... y ${resultadoFabricaciones.length - 10} más.\n`;
}

mensaje += `\n💡 **Filtros disponibles:**\n• "fabricaciones pendientes"\n• "fabricaciones en progreso"\n• "fabricaciones terminadas"\n• "fabricaciones de hoy/ayer/esta semana/este mes"`;

console.log('✅ Mensaje formateado para Telegram:', mensaje);

return {
  chat_id: datosBusqueda.chat_id,
  mensaje_telegram: mensaje,
  total_fabricaciones: resultadoFabricaciones.length,
  fabricaciones: resultadoFabricaciones
};

// CREAR PEDIDO DE COMPRA - SIMILAR A CREAR PEDIDO DE VENTA
const datosUsuario = $json;
const chatId = datosUsuario.chat_id;
const uid = datosUsuario.uid;

console.log('🛒 === CREAR PEDIDO DE COMPRA ===');
console.log('💬 Chat ID:', chatId);
console.log('🔑 UID:', uid);
console.log('📋 Datos recibidos:', JSON.stringify(datosUsuario, null, 2));

// EXTRAER INFORMACIÓN DEL PEDIDO
const proveedor = datosUsuario.proveedor || 'Proveedor no especificado';
const productos = datosUsuario.productos || [];

console.log('🏢 Proveedor:', proveedor);
console.log('📦 Productos:', productos);

// VALIDAR DATOS MÍNIMOS
if (!proveedor || proveedor === 'Proveedor no especificado') {
  return {
    error: true,
    mensaje_error: 'No se especificó el proveedor para el pedido de compra. Por favor, indica el nombre del proveedor.',
    chat_id: chatId
  };
}

if (!productos || productos.length === 0) {
  return {
    error: true,
    mensaje_error: 'No se especificaron productos para el pedido de compra. Por favor, indica qué productos y cantidades quieres comprar.',
    chat_id: chatId
  };
}

// BUSCAR PROVEEDOR
const consultaBuscarProveedor = {
  jsonrpc: "2.0",
  method: "call",
  params: {
    service: "object",
    method: "execute_kw",
    args: [
      "lsanchezgoshawk-bot-main-27977821",
      uid,
      "admin",
      "res.partner",
      "search_read",
      [["name", "ilike", proveedor], ["supplier_rank", ">", 0]],
      {
        fields: ["id", "name", "email", "phone"],
        limit: 5
      }
    ]
  },
  id: Math.floor(Math.random() * 1000000)
};

return {
  chat_id: chatId,
  uid: uid,
  proveedor_buscado: proveedor,
  productos_pedido: productos,
  consulta_buscar_proveedor: consultaBuscarProveedor
};

// Validar proveedor y preparar búsqueda de productos para compra (purchase_ok)
const respProveedor = $json.result || [];
const datosPrep = $('Preparar Pedido Compra').item.json;

const chatId = datosPrep.chat_id;
const uid = datosPrep.uid;
const productosSolicitados = datosPrep.productos_pedido || [];
const proveedorNombre = datosPrep.proveedor_buscado;

// Si faltan productos, pedimos al usuario
if (!Array.isArray(productosSolicitados) || productosSolicitados.length === 0) {
  return {
    error: true,
    mensaje_error: 'No se especificaron productos para el pedido de compra. Indica productos y cantidades.',
    chat_id: chatId
  };
}

// Validar proveedor
if (!Array.isArray(respProveedor) || respProveedor.length === 0) {
  return {
    error: true,
    mensaje_error: `No encontré proveedor que coincida con "${proveedorNombre}". Dime el nombre exacto del proveedor.`,
    chat_id: chatId
  };
}

// Si hay múltiples, tomamos el mejor por nombre exacto si existe, si no el primero
let proveedorElegido = respProveedor[0];
for (const p of respProveedor) {
  if ((p.name || '').toLowerCase() === (proveedorNombre || '').toLowerCase()) {
    proveedorElegido = p;
    break;
  }
}

// Construir dominio para productos (purchase_ok)
const condicionesProductos = [];
for (const prod of productosSolicitados) {
  if (prod && prod.nombre) condicionesProductos.push(['name','ilike', prod.nombre]);
}

let dominio;
if (condicionesProductos.length === 1) {
  dominio = [condicionesProductos[0], ['purchase_ok', '=', true]];
} else if (condicionesProductos.length === 2) {
  dominio = ['|', condicionesProductos[0], condicionesProductos[1], ['purchase_ok', '=', true]];
} else {
  let dominioOr = ['|', condicionesProductos[0], condicionesProductos[1]];
  for (let i = 2; i < condicionesProductos.length; i++) {
    dominioOr = ['|', dominioOr, condicionesProductos[i]];
  }
  dominio = [dominioOr, ['purchase_ok', '=', true]];
}

const payloadProductos = {
  jsonrpc: '2.0',
  method: 'call',
  params: {
    service: 'object',
    method: 'execute_kw',
    args: [
      'lsanchezgoshawk-bot-main-27977821',
      uid,
      'admin',
      'product.product',
      'search_read',
      [dominio],
      { fields: ['id','name','uom_id','standard_price'], limit: 50 }
    ]
  },
  id: Math.floor(Math.random() * 1e6)
};

return {
  chat_id: chatId,
  uid,
  proveedor: proveedorElegido,
  productos_solicitados: productosSolicitados,
  payload_productos_compra: payloadProductos
};

// Crear pedido de compra en borrador
const productosEncontrados = $json.result || [];
const datos = $('Procesar Proveedor y Preparar Productos Compra').item.json;
const uid = datos.uid;
const chatId = datos.chat_id;
const proveedor = datos.proveedor;
const solicitados = datos.productos_solicitados || [];

// Mapear líneas
const lineas = [];
const noEncontrados = [];
for (const req of solicitados) {
  const p = productosEncontrados.find(x => (x.name||'').toLowerCase().includes((req.nombre||'').toLowerCase()) || (req.nombre||'').toLowerCase().includes((x.name||'').toLowerCase()));
  if (p) {
    lineas.push({
      product_id: p.id,
      product_qty: req.cantidad || 1,
      price_unit: typeof p.standard_price === 'number' ? p.standard_price : 0,
      name: p.name
    });
  } else {
    noEncontrados.push(req.nombre);
  }
}

if (lineas.length === 0) {
  return {
    error: true,
    mensaje_error: 'No encontré productos válidos para compra en Odoo. Revisa los nombres.',
    chat_id: chatId
  };
}

const datosPedido = {
  partner_id: proveedor.id,
  state: 'draft',
  order_line: lineas.map(l => [0,0,l])
};

const payloadCreate = {
  jsonrpc: '2.0',
  method: 'call',
  params: {
    service: 'object',
    method: 'execute_kw',
    args: [
      'lsanchezgoshawk-bot-main-27977821',
      uid,
      'admin',
      'purchase.order',
      'create',
      [datosPedido]
    ]
  },
  id: Math.floor(Math.random() * 1e6)
};

return {
  chat_id: chatId,
  uid,
  proveedor,
  lineas_pedido: lineas,
  productos_no_encontrados: noEncontrados,
  payload_crear_pedido_compra: payloadCreate
};

// Formatear respuesta creación compra
const pedidoId = $json.result;
const datos = $('Crear Pedido Compra').item.json;
const proveedor = datos.proveedor;
const lineas = datos.lineas_pedido || [];
const noEncontrados = datos.productos_no_encontrados || [];

let texto = '';
if (!pedidoId) {
  texto = '❌ Error al crear el pedido de compra. Inténtalo de nuevo.';
} else {
  const header = '✅ Pedido de compra creado (ID: ' + pedidoId + ')\n\n';
  const prov = '🏢 Proveedor: ' + (proveedor && proveedor.name ? proveedor.name : '-') + '\n';
  const productos = '📦 Productos: \n' + lineas.map(function(l){
    const qty = (typeof l.product_qty === 'number' ? l.product_qty : 1);
    const price = (typeof l.price_unit === 'number' ? l.price_unit : Number(l.price_unit) || 0);
    return '• ' + (l.name || '-') + ' — Cant: ' + qty + ' — Precio: €' + price.toFixed(2);
  }).join('\n');
  texto = header + prov + productos;
  if (noEncontrados.length > 0) {
    texto += '\n\n⚠️ No encontrados: ' + noEncontrados.join(', ');
  }
  texto += '\n\n📝 El pedido está en borrador en Odoo.';
}

return { chat_id: datos.chat_id, texto: texto, pedido_compra_id: pedidoId };

const esFallback = $json.es_fallback === true;
const userId = Number($json.user_id || 0);

// Si es fallback, no descontar
return {
  ...$json,
  descuento_necesario: !esFallback,
  user_id_descuento: userId
};

// Preparar payload JSON-RPC para crear calendar.event en Odoo (con TZ Europe/Madrid → UTC)
const resp = $json; // respuesta de HTTP Buscar Participantes: { jsonrpc, id, result: [...] }
const encontrados = Array.isArray(resp.result) ? resp.result : [];

// Datos previos desde el nodo "Buscar Participantes Evento"
const prev = $('Buscar Participantes Evento').item.json;

// IDs de partners
const partnerIds = encontrados.map((p) => p.id);

// Calcular no encontrados (opcional, para mensaje)
const solicitados = Array.isArray(prev.participantes_solicitados) ? prev.participantes_solicitados : [];
const solicitadosNorm = solicitados.map((s) => s.toString().trim().toLowerCase());
const encontradosNorm = encontrados.map((p) => (p.name || '').toString().trim().toLowerCase());
const noEncontrados = solicitadosNorm.filter((s) => !encontradosNorm.includes(s));

// Conversión Europe/Madrid → UTC respetando DST
function tzOffsetFor(dateUtcMs, timeZone) {
  const f = new Intl.DateTimeFormat('en-US', {
    timeZone, hour12: false,
    year: 'numeric', month: '2-digit', day: '2-digit',
    hour: '2-digit', minute: '2-digit', second: '2-digit',
  });
  const parts = Object.fromEntries(f.formatToParts(new Date(dateUtcMs)).map(p => [p.type, p.value]));
  const asUTCms = Date.UTC(parts.year, Number(parts.month) - 1, parts.day, parts.hour, parts.minute, parts.second);
  return asUTCms - dateUtcMs;
}

function localTzToUtcString(fechaYmd, horaHm, timeZone) {
  const [Y, M, D] = fechaYmd.split('-').map(Number);
  const [h, m] = horaHm.split(':').map(Number);
  const localAsUTCms = Date.UTC(Y, M - 1, D, h, m, 0);
  const offset = tzOffsetFor(localAsUTCms, timeZone);
  const utcMs = localAsUTCms - offset;
  return new Date(utcMs).toISOString().slice(0, 19).replace('T', ' ');
}

const TZ = 'Europe/Madrid';
const fecha = prev.fecha;              // "YYYY-MM-DD"
const horaInicio = prev.hora_inicio;   // "HH:MM"
const horaFin = prev.hora_fin;         // "HH:MM"

// Si faltara hora_fin, usa misma hora de inicio (evento 0 min)
const start = localTzToUtcString(fecha, horaInicio, TZ);
const stop  = localTzToUtcString(fecha, horaFin || horaInicio, TZ);

// Valores del evento
const vals = {
  name: prev.titulo || 'Evento',
  start,
  stop,
  description: prev.descripcion || '',
  partner_ids: [[6, 0, partnerIds]],
};

const jsonRpcPayload = {
  jsonrpc: '2.0',
  method: 'call',
  params: {
    service: 'object',
    method: 'execute_kw',
    args: [
      'lsanchezgoshawk-bot-main-27977821',
      prev.uid,
      'admin',
      'calendar.event',
      'create',
      [vals],
    ],
  },
  id: Math.floor(Math.random() * 1000000),
};

console.log('🧩 Crear evento - vals:', JSON.stringify(vals, null, 2));
console.log('🚀 Payload crear evento:', JSON.stringify(jsonRpcPayload, null, 2));

return {
  payload: jsonRpcPayload,
  chat_id: prev.chat_id,
  participantes_encontrados: encontrados.map((p) => p.name),
  participantes_no_encontrados: noEncontrados,
  titulo: prev.titulo,
  fecha: prev.fecha,
  hora_inicio: prev.hora_inicio,
  hora_fin: prev.hora_fin,
  descripcion: prev.descripcion,
};

// CONTACTO ENCONTRADO PERO FALTA NUEVO VALOR - Guardar en memoria
const datos = $('Preparar Modificar Contacto').first().json;
const contactoData = $('Buscar Contacto').first().json.result[0];
const chatId = datos.chat_id;
const contextoKey = `memoria_${chatId}`;
let memoria = global[contextoKey] || {};

console.log('✅ CONTACTO ENCONTRADO, ❌ FALTA NUEVO VALOR');
console.log('📋 Contacto:', contactoData.name, 'ID:', contactoData.id);
console.log('🔧 Campo a modificar:', datos.campo);

// Guardar datos para modificar contacto
memoria.esperando_confirmacion = 'nuevo_valor_contacto';
memoria.esperando_respuesta = true;
memoria.datos_pendientes = {
  contacto: datos.contacto,
  contacto_id: contactoData.id,
  contacto_data: contactoData,
  campo: datos.campo,
  accion_original: 'modificar_contacto',
  tipo_modificacion: 'actualizar_campo',
  uid: datos.uid
};

// Actualizar historial
const entradaHistorial = {
  timestamp: new Date().toISOString(),
  usuario: `Modificar ${datos.campo} del contacto ${datos.contacto}`,
  bot: `Contacto encontrado. Esperando nuevo valor para ${datos.campo}`,
  accion: 'esperando_nuevo_valor_contacto',
  chat_id: chatId
};

if (!memoria.historial) memoria.historial = [];
memoria.historial.push(entradaHistorial);

global[contextoKey] = memoria;

return {
  chat_id: chatId,
  contacto: datos.contacto,
  contacto_id: contactoData.id,
  campo: datos.campo,
  contacto_nombre_completo: contactoData.name,
  tipo_respuesta: 'esperando_nuevo_valor',
  memoria_actualizada: memoria
};

