// Pide el nuevo valor al usuario
const datos = $node["Preparar Modificar Contacto"].json;
return {
  chat_id: datos.chat_id,
  texto: `¿Qué *nuevo valor* quieres poner en el campo *${datos.campo}* del contacto *${datos.contacto}*?`
};

// ✅ Validador de parámetros para CONSULTAR AUSENCIAS (robusto)
// - Permite usuario = null para listados globales
// - Soporta: hoy, ayer, esta_semana, semana_pasada, este_mes, mes_pasado, general, rango_fechas, fecha_especifica

const datosAI = $json || {};
const chatId  = datosAI.chat_id;

// Normalización de usuario (acepta string, número, objeto con .name)
const usuarioNorm = (() => {
  const u = datosAI.usuario;
  if (typeof u === 'string') return u.trim();
  if (typeof u === 'number') return String(u).trim();
  if (u && typeof u === 'object' && u.name) return String(u.name).trim();
  return '';
})();

let periodo = (datosAI.periodo && datosAI.periodo !== 'no_especificado') ? String(datosAI.periodo).trim() : '';

// Soporte de fechas
const fechaEspecifica = datosAI.fecha_especifica ? String(datosAI.fecha_especifica).trim() : '';
const fechaInicio     = datosAI.fecha_inicio     ? String(datosAI.fecha_inicio).trim()     : '';
const fechaFin        = datosAI.fecha_fin        ? String(datosAI.fecha_fin).trim()        : '';

const mensajeIA =
  datosAI.mensaje ||
  datosAI.response ||
  datosAI.text ||
  datosAI.respuesta_usuario ||
  datosAI.mensaje_original ||
  '';

const log = (...a) => console.log('[AUSENCIAS]', ...a);
log('🔍 === VERIFICACIÓN DATOS AUSENCIAS ===');
log('👤 Usuario específico:', usuarioNorm || '(TODOS)');
log('📅 Periodo recibido:', periodo || '(no especificado)');
log('📅 Fecha específica:', fechaEspecifica || '(no especificada)');
log('📅 Rango:', (fechaInicio && fechaFin) ? `${fechaInicio} → ${fechaFin}` : '(sin rango)');
log('💬 Mensaje IA:', mensajeIA || '(sin mensaje)');

// Helpers
const isYYYYMMDD = (s) => /^\d{4}-\d{2}-\d{2}$/.test(String(s || ''));
const cmpDates = (a, b) => new Date(a).getTime() - new Date(b).getTime();

const PERIODOS_VALIDOS = new Set([
  'hoy', 'ayer',
  'esta_semana', 'semana_pasada',
  'este_mes', 'mes_pasado',
  'general',
  'rango_fechas',
  'fecha_especifica'
]);

// Autocompletar periodo si no viene
if (!periodo) {
  if (fechaEspecifica) periodo = 'fecha_especifica';
  else if (fechaInicio && fechaFin) periodo = 'rango_fechas';
  else periodo = 'general';
}

// Validaciones
let datosCompletos = false;
let razonIncompleta = null;

if (!PERIODOS_VALIDOS.has(periodo)) {
  razonIncompleta = 'periodo_no_valido';
} else {
  let fechasOK = true;

  if (periodo === 'fecha_especifica') {
    fechasOK = isYYYYMMDD(fechaEspecifica);
    if (!fechasOK) razonIncompleta = 'fecha_especifica_invalida';
  }

  if (periodo === 'rango_fechas') {
    fechasOK = isYYYYMMDD(fechaInicio) && isYYYYMMDD(fechaFin);
    if (!fechasOK) {
      razonIncompleta = 'fechas_incompletas';
    } else if (cmpDates(fechaFin, fechaInicio) < 0) {
      fechasOK = false;
      razonIncompleta = 'rango_invertido';
    }
  }

  datosCompletos = fechasOK; // usuario opcional
}

log('✅ Datos completos:', datosCompletos);
log('❌ Razón incompleta:', razonIncompleta || '(ninguna)');

// ✅ Resolver fechas automáticamente según periodo (si faltan)
const pad = (n) => String(n).padStart(2, '0');
const fmtLocal = (d) => `${d.getFullYear()}-${pad(d.getMonth() + 1)}-${pad(d.getDate())}`;

// Semana lunes-domingo (España)
const getWeekRange = (baseDate) => {
  const d = new Date(baseDate);
  const day = d.getDay();             // 0 dom, 1 lun, ... 6 sáb
  const diffToMonday = (day + 6) % 7; // lunes => 0
  const monday = new Date(d);
  monday.setDate(d.getDate() - diffToMonday);
  const sunday = new Date(monday);
  sunday.setDate(monday.getDate() + 6);
  return { desde: fmtLocal(monday), hasta: fmtLocal(sunday) };
};

let fecha_inicio_calc = fechaInicio || null;
let fecha_fin_calc    = fechaFin || null;
let fecha_especifica_calc = fechaEspecifica || null;

// ✅ si solo viene una de las dos, alineamos antes de calcular
if (fecha_inicio_calc && !fecha_fin_calc) fecha_fin_calc = fecha_inicio_calc;
if (!fecha_inicio_calc && fecha_fin_calc) fecha_inicio_calc = fecha_fin_calc;

// ✅ si aún faltan, calculamos por periodo
if ((!fecha_inicio_calc || !fecha_fin_calc) && periodo) {
  const hoy = new Date();

  if (periodo === 'hoy') {
    const f = fmtLocal(hoy);
    fecha_inicio_calc = f;
    fecha_fin_calc = f;

  } else if (periodo === 'ayer') {
    const a = new Date(hoy);
    a.setDate(hoy.getDate() - 1);
    const f = fmtLocal(a);
    fecha_inicio_calc = f;
    fecha_fin_calc = f;

  } else if (periodo === 'esta_semana') {
    const r = getWeekRange(hoy);
    fecha_inicio_calc = r.desde;
    fecha_fin_calc = r.hasta;

  } else if (periodo === 'semana_pasada') {
    const last = new Date(hoy);
    last.setDate(hoy.getDate() - 7);
    const r = getWeekRange(last);
    fecha_inicio_calc = r.desde;
    fecha_fin_calc = r.hasta;

  } else if (periodo === 'este_mes' || periodo === 'general') {
    // ✅ 'general' => por defecto mes actual
    const y = hoy.getFullYear();
    const m = hoy.getMonth(); // 0-11
    const desde = new Date(y, m, 1);
    const hasta = new Date(y, m + 1, 0);
    fecha_inicio_calc = fmtLocal(desde);
    fecha_fin_calc = fmtLocal(hasta);

  } else if (periodo === 'mes_pasado') {
    const y = hoy.getFullYear();
    const m = hoy.getMonth();
    const desde = new Date(y, m - 1, 1);
    const hasta = new Date(y, m, 0);
    fecha_inicio_calc = fmtLocal(desde);
    fecha_fin_calc = fmtLocal(hasta);

  } else if (periodo === 'fecha_especifica' && fecha_especifica_calc) {
    fecha_inicio_calc = fecha_especifica_calc;
    fecha_fin_calc = fecha_especifica_calc;
  }
}

// Seguridad final
if (periodo === 'fecha_especifica' && fecha_especifica_calc && (!fecha_inicio_calc || !fecha_fin_calc)) {
  fecha_inicio_calc = fecha_especifica_calc;
  fecha_fin_calc = fecha_especifica_calc;
}

log('🧮 Fechas calculadas =>', { fecha_inicio_calc, fecha_fin_calc, fecha_especifica_calc });

// Salida normalizada
return {
  datos_completos: datosCompletos,
  razon_incompleta: razonIncompleta,
  periodo,
  fecha_especifica: fecha_especifica_calc || null,
  fecha_inicio: fecha_inicio_calc || null,
  fecha_fin: fecha_fin_calc || null,
  usuario: usuarioNorm || null,
  chat_id: chatId,
  mensaje_ia: mensajeIA,
  datos_originales: datosAI
};

