parameters.jsCode:
// DATOS INCOMPLETOS PARA AUSENCIAS - Guardar en memoria
const datosVerificacion = $('Verificar Datos Ausencias Completos').first().json;
const chatId = datosVerificacion.chat_id;
const contextoKey = `memoria_${chatId}`;
let memoria = global[contextoKey] || {};

console.log('❌ DATOS INCOMPLETOS PARA CONSULTA DE AUSENCIAS');
console.log('📅 Período:', datosVerificacion.periodo);
console.log('❌ Razón:', datosVerificacion.razon_incompleta);

// Guardar datos para completar consulta de ausencias
memoria.esperando_confirmacion = 'completar_datos_ausencias';
memoria.esperando_respuesta = true;
memoria.datos_pendientes = {
  accion_original: 'consultar_ausencias',
  periodo_parcial: datosVerificacion.periodo,
  fecha_especifica: datosVerificacion.fecha_especifica,
  razon_incompleta: datosVerificacion.razon_incompleta,
  mensaje_ia_original: datosVerificacion.mensaje_ia,
  datos_originales: datosVerificacion.datos_originales,
  uid: null // Se obtendrá cuando se complete
};

// Actualizar historial
const entradaHistorial = {
  timestamp: new Date().toISOString(),
  usuario: `Consultar ausencias - datos incompletos`,
  bot: `Faltan datos para consulta de ausencias: ${datosVerificacion.razon_incompleta}`,
  accion: 'ausencias_datos_incompletos',
  chat_id: chatId
};

if (!memoria.historial) memoria.historial = [];
memoria.historial.push(entradaHistorial);

global[contextoKey] = memoria;

// Determinar mensaje apropiado
let mensajeParaUsuario = datosVerificacion.mensaje_ia;

if (!mensajeParaUsuario || mensajeParaUsuario.trim() === '') {
  // Mensaje por defecto si la IA no proporcionó uno
  switch (datosVerificacion.razon_incompleta) {
    case 'falta_periodo':
      mensajeParaUsuario = '📅 **Para consultar las ausencias necesito que especifiques el período.**\n\n¿Qué período quieres consultar?\n\n• **"hoy"** - ausencias de hoy\n• **"esta semana"** - ausencias de esta semana\n• **"este mes"** - ausencias de este mes\n• **Una fecha específica** (ej: "2024-10-15")\n• **Un rango** (ej: "del 1 al 15 de octubre")';
      break;
    case 'periodo_no_valido':
      mensajeParaUsuario = '❌ **El período especificado no es válido.**\n\nPor favor, especifica:\n• **"hoy"**, **"esta semana"**, **"este mes"**\n• **Una fecha específica** (ej: "15 de octubre")\n• **Un rango de fechas** (ej: "del 1 al 15 de octubre")';
      break;
    default:
      mensajeParaUsuario = '📅 **Necesito más información para consultar las ausencias.**\n\n¿Qué período quieres consultar?';
  }
}

return {
  chat_id: chatId,
  mensaje_ia: mensajeParaUsuario,
  razon_incompleta: datosVerificacion.razon_incompleta,
  periodo_parcial: datosVerificacion.periodo,
  tipo_respuesta: 'ausencias_datos_incompletos',
  memoria_actualizada: memoria
};

// Preparar Consulta Ausencias1 — versión corregida
// ✅ PRIORIDAD: usar fecha_inicio/fecha_fin del validador (hoy/ayer/semana/etc.)
// ✅ Dominio Odoo correcto: (state ok) AND (solape datetime OR solape date) [AND empleado]

const datos = $('Verificar Datos Ausencias Completos').first().json;
const uid = $('Autenticar Odoo').item.json.result;
if (!uid) throw new Error('No se pudo obtener el UID de autenticación');

const usuario = (datos.usuario || '').trim();
const periodoEntrada = (datos.periodo || 'no_especificado').toString().toLowerCase();

// ✅ Fechas ya calculadas por el validador (si vienen)
let fechaInicio = datos.fecha_inicio || null;
let fechaFin    = datos.fecha_fin || null;
const fechaEspecificaEntrada = datos.fecha_especifica || null;

// Mensaje original (por si hay que parsear)
const rawMsg = (
  datos.mensaje_ia ||
  datos.datos_originales?.mensaje_original ||
  datos.datos_originales?.mensaje ||
  datos.datos_originales?.text ||
  ''
).toString();

const msg = rawMsg
  .toLowerCase()
  .normalize('NFD')
  .replace(/[\u0300-\u036f]/g, ' ')
  .replace(/\s+/g, ' ')
  .trim();

const hoy = new Date();
const curY = hoy.getFullYear();
const pad = (n) => String(n).padStart(2, '0');
const toISO = (y, m, d) => `${y}-${pad(m)}-${pad(d)}`;
const lastDay = (y, m) => new Date(y, m, 0).getDate();
const normYear = (y) => (y < 100 ? 2000 + y : y);

const meses = {
  'enero':1,'ene':1,
  'febrero':2,'feb':2,
  'marzo':3,'mar':3,
  'abril':4,'abr':4,
  'mayo':5,'may':5,
  'junio':6,'jun':6,
  'julio':7,'jul':7,
  'agosto':8,'ago':8,
  'septiembre':9,'setiembre':9,'septiebre':9,'sept':9,'sep':9,
  'octubre':10,'oct':10,
  'noviembre':11,'nov':11,
  'diciembre':12,'dic':12,
};

// ==========================
// 1) SI NO VIENEN FECHAS DEL VALIDADOR → parsear mensaje
// ==========================
if (!fechaInicio || !fechaFin) {
  let r;

  // Rangos: del 3 al 10 de marzo (2026)
  r = msg.match(/\bdel\s+(\d{1,2})\s+al\s+(\d{1,2})\s+de\s+(enero|febrero|marzo|abril|mayo|junio|julio|agosto|septiembre|setiembre|septiebre|sept|sep|octubre|noviembre|diciembre|ene|feb|mar|abr|may|jun|jul|ago|oct|nov|dic)(?:\s+de\s+(\d{2,4}))?\b/);
  if (r) {
    const d1 = +r[1], d2 = +r[2], mNum = meses[r[3]], y = r[4] ? normYear(+r[4]) : curY;
    fechaInicio = toISO(y, mNum, Math.min(d1, d2));
    fechaFin    = toISO(y, mNum, Math.max(d1, d2));
  }

  // Rangos numéricos: 01/02/2026 al 10/02/2026
  if (!fechaInicio) {
    r = msg.match(/\bdel\s+(\d{1,2})[\/\-](\d{1,2})(?:[\/\-](\d{2,4}))?\s+al\s+(\d{1,2})[\/\-](\d{1,2})(?:[\/\-](\d{2,4}))?\b/);
    if (r) {
      const d1 = +r[1], m1 = +r[2], y1 = r[3] ? normYear(+r[3]) : curY;
      const d2 = +r[4], m2 = +r[5], y2 = r[6] ? normYear(+r[6]) : (r[3] ? y1 : curY);
      const f1 = new Date(y1, m1 - 1, d1), f2 = new Date(y2, m2 - 1, d2);
      const a = f1 <= f2 ? f1 : f2, b = f1 <= f2 ? f2 : f1;
      fechaInicio = toISO(a.getFullYear(), a.getMonth() + 1, a.getDate());
      fechaFin    = toISO(b.getFullYear(), b.getMonth() + 1, b.getDate());
    }
  }

  // Fecha única: 29/01/2026
  if (!fechaInicio) {
    r = msg.match(/\b(\d{1,2})[\/\-](\d{1,2})(?:[\/\-](\d{2,4}))?\b/);
    if (r) {
      const d = +r[1], m = +r[2], y = r[3] ? normYear(+r[3]) : curY;
      fechaInicio = fechaFin = toISO(y, m, d);
    }
  }

  // Fecha única: 29 de enero (2026)
  if (!fechaInicio) {
    r = msg.match(/\b(\d{1,2})\s+de\s+(enero|febrero|marzo|abril|mayo|junio|julio|agosto|septiembre|setiembre|septiebre|sept|sep|octubre|noviembre|diciembre|ene|feb|mar|abr|may|jun|jul|ago|oct|nov|dic)(?:\s+de\s+(\d{2,4}))?\b/);
    if (r) {
      const d = +r[1], mNum = meses[r[2]], y = r[3] ? normYear(+r[3]) : curY;
      fechaInicio = fechaFin = toISO(y, mNum, d);
    }
  }

  // Solo mes: "enero" / "enero de 2026"
  if (!fechaInicio) {
    r = msg.match(/\b(enero|febrero|marzo|abril|mayo|junio|julio|agosto|septiembre|setiembre|septiebre|sept|sep|octubre|noviembre|diciembre|ene|feb|mar|abr|may|jun|jul|ago|oct|nov|dic)(?:\s+de\s+(\d{2,4}))?\b/);
    if (r) {
      const mNum = meses[r[1]], y = r[2] ? normYear(+r[2]) : curY;
      const ld = lastDay(y, mNum);
      fechaInicio = toISO(y, mNum, 1);
      fechaFin    = toISO(y, mNum, ld);
    }
  }

  // Fallback a fecha específica si venía
  if (!fechaInicio && fechaEspecificaEntrada) {
    fechaInicio = fechaEspecificaEntrada;
