const toES = (isoDateStr) => {
  // Acepta 'YYYY-MM-DD HH:mm:ss' o ISO
  const safe = isoDateStr.includes('T') ? isoDateStr : isoDateStr.replace(' ', 'T');
  const d = new Date(safe);
  if (Number.isNaN(d.getTime())) return isoDateStr; // fallback
  return d.toLocaleDateString('es-ES', { year: 'numeric', month: 'short', day: 'numeric' });
};

const plural = (n, uno, muchos) => (n === 1 ? uno : muchos);
const round1 = (n) => Math.round(n * 10) / 10;

console.log('🏖️ Ausencias encontradas:', ausencias.length);
console.log('📋 Lista de ausencias:', ausencias.map(a => `${a.employee_id?.[1]} - ${a.holiday_status_id?.[1]} (${a.date_from} → ${a.date_to})`));

let titulo = '';
let icono = '🏖️';

if (fechaInicio === fechaFin) {
  titulo = `Ausencias del ${toES(`${fechaInicio} 00:00:00`)}`;
  icono = '📅';
} else {
  titulo = `Ausencias del ${fechaInicio} al ${fechaFin}`;
  icono = '📋';
}

let texto = `${icono} **${titulo}**\n`;
if (empleadoFiltro) {
  texto += `🔎 **Empleado:** ${empleadoFiltro}\n\n`;
} else {
  texto += `\n`;
}

if (ausencias.length === 0) {
  texto += `✅ **Sin ausencias registradas** en el periodo consultado.\n`;
  texto += `Todo el equipo está disponible. 💪\n`;
} else {
  // Agrupar por empleado
  const ausenciasPorEmpleado = {};
  for (const a of ausencias) {
    const empleado = (a.employee_id && a.employee_id[1]) ? a.employee_id[1] : '— sin nombre —';
    if (!ausenciasPorEmpleado[empleado]) ausenciasPorEmpleado[empleado] = [];
    ausenciasPorEmpleado[empleado].push(a);
  }

  const empleados = Object.keys(ausenciasPorEmpleado);
  texto += `👥 **${empleados.length} ${plural(empleados.length, 'empleado con ausencia', 'empleados con ausencias')}**:\n\n`;

  const iconoTipoDe = (tipo) => {
    const t = (tipo || '').toLowerCase();
    if (t.includes('vacacion')) return '🏖️';
    if (t.includes('enferm')) return '🤒';
    if (t.includes('permiso') || t.includes('ausencia')) return '📋';
    if (t.includes('matern') || t.includes('patern')) return '👶';
    return '🏷️';
    };

  // Cálculo de resumen global y por tipo
  let totalRegistros = 0;
  let totalDias = 0;
  const porTipo = new Map(); // tipo -> {count, days}

  empleados.forEach((empleado, idx) => {
    const lista = ausenciasPorEmpleado[empleado] || [];
    texto += `**${idx + 1}. ${empleado}**\n`;
    lista.forEach(a => {
      const tipo = a.holiday_status_id?.[1] || 'Tipo no especificado';
      const desde = toES(a.date_from);
      const hasta = toES(a.date_to);
      const dias = round1(Number(a.number_of_days || 0));
      const icon = iconoTipoDe(tipo);

      if (desde === hasta) {
        texto += `   ${icon} ${tipo} — ${desde} (${dias} ${plural(dias, 'día', 'días')})\n`;
      } else {
        texto += `   ${icon} ${tipo} — ${desde} a ${hasta} (${dias} ${plural(dias, 'día', 'días')})\n`;
      }

      totalRegistros += 1;
      totalDias += dias;

      const prev = porTipo.get(tipo) || { count: 0, days: 0 };
      prev.count += 1;
      prev.days = round1(prev.days + dias);
      porTipo.set(tipo, prev);
    });
    texto += `\n`;
  });

  texto += `📊 **Resumen:** ${totalRegistros} ${plural(totalRegistros, 'ausencia', 'ausencias')} `;
  texto += `que suman ${round1(totalDias)} ${plural(totalDias, 'día', 'días')}.\n`;

  if (porTipo.size > 1) {
    texto += `• Por tipo: `;
    const partes = [];
    for (const [tipo, v] of porTipo.entries()) {
      partes.push(`${tipo} (${v.count}/${round1(v.days)} ${plural(v.days, 'día', 'días')})`);
    }
    texto += partes.join(' · ') + '\n';
  }
}

texto += `\n\n📅 **Período consultado:** ${fechaInicio}${fechaInicio !== fechaFin ? ` al ${fechaFin}` : ''}`;

