const seed = $('FSM ▸ Preparar Datos Completos')?.first()?.json || {};
const db = ctx.db || seed.db || 'lsanchezgoshawk-bot-main-27977821';
const uid = ctx.uid || $('Autenticar Odoo')?.first()?.json?.result;
if (!uid) throw new Error('Falta uid (Autenticar Odoo)');

const r = ctx.result || ctx.body?.result;
if (!r) throw new Error('No se obtuvo ID de tarea (FSM)');
const task_id = Number(r);

// Rehidratar negocio
const proyecto_id = ctx.proyecto_id || seed.proyecto_id;
const tarea = ctx.tarea || seed.tarea;
const horas = Number(ctx.horas ?? seed.horas ?? 0);
const descripcion = ctx.descripcion || seed.descripcion || `Trabajo en ${tarea}`;
const fecha_hoy = ctx.fecha_hoy || seed.fecha_hoy || new Date().toISOString().split('T')[0];

const payload = {
  jsonrpc: '2.0',
  method: 'call',
  params: {
    service: 'object',
    method: 'execute_kw',
    args: [
      db, uid, 'admin',
      'account.analytic.line', 'create',
      [[{
        name: descripcion,
        project_id: proyecto_id,
        task_id: task_id,
        unit_amount: horas,
        date: fecha_hoy
      }]]
    ]
  },
  id: Math.floor(Math.random()*1e6)
};

return {
  ...ctx,
  db, uid,
  proyecto_id, tarea, horas, descripcion, fecha_hoy,
  task_id,
  payload
};

// PASO 4: PUBLICAR NOTA DE MATERIALES en la tarea (si hay)
const ctx = $json;

// Rehidratar contexto base
const seed = $('FSM ▸ Preparar Datos Completos')?.first()?.json || {};
const db = ctx.db || seed.db || 'lsanchezgoshawk-bot-main-27977821';
const uid = ctx.uid || $('Autenticar Odoo')?.first()?.json?.result;
if (!uid) throw new Error('Falta uid (Autenticar Odoo)');

// Rehidratar materiales (prioridad: ctx → seed)
const productosSeed = Array.isArray(seed.productos) ? seed.productos : [];
const productos = Array.isArray(ctx.productos) ? ctx.productos : productosSeed;

// Si no hay materiales, no publicamos nota
if (!Array.isArray(productos) || productos.length === 0) {
  return {
    ...ctx,
    db,
    uid,
    productos: [],
    hay_materiales: false,
    skip_nota: true,
    motivo: 'Sin materiales',
  };
}

// Texto plano
const lineas = (Array.isArray(productos) ? productos : [])
  .map(p => `• ${Number(p.cantidad || 1)} × ${String(p.nombre || '').trim()}`)
  .join('\n');

const texto = [
  '🧰 Material utilizado:',
  lineas,
  'Contexto: Servicio de campo (FSM)',
].join('\n');

// Resolver task_id SOLO con nodos de la rama FSM
const taskId =
  ctx.task_id ??
  $('FSM ▸ Preparar Registrar Horas (final)')?.first()?.json?.task_id ??
  $('FSM ▸ HTTP Crear Tarea')?.first()?.json?.result;

if (!taskId) {
  return {
    ...ctx,
    db,
    uid,
    productos,
    hay_materiales: true,
    skip_nota: true,
    motivo: 'Sin task_id',
  };
}

// 👇 NUEVO: sacar author_id desde "Propagar Usuario Odoo" → "Buscar Usuario Odoo" o desde seed
let authorId = null;

// Intento 1: Desde "Propagar Usuario Odoo" (nueva fuente)
try {
  const propagarUsuario = $('Propagar Usuario Odoo')?.first()?.json;
  if (propagarUsuario) {
    const usuarioData = Array.isArray(propagarUsuario) ? propagarUsuario[0] : propagarUsuario;
    // odoo_partner_id es el partner_id del usuario en Odoo
    if (Number.isFinite(usuarioData?.odoo_partner_id)) {
      authorId = Number(usuarioData.odoo_partner_id);
    }
  }
} catch (e) {
  console.log('⚠️ No se pudo extraer partner_id desde Propagar Usuario Odoo:', e.message);
}

// Intento 2: Fallback a "Buscar Usuario Odoo"
if (!Number.isFinite(authorId)) {
  try {
    const buo = $('Buscar Usuario Odoo')?.first()?.json;
    const lista = buo?.json?.result ?? buo?.result ?? buo?.body?.result ?? [];
    const u = Array.isArray(lista) && lista[0] ? lista[0] : null;
    if (u?.partner_id) {
      authorId = Array.isArray(u.partner_id)
        ? Number(u.partner_id[0])
        : Number(u.partner_id);
    }
  } catch {}
}

// Intento 3: Fallback a seed
if (!Number.isFinite(authorId) && Number.isFinite(seed.odoo_partner_id)) {
  authorId = Number(seed.odoo_partner_id);
}

// Valores de la nota
const valsMessage = {
  body: texto,
  message_type: 'comment',
  subtype_xmlid: 'mail.mt_comment',
};

if (Number.isFinite(authorId)) {
  valsMessage.author_id = authorId;  // 👈 autor real de la nota
}

const payload = {
  jsonrpc: '2.0',
  method: 'call',
  params: {
    service: 'object',
    method: 'execute_kw',
    args: [
      db,
      uid,
      'admin',
      'project.task',
      'message_post',
      [ taskId ],
      valsMessage,
    ],
  },
  id: Math.floor(Math.random() * 1e6),
};

return {
  ...ctx,
  db,
  uid,
  task_id: taskId,
  productos,
  hay_materiales: true,
  payload,
};

// Construir salida como ARRAY y asegurar chat_id y horas_id correctos
const items = $input.all();

return items.map(({ json: ctx }) => {
  // horas_id desde el HTTP que crea horas (directo o FSM), con fallbacks
  const horasId =
    $('FSM ▸ HTTP Registrar Horas')?.first()?.json?.result ??
    $('FSM ▸ HTTP Registrar Horas')?.first()?.json?.body?.result ??
    ctx.result ?? ctx.body?.result ?? null;

  // chat_id robusto
  const chatId =
    ctx.chat_id ??
    $('FSM ▸ Preparar Datos Completos')?.first()?.json?.chat_id ??
    $('FSM ▸ HTTP Registrar Horas')?.first()?.json?.registro?.chat_id ??
    0;

  return {
    json: {
      chat_id: chatId,
      resumen_creado: {
        proyecto: ctx.proyecto ?? $('FSM ▸ Preparar Datos Completos')?.first()?.json?.proyecto,
        proyecto_id: ctx.proyecto_id,
        tarea: ctx.tarea ?? $('FSM ▸ Preparar Datos Completos')?.first()?.json?.tarea,
        tarea_id: ctx.task_id,
        horas: ctx.horas ?? $('FSM ▸ Preparar Datos Completos')?.first()?.json?.horas ?? 0,
        horas_id: horasId,
        descripcion: ctx.descripcion ?? $('FSM ▸ Preparar Datos Completos')?.first()?.json?.descripcion,
        fecha: ctx.fecha_hoy ?? $('FSM ▸ Preparar Datos Completos')?.first()?.json?.fecha_hoy
      }
    }
  };
});

