parameters.jsCode:
// Lee la compañía del usuario (uid) para usarla al crear el proyecto FSM
const ctx = $json;

// Rehidratar
const db = ctx.db
  || $('FSM ▸ Preparar Datos Completos')?.first()?.json?.db
  || 'lsanchezgoshawk-bot-main-27977821';
const uid = ctx.uid
  || $('Autenticar Odoo')?.first()?.json?.result;
if (!uid) throw new Error('Falta uid (Autenticar Odoo)');

const payload = {
  jsonrpc: '2.0',
  method: 'call',
  params: {
    service: 'object',
    method: 'execute_kw',
    args: [
      db,
      uid,
      'admin',
      'res.users',
      'read',
      [[uid], ['company_id']]
    ],
  },
  id: Math.floor(Math.random() * 1e6),
};

return { ...ctx, db, uid, payload };

// Validar que haya cliente antes de seguir el flujo FSM
const ctx = $json;

if (ctx.cliente_faltante === true) {
  const chatId = Number(ctx.chat_id || 0);
  const contextoKey = `memoria_${chatId}`;

  // Guardar datos pendientes en memoria global (puedes además persistir en Postgres si quieres)
  let memoria = global[contextoKey] || {};
  memoria.esperando_confirmacion = 'solicitar_cliente_fsm';
  memoria.esperando_respuesta = true;
  memoria.datos_pendientes = {
    accion_original: 'registrar_parte_y_stock',
    proyecto: ctx.proyecto,
    tarea: ctx.tarea,
    horas: Number(ctx.horas || 0),
    descripcion: ctx.descripcion || '',
    productos: Array.isArray(ctx.productos) ? ctx.productos : [],
    cliente: '', // faltante
    db: ctx.db,
    uid: ctx.uid,
    fecha: ctx.fecha_hoy,
  };
  global[contextoKey] = memoria;

  return {
    accion: 'falta_cliente_fsm',
    chat_id: chatId,
    mensaje_bot: 'Falta cliente para el parte de trabajo (FSM). ¿A qué cliente va dirigido?',
    memoria_actualizada: memoria,
  };
}

// Cliente OK → continuar
return { ...ctx, accion: 'continuar_fsm' };

// Busca el partner por nombre (si hay cliente). Si no hay cliente, saltar búsqueda.
const seed = $('FSM ▸ Preparar Datos Completos')?.first()?.json || {};
const ctx = $json;

const db = ctx.db || seed.db || 'lsanchezgoshawk-bot-main-27977821';
const uid = ctx.uid || $('Autenticar Odoo')?.first()?.json?.result;
if (!uid) throw new Error('Falta uid (Autenticar Odoo)');

const cliente = String(ctx.cliente || seed.cliente || '').trim();
if (!cliente) {
  // No hay cliente → saltar búsqueda, pero NO montes el payload de create aquí
  return { ...ctx, db, uid, skip_cliente_search: true };
}

const payload = {
  jsonrpc: '2.0',
  method: 'call',
  params: {
    service: 'object',
    method: 'execute_kw',
    args: [
      db, uid, 'admin',
      'res.partner', 'search_read',
      [[['name', 'ilike', cliente]]],
      { fields: ['id','name'], limit: 1 }
    ],
  },
  id: Math.floor(Math.random()*1e6)
};

return { ...ctx, db, uid, cliente, skip_cliente_search: false, payload };

const ctx = $json;

let partnerId = null;
let partnerName = null;

if (!ctx.skip_cliente_search) {
  const r = ctx.result || ctx.body?.result || [];
  if (Array.isArray(r) && r[0]?.id) {
    partnerId = Number(r[0].id);
    partnerName = String(r[0].name || '').trim();
  }
}

return { ...ctx, partner_id: partnerId, partner_name: partnerName };

// Asignar cliente (partner_id) a la tarea SIN IF: siempre retorna un payload válido
const ctx = $json;
const seed = $('FSM ▸ Preparar Datos Completos')?.first()?.json || {};

const db  = ctx.db || seed.db || 'lsanchezgoshawk-bot-main-27977821';
const uid = ctx.uid || $('Autenticar Odoo')?.first()?.json?.result;

// Rehidratar task_id de forma robusta
let taskId =
  Number(ctx.task_id) ||
  Number($('FSM ▸ Preparar Registrar Horas')?.first()?.json?.task_id) ||
  Number($('FSM ▸ HTTP Crear Tarea')?.first()?.json?.result);

if (!taskId) {
  const ht = $('HTTP Buscar Tarea FSM')?.first()?.json;
  const rr = ht?.result || ht?.body?.result || [];
  if (Array.isArray(rr) && rr[0]?.id) taskId = Number(rr[0].id);
}

// Rehidratar partner_id
const partnerId = Number(
  ctx.partner_id ?? $('FSM ▸ Resolver Cliente ID')?.first()?.json?.partner_id
);

// Construir args del JSON-RPC: si tenemos taskId/partnerId → write; si no, un no-op seguro
let args;
if (uid && Number.isFinite(taskId) && Number.isFinite(partnerId)) {
  args = [
    db, uid, 'admin',
    'project.task', 'write',
    [[ taskId ], { partner_id: partnerId }]
  ];
} else {
  // No-op seguro para evitar 400 cuando falte algún dato (sin IF)
  args = [
    db, uid || 1, 'admin',
    'res.partner', 'search_read',
    [[['id', '=', 0]]],
    { fields: ['id'], limit: 0 }
  ];
}

const payload = {
  jsonrpc: '2.0',
  method: 'call',
  params: { service: 'object', method: 'execute_kw', args },
  id: Math.floor(Math.random() * 1e6),
};

return {
  ...ctx,
  db, uid,
  task_id: Number.isFinite(taskId) ? taskId : null,
  partner_id: Number.isFinite(partnerId) ? partnerId : null,
  payload
};

// Construye el create final incluyendo partner_id y user_id si existen (robusto)
const ctx  = $json;
const seed = $('FSM ▸ Preparar Datos Completos')?.first()?.json || {};

const db  = ctx.db || seed.db || 'lsanchezgoshawk-bot-main-27977821';
const uid = ctx.uid || $('Autenticar Odoo')?.first()?.json?.result;
if (!uid) throw new Error('Falta uid (Autenticar Odoo)');

// Helpers
const asNum = v => {
  const n = Number(v);
  return Number.isFinite(n) ? n : null;
};
const getJson = (name) => {
  try { return $(name)?.first()?.json || null; } catch { return null; }
};
const httpOut = (name) => {
  const h = getJson(name) || {};
  return h.result ?? h.body?.result ?? h.json?.result ?? null;
};

// Recolectar candidatos de proyecto_id
const candProyecto = [
  ctx.proyecto_id,
  seed.proyecto_id,
  getJson('FSM ▸ Preparar Crear Tarea')?.proyecto_id,
  httpOut('FSM ▸ HTTP Crear Proyecto'),
  getJson('FSM ▸ Resolver Proyecto ID')?.proyecto_id,
  getJson('FSM ▸ Resolver Tarea ID')?.proyecto_id,
  (() => {
    const ht = getJson('FSM ▸ HTTP Buscar Tarea');
    const rr = ht?.result || ht?.body?.result || ht?.json?.result || [];
    return Array.isArray(rr) && rr[0]?.project_id
      ? (Array.isArray(rr[0].project_id) ? rr[0].project_id[0] : rr[0].project_id)
      : null;
  })(),
].map(asNum).filter(Number.isFinite);

const proyectoId = candProyecto[0] || null;

// Recolectar candidatos de task_id
const candTask = [
  ctx.task_id,
  getJson('FSM ▸ Preparar Registrar Horas')?.task_id,
  httpOut('FSM ▸ HTTP Crear Tarea'),
  getJson('FSM ▸ Resolver Tarea ID')?.task_id,
  (() => {
    const ht2 = getJson('FSM ▸ HTTP Buscar Tarea');
    const rr2 = ht2?.result || ht2?.body?.result || ht2?.json?.result || [];
    return Array.isArray(rr2) && rr2[0]?.id ? rr2[0].id : null;
  })(),
].map(asNum).filter(Number.isFinite);

const taskId = candTask[0] || null;

if (!proyectoId || !taskId) {
  throw new Error(`Faltan ids de proyecto o tarea (proyecto_id=${proyectoId ?? 'null'}, task_id=${taskId ?? 'null'})`);
}

// partner_id opcional
let partnerId = null;
if (typeof ctx.partner_id === 'number') partnerId = ctx.partner_id;
const resolvedPartner = getJson('FSM ▸ Resolver Cliente ID')?.partner_id;
if (partnerId === null && typeof resolvedPartner === 'number') partnerId = resolvedPartner;

// 👇 NUEVO: Extraer odoo_user_id desde 'Propagar Usuario Odoo'
let odoo_user_id = null;
try {
  const propagarUsuario = $('Propagar Usuario Odoo')?.first()?.json;
  if (propagarUsuario) {
    const usuarioData = Array.isArray(propagarUsuario) ? propagarUsuario[0] : propagarUsuario;
    odoo_user_id = usuarioData?.odoo_user_id;
  }
} catch (e) {
  console.log('⚠️ No se pudo extraer odoo_user_id:', e.message);
}

// Datos de la línea
const horas      = asNum(ctx.horas ?? seed.horas) ?? 0;
const fecha_hoy  = ctx.fecha_hoy || seed.fecha_hoy || new Date().toISOString().split('T')[0];
const descripcion= ctx.descripcion || seed.descripcion || `Trabajo en ${ctx.tarea || ''}`;

const lineVals = {
  name: descripcion,
  project_id: proyectoId,
  task_id: taskId,
  unit_amount: horas,
  date: fecha_hoy,
  ...(Number.isFinite(partnerId) ? { partner_id: partnerId } : {}),
  ...(Number.isFinite(odoo_user_id) ? { user_id: odoo_user_id } : {}),
};

const payload = {
  jsonrpc: '2.0',
  method: 'call',
  params: {
    service: 'object',
    method: 'execute_kw',
    args: [
      db, uid, 'admin',
      'account.analytic.line', 'create',
      [[ lineVals ]]
    ]
  },
  id: Math.floor(Math.random()*1e6)
};

return { ...ctx, db, uid, proyecto_id: proyectoId, task_id: taskId, partner_id: partnerId ?? null, odoo_user_id, payload };

// PREPARAR BÚSQUEDA PRODUCTOS (tokens + default_code + AND sale_ok)
// Objetivo: conseguir un pool amplio y relevante para que el fuzzy sugiera
// "hamburguesa con queso" y "azucar 10 kg", etc.

const datos = $json;
const uid = datos.uid;
const productos = Array.isArray(datos.productos) ? datos.productos : [];

const STOP = new Set(['con','de','y','para','en','la','el','los','las','por','del','a','al']);
const normalize = (s) =>
  (s || '')
    .toString()
    .normalize('NFD').replace(/[\u0300-\u036f]/g,'')
    .toLowerCase()
    .replace(/[^a-z0-9%\. ]+/g,' ')
    .replace(/\s+/g,' ')
    .trim();

// genera variantes útiles: singular/plural básico y número+unidad
function variants(tok) {
  const out = new Set([tok]);
  // singularización sencilla
  if (tok.endsWith('es')) out.add(tok.slice(0, -2));
  if (tok.endsWith('s'))  out.add(tok.slice(0, -1));

  // número+unidad: 5kg -> 5 , kg , 5kg
  const m = tok.match(/^(\d+(?:\.\d+)?)(kg|g|mg|l|ml|cm|mm|m|u|uds?)$/);
  if (m) { out.add(m[1]); out.add(m[2]); }

  return [...out];
}

function tokens(nombre) {
  const base = normalize(nombre);
  if (!base) return [];
  const raw = base.split(' ').filter(t => t && !STOP.has(t));
  const uniq = new Set();
  for (const tk of raw) for (const v of variants(tk)) if (v.length >= 2) uniq.add(v);
  return [...uniq];
}

// 1) Construir hojas (leaves) para OR global: name ilike tk  OR  default_code ilike tk
let leaves = [];
for (const p of productos) {
  if (!p || typeof p.nombre !== 'string') continue;
  const tks = tokens(p.nombre);
  for (const tk of tks) {
    leaves.push(['name', 'ilike', tk]);
    leaves.push(['default_code', 'ilike', tk]);
  }
}

// deduplicar leaves
const seen = new Set();
leaves = leaves.filter(l => {
  const k = JSON.stringify(l);
  if (seen.has(k)) return false;
  seen.add(k);
  return true;
});

// --- construir dominio correcto (plano) ---

let domain;

if (leaves.length === 0) {
  // sin términos -> dominio imposible (evita resultados)
  domain = [['id', '=', -1]];
} else {
  // OR de todas las hojas:  [ '|', '|', ..., hoja1, hoja2, ..., hojaN ]
  const orTokens = (leaves.length === 1)
    ? [leaves[0]]
    : [...Array(leaves.length - 1).fill('|'), ...leaves];

  // AND con sale_ok:  ['&', ['sale_ok','=',true],  ...orTokens ]
  domain = ['&', ['sale_ok', '=', true], ...orTokens];
}

console.log('🔍 Dominio construido (plano):', JSON.stringify(domain));


// 2) Payload a Odoo
const payloadId = Math.floor(Math.random() * 1e6);
const jsonRpcPayload = {
  jsonrpc: '2.0',
  method: 'call',
  params: {
    service: 'object',
    method: 'execute_kw',
    args: [
      'lsanchezgoshawk-bot-main-27977821',
      uid,
      'admin',
      'product.product',
      'search_read',
      [domain],
      {
        fields: ['id', 'name', 'list_price', 'uom_id', 'default_code'],
        limit: 300  // un poco más amplio para que el fuzzy tenga material
      }
    ]
  },
  id: payloadId
};

return {
  payload: jsonRpcPayload,
  productos_solicitados: productos,
  cliente_id: datos.cliente_id,
  cliente_encontrado: datos.cliente_encontrado,
  cliente_datos: datos.cliente_datos,
  cliente_nombre: datos.cliente_nombre,
  productos: datos.productos,
  chat_id: datos.chat_id,
  uid: datos.uid,
  datos_originales: datos.datos_originales
};

// Adaptar reanudación → input de FSM Inventario
const d = $json || {};
const r = d.reanudacion || {};
const productos = Array.isArray(r.productos) ? r.productos : [];

if (!productos.length) {
  throw new Error('Reanudación sin productos');
}

return {
  // acción/finalidad
  accion: 'fsm_inventario',
  tipo_accion: (r.tipo_accion || 'reponer'),
  warehouse_hint: (r.warehouse_hint || 'principal'),

  // lote de productos (tal como espera tu FSM Inventario)
  productos: productos.map(p => ({
    nombre: String(p?.nombre || '').trim(),
    cantidad: Number(p?.cantidad || 1) || 1,
  })),

  // credenciales/base
  base_url: r.base_url || d.base_url,
  db: r.db || d.db,
  uid: r.uid || d.uid,
  admin_password: r.admin_password || d.admin_password || 'admin',

  // usuario
  chat_id: d.chat_id,
  user_id: d.user_id,
  user_name: d.user_name,

  // bandera opcional para limpiar memoria al terminar
  limpiar_memoria: true,
};

// PREPARAR PRODUCTOS (detección fuzzy + parser de correcciones con filtrado por tokens)

const httpResult = $json.result; // de HTTP Buscar Productos FIXED
const datosBusqueda = $('Preparar Búsqueda Productos').item?.json || {};
const productosSolicitados = Array.isArray(datosBusqueda.productos_solicitados) ? datosBusqueda.productos_solicitados : [];
const chatId = datosBusqueda.chat_id || $('Router de Acciones FIXED').item?.json?.chat_id;
const uid = datosBusqueda.uid || $('Autenticar Odoo').item?.json?.result;
const clienteId = datosBusqueda.cliente_id ?? $('Procesar Cliente Encontrado').item?.json?.cliente_id ?? null;
const clienteDatos = datosBusqueda.cliente_datos || $('Procesar Cliente Encontrado').item?.json?.cliente_datos || {};
const clienteNombre = clienteDatos?.name || datosBusqueda.cliente_nombre || '';

// ================== 0) Parser de correcciones (nombre x cantidad) ==================
const mensaje = (
  $('Router de Acciones FIXED').item?.json?.mensaje_original ||
  $('Router de Acciones FIXED').item?.json?.text ||
  $('Switch Acciones').item?.json?.mensaje_original ||
  $json.mensaje || $json.text || ''
).toString();

function parseCorrecciones(texto) {
  const partes = texto.split(/[,;\n]+/);
  const res = [];
  for (const p of partes) {
    const t = p.trim();
    if (!t) continue;
    const m = t.match(/(.+?)\s*x\s*([\d]+(?:[.,]\d+)?)/i);
    if (m) {
      const nombre = m[1].trim();
      const cantidad = parseFloat(m[2].replace(',', '.'));
      if (nombre && !Number.isNaN(cantidad)) res.push({ nombre, cantidad });
    }
  }
  return res;
}

const productosCorregidos = parseCorrecciones(mensaje);
if (productosCorregidos.length > 0) {
  return {
    correcciones_recibidas: true,
    productos_corregidos: productosCorregidos,
    chat_id: chatId,
    uid,
    cliente_id: clienteId,
    cliente: clienteNombre
  };
}

// ================== 1) Detección inicial + fuzzy ==================
const pool = Array.isArray(httpResult) ? httpResult : [];

const STOP = new Set(['con','de','y','para','en','la','el','los','las','por','del','a','al']);
const normalize = (s) =>
  (s || '')
    .toString()
    .normalize('NFD').replace(/[\u0300-\u036f]/g,'')
    .replace(/\s+/g,' ')
    .toLowerCase()
    .trim();

function tokenVariants(tok) {
  const out = new Set([tok]);
  if (tok.endsWith('es')) out.add(tok.slice(0, -2));
  if (tok.endsWith('s'))  out.add(tok.slice(0, -1));
  const m = tok.match(/^(\d+(?:\.\d+)?)(kg|g|mg|l|ml|cm|mm|m|u|uds?)$/);
  if (m) { out.add(m[1]); out.add(m[2]); }
  return [...out];
}

function tokens(s) {
  const base = normalize(s);
  if (!base) return [];
  const raw = base.split(/[ \-_/]+/).filter(t => t && !STOP.has(t));
  const uniq = new Set();
  for (const tk of raw) for (const v of tokenVariants(tk)) if (v.length >= 2 || /^\d+(?:\.\d+)?$/.test(v)) uniq.add(v);
  return [...uniq];
}

function levenshtein(a, b) {
  a = normalize(a); b = normalize(b);
  const m = a.length, n = b.length;
  if (m === 0) return n;
  if (n === 0) return m;
  const dp = Array.from({ length: m + 1 }, () => Array(n + 1).fill(0));
  for (let i = 0; i <= m; i++) dp[i][0] = i;
  for (let j = 0; j <= n; j++) dp[0][j] = j;
  for (let i = 1; i <= m; i++) {
    for (let j = 1; j <= n; j++) {
      const cost = a[i - 1] === b[j - 1] ? 0 : 1;
      dp[i][j] = Math.min(
        dp[i - 1][j] + 1,
        dp[i][j - 1] + 1,
        dp[i - 1][j - 1] + cost
      );
    }
  }
  return dp[m][n];
}

function jaccardToken(a, b) {
  const A = new Set(tokens(a));
  const B = new Set(tokens(b));
  const inter = [...A].filter(x => B.has(x)).length;
  const uni = new Set([...A, ...B]).size;
  return uni === 0 ? 0 : inter / uni;
}

function similarity(a, b) {
  const la = normalize(a), lb = normalize(b);
  if (!la || !lb) return 0;
  if (la === lb) return 1;
  const maxLen = Math.max(la.length, lb.length);
  const ld = levenshtein(la, lb);
  const levScore = 1 - (ld / Math.max(1, maxLen));
  const jac = jaccardToken(a, b);
  return (levScore * 0.7) + (jac * 0.3);
}

// Umbrales
const THRESHOLD_HIGH = 0.88;    // aceptar automáticamente
const MIN_SUGGESTION = 0.35;    // descartar ruido de muy baja similitud

const byName = new Map(pool.map(p => [normalize(p.name), p]));
const lineasPreconfirmadas = [];
const productosAmbiguos = [];
const productosNoEncontrados = [];

for (const sol of productosSolicitados) {
  const nombreSolicitado = (sol?.nombre || '').toString();
  const cant = Number(sol?.cantidad || 1);
  if (!nombreSolicitado) continue;

  const qNorm = normalize(nombreSolicitado);
  const qTokens = new Set(tokens(nombreSolicitado));

  // 1) Exacto por nombre normalizado
  if (byName.has(qNorm)) {
    const p = byName.get(qNorm);
    lineasPreconfirmadas.push({
      product_id: p.id,
      product_uom_qty: cant,
      price_unit: p.list_price,
      name: p.name
    });
    continue;
  }

  // 2) Prefiltro: solo candidatos que compartan tokens con la consulta
  const candidates = [];
  for (const p of pool) {
    const cName = p.name || '';
    const cTokens = new Set(tokens(cName));
    const overlap = [...qTokens].some(t => cTokens.has(t));  // ¿comparte algún token?
    const score = similarity(nombreSolicitado, cName);

    // Mantén candidatos si comparten token o tienen un mínimo de similitud
    if (overlap || score >= MIN_SUGGESTION) {
      candidates.push({
        id: p.id,
        name: cName,
        price: p.list_price,
        score
      });
    }
  }

  // Ordenar por puntuación
  candidates.sort((a, b) => b.score - a.score);

  const best = candidates[0];
  const top = candidates.slice(0, 7); // sugerencias por ESTE producto

  if (best && best.score >= THRESHOLD_HIGH) {
    lineasPreconfirmadas.push({
      product_id: best.id,
      product_uom_qty: cant,
      price_unit: best.price,
      name: best.name
    });
  } else if (top.length > 0) {
    productosAmbiguos.push({
      solicitado: nombreSolicitado,
      cantidad: cant,
      candidatos: top.map(x => ({ id: x.id, name: x.name, price: x.price, score: Number(x.score.toFixed(2)) }))
    });
  } else {
    productosNoEncontrados.push(nombreSolicitado);
    productosAmbiguos.push({ solicitado: nombreSolicitado, cantidad: cant, candidatos: [] });
  }
}

// 3) Si hay ambigüedad → mensaje por producto con SUS sugerencias
if (productosAmbiguos.length > 0) {
  const instrucciones = [
    '🔎 Algunos productos necesitan corrección.',
    '',
    'Envía **TODAS** las correcciones en **un solo** mensaje con el formato:',
    '`Nombre correcto x cantidad`, separados por comas.',
    ''
  ];
  for (const amb of productosAmbiguos) {
    const sug = amb.candidatos.length
      ? amb.candidatos.map((c,i) => `${i+1}. ${c.name}`).join('\n')
      : '— Sin sugerencias —';
    instrucciones.push(`• **${amb.solicitado}** x ${amb.cantidad}`);
    instrucciones.push(`  Sugerencias:\n${sug}`);
    instrucciones.push('');
  }
  instrucciones.push("Ej.: `Tarta de queso x 2, Galletas con caramelo x 1, Saco de azucar 10kg x 5`");

  return {
    requiere_correccion: true,
    texto_correccion: instrucciones.join('\n'),
    productos_ambiguos: productosAmbiguos,
    productos_solicitados: productosSolicitados,
    lineas_preconfirmadas: lineasPreconfirmadas,
    productos_no_encontrados: productosNoEncontrados,
    chat_id: chatId,
    uid,
    cliente_id: clienteId,
    cliente_datos: clienteDatos
  };
}

// 4) Sin ambigüedad → seguir flujo normal
return {
  requiere_correccion: false,
  productos_solicitados: productosSolicitados,
  lineas_preconfirmadas: lineasPreconfirmadas,
  productos_no_encontrados: productosNoEncontrados,
  chat_id: chatId,
  uid,
  cliente_id: clienteId,
  cliente_datos: clienteDatos
};

// Fusionar: originales válidos + preconfirmados + correcciones del usuario

// 1) Cargar y parsear la memoria de forma robusta
let row = $('Leer Memoria Corrección Pedido').item?.json;
let datos = {};
if (row) {
  // Puede venir como { datos_pendientes: { ... } } o como string JSON
  let dp = row.datos_pendientes ?? row[0]?.datos_pendientes ?? {};
  if (typeof dp === 'string') {
    try { datos = JSON.parse(dp); } catch { datos = {}; }
  } else {
    datos = dp || {};
  }
}

const corregidos = $('Preparar Productos').item?.json?.productos_corregidos || [];
const originales = Array.isArray(datos.productos_originales) ? datos.productos_originales : [];
const ambiguos   = Array.isArray(datos.productos_ambiguos)   ? datos.productos_ambiguos   : [];
const preOk      = Array.isArray(datos.lineas_preconfirmadas)? datos.lineas_preconfirmadas: [];

// 2) Normalizadores y sets de ayuda
const norm = s => (s||'').toString()
  .normalize('NFD').replace(/[\u0300-\u036f]/g,'')
  .toLowerCase().replace(/\s+/g,' ').trim();

const ambiguoSet = new Set(ambiguos.map(a => norm(a.solicitado)));

// 3) Pasar preconfirmadas a formato {nombre, cantidad}
const preOkSolicitados = preOk.map(l => ({
  nombre: l.name,
  cantidad: Number(l.product_uom_qty || 0) || 0,
}));

const preOkSet = new Set(preOkSolicitados.map(p => norm(p.nombre)));

// 4) Filtrar originales válidos (los que NO eran ambiguos) y que no estén ya como preconfirmados
const originalesValidos = originales
  .filter(p => !ambiguoSet.has(norm(p.nombre)))
  .filter(p => !preOkSet.has(norm(p.nombre)));

// 5) Construir mapa final (dedup por nombre normalizado)
const resultMap = new Map();

// primero los preconfirmados (prioridad alta)
for (const p of preOkSolicitados) {
  const k = norm(p.nombre);
  resultMap.set(k, { nombre: p.nombre, cantidad: Number(p.cantidad || 0) || 0 });
}

// luego originales válidos (solo si no existen; si existen, suma)
for (const p of originalesValidos) {
  const k = norm(p.nombre);
  const qty = Number(p.cantidad || 0) || 0;
  if (!resultMap.has(k)) {
    resultMap.set(k, { nombre: p.nombre, cantidad: qty });
  } else {
    const cur = resultMap.get(k);
    cur.cantidad = Number(cur.cantidad || 0) + qty;
    resultMap.set(k, cur);
  }
}

// por último, las correcciones del usuario (sustituyen a ambiguos; si coincide nombre, suma)
for (const p of corregidos) {
  const k = norm(p.nombre);
  const qty = Number(p.cantidad || 0) || 0;
  if (!resultMap.has(k)) {
    resultMap.set(k, { nombre: p.nombre, cantidad: qty });
  } else {
    const cur = resultMap.get(k);
    cur.cantidad = Number(cur.cantidad || 0) + qty;
    // Conservamos el nombre “más reciente” (el de la corrección, normalmente el bueno)
    cur.nombre = p.nombre || cur.nombre;
    resultMap.set(k, cur);
  }
}

// 6) Array final de productos sin duplicados
const productosFinales = Array.from(resultMap.values())
  .filter(p => p && p.nombre && (Number(p.cantidad) || 0) > 0);

// 7) Normalizar chat_id para SQL (lista y escalar)
const chat_id_raw = $('Preparar Productos').item?.json?.chat_id
  ?? $('Router de Acciones FIXED').item?.json?.chat_id;

const arr = Array.isArray(chat_id_raw) ? chat_id_raw : [ chat_id_raw ];
