// RUN ONCE FOR ALL ITEMS — fan-out desde Preparar FSM Inventario (o Preparar FSM)
const S = v => v == null ? '' : String(v);
const getFSMItems = () => {
  const a = $items('Preparar FSM Inventario', 0).map(i => i.json);
  if (a.length) return a;
  return $items('Preparar FSM', 0).map(i => i.json);
