const fsmItems = getFSMItems();

function buildDomain(nameRaw){
  const name = S(nameRaw).trim();
  if (!name) return [['id','=',-1]]; // evita búsquedas vacías
  const tokens = name.toLowerCase().split(/\s+/).filter(Boolean);
  const singular = w => w.endsWith('es') ? w.slice(0,-2) : (w.endsWith('s') ? w.slice(0,-1) : w);
  const tokensSing = tokens.map(singular);
  const andFlat = arr => arr.length<=1 ? arr.slice() : Array(arr.length-1).fill('&').concat(arr);
  const orFlat  = arrs => arrs.length<=1 ? (arrs[0]||[]) : Array(arrs.length-1).fill('|').concat(...arrs);
  const leavesTokens = tokens.map(t => ['name','ilike', t]);
  const leavesSing   = tokensSing.map(t => ['name','ilike', t]);
  const domainFlat = orFlat([
    andFlat(leavesTokens).length ? [ ...andFlat(leavesTokens) ] : [],
    andFlat(leavesSing).length   ? [ ...andFlat(leavesSing) ]   : [],
    [ ['name','ilike', name] ],
    [ ['default_code','ilike', name] ],
    [ ['product_variant_ids.default_code','ilike', name] ],
    [ ['product_variant_ids.barcode','ilike', name] ],
  ].filter(a => a.length));
  return domainFlat.length ? domainFlat : [['id','=',-1]];
}

function buildPayload(fsm){
  const db = S(fsm.db);
  const uid = Number(fsm.uid);
  const admin = fsm.admin_password || 'admin';
  const domain = buildDomain(fsm.product_name);
  return {
    jsonrpc: '2.0',
    method: 'call',
    params: {
      service: 'object',
      method: 'execute_kw',
      args: [
        db || '', Number.isFinite(uid) ? uid : 0, admin,
        'product.template','search_read',
        [ domain ],
        { fields: ['id','name'], limit: 10, context: { lang: 'es_ES' } }
      ]
    },
    id: Math.floor(Math.random()*1e6),
  };
}

// Emitimos un item por cada producto (con passthrough del contexto bueno)
return fsmItems.map(fsm => ({ ...fsm, payload: buildPayload(fsm) }));

// PER-ITEM: por cada resultado de "HTTP Buscar Plantilla" genera su payload a product.product
const S = v => v == null ? '' : String(v);

return items.map((item, i) => {
  // Resultado del HTTP Buscar Plantilla para ESTE item
  const httpRes = item.json;
  const tmplRes = Array.isArray(httpRes.result) ? httpRes.result : [];
  const tmplIds = tmplRes.map(r => Number(r.id)).filter(Number.isFinite);

  // Contexto del FSM alineado por índice
  const fsm = $items('Preparar FSM Inventario', 0)[i]?.json
           || $items('Preparar FSM', 0)[i]?.json
           || {};
  const db    = S(fsm.db);
  const uid   = Number(fsm.uid);
  const admin = fsm.admin_password || 'admin';

  // Dominio: product_tmpl_id = id (o OR en prefija)
  let domain;
  if (tmplIds.length === 0) {
    domain = [['id','=',-1]]; // dominio imposible: no hay plantillas
  } else if (tmplIds.length === 1) {
    domain = [['product_tmpl_id','=', tmplIds[0]]];
  } else {
    let expr = ['product_tmpl_id','=', tmplIds[0]];
    for (let j = 1; j < tmplIds.length; j++) expr = ['|', expr, ['product_tmpl_id','=', tmplIds[j]]];
    domain = [expr];
  }

  const payload = {
    jsonrpc: '2.0',
    method: 'call',
    params: {
      service: 'object',
      method: 'execute_kw',
      args: [
        db || '', Number.isFinite(uid) ? uid : 0, admin,
        'product.product', 'search_read',
        [ domain ],
        { fields: ['id','name','uom_id','default_code','barcode','product_tmpl_id'], limit: 50, context: { lang: 'es_ES' } }
      ]
    },
    id: Math.floor(Math.random() * 1e6),
  };

  // Passthrough del contexto para los siguientes nodos
  return { ...fsm, payload, skip_search: tmplIds.length === 0 };
});

// Soporta: múltiples items de entrada y/o un item con "productos"[]
// Genera SIEMPRE 1 item por producto

const S = (v) => (v == null ? '' : String(v));
const norm = (s) => S(s).normalize('NFD').replace(/[\u0300-\u036f]/g,'').toLowerCase();

// Entradas (uno o varios items)
const inItems = (Array.isArray(items) && items.length) ? items.map(i => i.json) : [$json];

function computeForOne(d0){
  const d = d0 || {};
  const msg = norm(d.mensaje_usuario || d.mensaje_original || d.respuesta_usuario || '');

  // Acción → {consumir|reponer}
  const rawAct = S(d.action);
  const detected = rawAct || (/(consum|gast|salid|descuent|baja)/.test(msg) ? 'consumir' : (/(rep|entrad|abon|repos)/.test(msg) ? 'reponer' : ''));
  const actionNorm = /^(consumir|gastar|salida|consumo|baja)$/i.test(detected) ? 'consumir' : 'reponer';

  // Horas
  let hours = null;
  { const m = msg.match(/(\d+(?:[.,]\d+)?)\s*(?:h|hora|horas)\b/); if (m) hours = parseFloat(m[1].replace(',', '.')); }

  // Obra
  let work_label = '';
  { let m = msg.match(/\bobra(?:\s+de(?:l| la)?)?\s+([a-z0-9\s\-_.]+)/);
    if (m) work_label = m[1].trim();
    if (!work_label) { const m2 = msg.match(/\ben\s+la\s+([a-z0-9\s\-_.]+)/); if (m2) work_label = m2[1].trim(); } }

  // Warehouse hint
  const sw = $('Switch Acciones'); // si existe
  const swJson = (sw && sw.item && sw.item.json) ? sw.item.json : {};
  let force_loc_id = null;
  if (swJson.location_id != null) { const n = Number(swJson.location_id); if (Number.isFinite(n) && n > 0) force_loc_id = n; }

  let warehouse_hint = S(d.warehouse_hint || d.almacen || d['almacén'] || swJson.warehouse_hint || swJson.almacen || swJson.code || swJson.name).trim();
  if (!warehouse_hint) {
    if (/\balmacen\s+(principal|central)\b/.test(msg)) warehouse_hint = 'principal';
    else {
      const rawText = S(d.mensaje_original || d.respuesta_usuario || d.mensaje_usuario || '');
      const codeMatch = rawText.match(/\balmac[eé]n\s+([A-Za-z0-9/_-]{1,15})\b/) ||
                        rawText.match(/\b(?:del|desde|de|en)\s+([A-Za-z0-9/_-]{1,15})\b/) ||
                        rawText.match(/\b(WH[0-9A-Za-z/]*)\b/i);
      if (codeMatch) warehouse_hint = codeMatch[1].trim();
    }
  }
  if (!warehouse_hint) warehouse_hint = 'principal';

  // Productos de este d: (1) usar d.productos[] si existe; (2) si no, usar product_name/cantidad; (3) si no, intentar parseo básico del texto
  let productos = Array.isArray(d.productos) ? d.productos.slice() : [];
  if (!productos.length) {
    const pn = S(d.product_name || d.producto || d.product).trim();
    const qty = Number(d.quantity ?? d.cantidad ?? d.unidades ?? 1) || 1;
    if (pn) productos.push({ nombre: pn, cantidad: qty });
  }
  if (!productos.length && S(d.mensaje_original || d.mensaje_usuario || '').trim()) {
    const raw = S(d.mensaje_original || d.mensaje_usuario || '').trim();
    const partes = raw.split(/\s*(?:,|;|\by\b|\be\b)\s*/i).filter(Boolean);
    for (const parte of partes) {
      let m = parte.match(/^\s*(\d+(?:[.,]\d+)?)\s+(.+?)\s*$/i);
      if (m) { const cantidad = Number(m[1].replace(',', '.')) || 1; const nombre = m[2].replace(/^de\s+/i,'').trim(); if (nombre) productos.push({ nombre, cantidad }); continue; }
      m = parte.match(/\b(?:un|una|1)?\s*(\w+)\s+de\s+(.+)$/i);
      if (m) { const nombre = `${m[1]} ${m[2]}`.trim(); if (nombre) productos.push({ nombre, cantidad: 1 }); continue; }
      const nombreSolo = parte.trim(); if (nombreSolo) productos.push({ nombre: nombreSolo, cantidad: 1 });
    }
  }

  if (!d.base_url || !d.db || !d.uid) throw new Error('Faltan credenciales/base_url/db/uid');
  if (!productos.length) throw new Error('No se recibieron productos');

  // Base común para este item d
  const base = {
    base_url: d.base_url,
    db: d.db,
    uid: d.uid,
    chat_id: d.chat_id,
    has_time: Number.isFinite(hours) && hours > 0,
    hours: Number.isFinite(hours) ? hours : 0,
    work_label,
    action: actionNorm,
    warehouse_hint,
    force_loc_id,
    admin_password: d.admin_password || 'admin',
    user_id: d.user_id,
    user_name: d.user_name,
  };

  // Emitir 1 salida por producto
  return productos.map(p => {
    const nombre = S(p.nombre || p.producto || p.name).trim();
    const cantidad = Number(p.cantidad ?? p.quantity ?? 1) || 1;
    if (!nombre) throw new Error('Producto sin nombre');
    return { ...base, has_stock: true, product_name: nombre, quantity: cantidad };
  });
}

// Aplanar todos los productos de todos los items de entrada
const out = inItems.flatMap(d => computeForOne(d));

// Devolver un ARRAY de objetos (uno por producto)
return out;

// Genera UN payload por item y conserva el contexto bueno (db/uid/etc) desde el FSM por índice
const S = v => v == null ? '' : String(v);

return items.map(({ json }, idx) => {
  // ← Contexto correcto alineado por índice
  const fsm = ($items('Preparar FSM Inventario', 0)[idx]?.json)
           || ($items('Preparar FSM', 0)[idx]?.json)
           || {};
  const base = { ...fsm }; // usamos el FSM como base (no el json del HTTP)

  const db   = S(base.db);
  const uid  = Number(base.uid);
  const admin_password = base.admin_password || 'admin';
  const raw  = S(base.product_name).trim();

  if (!db || !Number.isFinite(uid)) throw new Error(`Faltan db/uid en Payload Buscar Producto [idx ${idx}]`);
  if (!raw) return { ...base, error: true, mensaje_error: 'Nombre de producto vacío' };

  // Leer plantillas emparejadas por índice desde "HTTP Buscar Plantilla"
  let tmplIds = [];
  try {
    const hp = $items('HTTP Buscar Plantilla', 0)[idx]?.json?.result;
    if (Array.isArray(hp) && hp.length) {
      tmplIds = hp.map(r => Number(r.id)).filter(n => Number.isFinite(n));
    }
  } catch (_) {}

  // Helper OR '=' plano
  const buildOrEquals = (field, values) => {
    if (!values.length) return [];
    let expr = [field, '=', values[0]];
    for (let i = 1; i < values.length; i++) expr = ['|', expr, [field, '=', values[i]]];
    return expr;
  };

  // Si hay plantillas → variantes por product_tmpl_id
  if (tmplIds.length) {
    const domain = [ buildOrEquals('product_tmpl_id', tmplIds) ];
    const payload = {
      jsonrpc: '2.0',
      method: 'call',
      params: {
        service: 'object',
        method: 'execute_kw',
        args: [
          db, uid, admin_password,
          'product.product', 'search_read',
          [ domain ],
          { fields: ['id','name','uom_id','default_code','barcode','product_tmpl_id'], limit: 50, context: { lang: 'es_ES' } }
        ],
      },
      id: Math.floor(Math.random()*1e6),
    };
    return { ...base, payload };
  }

  // Si NO hay plantillas → dominio por tokens/ilike
  const term   = raw;
  const tokens = term.toLowerCase().split(/\s+/).filter(Boolean);
  const singular = w => w.endsWith('es') ? w.slice(0,-2) : (w.endsWith('s') ? w.slice(0,-1) : w);
  const tokensSing = tokens.map(singular);

  const buildAndFlat = (conds) => conds.length <= 1 ? conds.slice() : Array(conds.length - 1).fill('&').concat(conds);
  const buildOrFlat  = (ops)   => ops.length <= 1 ? (ops[0] || []) : Array(ops.length - 1).fill('|').concat(...(ops));

  const leavesTokens = tokens.map(t => ['name','ilike', t]);
  const leavesSing   = tokensSing.map(t => ['name','ilike', t]);

  const andTokensFlat = buildAndFlat(leavesTokens);
  const andSingFlat   = buildAndFlat(leavesSing);

  const codeLeaf     = ['default_code','ilike', term];
  const barcodeLeaf  = ['barcode','ilike', term];
  const fullNameLeaf = ['name','ilike', term];

  const domainFlat = buildOrFlat([
    andTokensFlat.length ? [ ...andTokensFlat ] : [],
    andSingFlat.length   ? [ ...andSingFlat ]   : [],
    [ codeLeaf ],
    [ barcodeLeaf ],
    [ fullNameLeaf ],
  ].filter(a => a.length));

  const finalDomain = domainFlat.length ? domainFlat : [['id','=',-1]];

  const payload = {
    jsonrpc: '2.0',
    method: 'call',
    params: {
      service: 'object',
      method: 'execute_kw',
      args: [
        db, uid, admin_password,
        'product.product', 'search_read',
        [ finalDomain ],
        { fields: ['id','name','uom_id','default_code','barcode','product_tmpl_id'], limit: 20, context: { lang: 'es_ES' } }
      ],
    },
    id: Math.floor(Math.random()*1e6),
  };

  return { ...base, payload };
});

// Ejecutar este nodo en "Run once for all items"

// 1) Trae TODOS los items de productos resueltos
const bases = $items('Resolver Coincidencias Producto', 0).map(i => i.json);

// 2) Las ubicaciones suelen venir como 1 solo item por nodo HTTP → toma la primera
const hInt  = $items('HTTP Ubicación Interna', 0)[0]?.json?.result?.[0] || null;
const hSup  = $items('HTTP Ubicación Proveedor', 0)[0]?.json?.result?.[0] || null;
const hPick = $items('HTTP PickingType Entrada', 0)[0]?.json?.result?.[0] || null;
const hWh   = $items('HTTP Buscar Almacén', 0)[0]?.json?.result?.[0] || null;
const hScr  = $items('HTTP Ubicación Scrap', 0)[0]?.json?.result?.[0] || null;

// 3) Calcula la ubicación interna (WH/Stock) priorizando el lot_stock_id del almacén
let locInt = hInt;
if (hWh && Array.isArray(hWh.lot_stock_id)) {
  locInt = { id: hWh.lot_stock_id[0], complete_name: 'Almacén principal' };
}

const locSup   = hSup;
const pickIn   = hPick;
const locScrap = hScr;

// 4) Validación mínima
if (!locInt) throw new Error('No se encontró ubicación interna (WH/Stock)');

// 5) Construye una salida por CADA producto (item) alineando por índice
const out = bases.map((base) => {
  const action = String(base.action || '').toLowerCase();
  const isConsumo = /(consumir|gastar|salida)/.test(action);

  if (!base?.producto?.id) {
    return {
      ...base,
      error: true,
      mensaje_error: 'Producto no resuelto en Resolver Coincidencias Producto',
    };
  }

  if (isConsumo) {
    if (!locScrap) {
      return { ...base, error: true, mensaje_error: 'No se encontró ubicación Scrap (Virtual Locations/Scrap)' };
    }
    const payload = {
      jsonrpc: '2.0',
      method: 'call',
      params: {
        service: 'object',
        method: 'execute_kw',
        args: [
          base.db,
          base.uid,
          base.admin_password || 'admin',
          'stock.scrap',
          'create',
          [[{
            product_id: base.producto.id,
            scrap_qty: base.quantity,
            location_id: locInt.id,        // ORIGEN: WH/Stock
            scrap_location_id: locScrap.id // DESTINO: Virtual Locations/Scrap
          }]]
        ]
      },
      id: Math.floor(Math.random()*1e6)
    };
    return { ...base, locInt, locScrap, payload, modelo: 'stock.scrap' };
  }

  // Reposición (entrada)
  if (!locSup || !pickIn) {
    return { ...base, error: true, mensaje_error: 'No se encontró ubicación proveedor o picking de entrada' };
  }

  const pickingVals = {
    picking_type_id: pickIn.id,
    location_id: locSup.id,
    location_dest_id: locInt.id,
    move_ids_without_package: [
      [0,0,{
        name: base.producto.name,
        product_id: base.producto.id,
        product_uom: Array.isArray(base.producto.uom_id) ? base.producto.uom_id[0] : base.producto.uom_id,
        product_uom_qty: base.quantity,
        location_id: locSup.id,
        location_dest_id: locInt.id
      }]]
  };

  const payload = {
    jsonrpc: '2.0',
    method: 'call',
    params: {
      service: 'object',
      method: 'execute_kw',
      args: [
        base.db,
        base.uid,
        base.admin_password || 'admin',
        'stock.picking',
        'create',
        [[pickingVals]]
      ]
    },
    id: Math.floor(Math.random()*1e6)
  };

  return { ...base, locInt, locSup, pickIn, payload, modelo: 'stock.picking' };
});

// 6) SIEMPRE devolver array (un item por producto)
return out;

// Genera UN payload por item de entrada y conserva el contexto en cada item (passthrough)
const S = v => v == null ? '' : String(v);

return items.map(({ json }) => {
  const fsm = json;                         // item actual de "Preparar FSM"
  const hint = S(fsm.warehouse_hint || 'principal').trim();
  const domain = ['|', ['name','ilike', hint], ['code','ilike', hint]];

  const body = {
    jsonrpc: '2.0',
    method: 'call',
    params: {
      service: 'object',
      method: 'execute_kw',
      args: [
        fsm.db, fsm.uid, (fsm.admin_password || 'admin'),
        'stock.warehouse', 'search_read',
        [ domain ],
        { fields: ['id','name','code','lot_stock_id'], limit: 1 }
      ]
    },
    id: Math.floor(Math.random() * 1e6),
  };

  return { ...fsm, payload: body };        // ⬅️ mantenemos db/uid/product_name/etc.
});

// Toma el item emparejado que salió de "Preparar Validación Picking"
const base = $items('Preparar Validación Picking', 0)[$itemIndex]?.json;
if (!base) throw new Error(`No hay item emparejado desde "Preparar Validación Picking" en índice ${$itemIndex}`);

const validate = base.validate_payload;
if (!validate) throw new Error('No se encontró validate_payload en este item');

return { payload: validate };

// Procesa TODOS los items que llegan del "HTTP Buscar Producto" y alinea por índice
const norm = (s) => (s || '').normalize('NFD').replace(/[\u0300-\u036f]/g, '').toLowerCase();

// Items de entrada (cada uno es la respuesta de Odoo para un producto)
const inItems = $items();  // los items que llegan a este nodo

// Contexto emparejado por índice desde "Payload Buscar Producto"
const ctxItems = $items('Payload Buscar Producto', 0);

// Por cada item de entrada, resolvemos el producto y devolvemos un item de salida
const out = inItems.map((it, i) => {
  const base = ctxItems[i]?.json || {};
  const results = Array.isArray(it.json.result) ? it.json.result : [];
  const buscado = String(base.product_name || '').trim();
  const nb = norm(buscado);

  // Scoring simple
  const scored = results.map(p => {
    const n = norm(p.name);
    let score = 0;
    if (n === nb) score = 100;
    else if (n.startsWith(nb)) score = 70;
    else if (n.includes(nb)) score = 50;
    return { p, score };
  }).sort((a,b) => b.score - a.score);

  const top   = scored.filter(s => s.score > 0).map(s => s.p);
  const exact = scored.find(s => s.score === 100)?.p || null;

  if (exact) {
    return { ...base, producto: exact, coincidencias: [], requiere_correccion: false };
  }
  if (top.length === 1) {
    return { ...base, producto: top[0], coincidencias: [], requiere_correccion: false };
  }

  const sugerencias = results.slice(0, 5).map((p, idx) => `${idx+1}. ${p.name}`);
  const texto = results.length === 0
    ? `❌ No encontré "${buscado}".\nEscribe el nombre correcto del producto.`
    : `🔎 No encontré coincidencia exacta para "${buscado}".\nPosibles:\n${sugerencias.join('\n')}\n\nResponde con el nombre correcto (o copia/pega una sugerencia).`;

  return {
    ...base,
    requiere_correccion: true,
    coincidencias: results.slice(0, 10),
    mensaje_sugerencias: texto,
    producto_buscado: buscado,
  };
});

// SIEMPRE devolver un array (un item por producto)
return out;

// Preparar Memoria FSM Inventario para corrección de producto
// Lee user_name y user_id desde 'Preparar FSM Inventario'
const src = $('Preparar FSM Inventario').first()?.json || {};
const d = { ...src, ...$json };

const esperando_confirmacion = 'fsm_inventario_correccion_producto';
const ultimo_mensaje_bot = d.mensaje_sugerencias || 'Indica el nombre correcto del producto.';

// Compactar coincidencias a nombres
const coincidenciasNombres = Array.isArray(d.coincidencias)
  ? d.coincidencias.map(c => (typeof c === 'string' ? c : (c?.name || ''))).filter(Boolean)
  : [];

const datos_pendientes = {
  contexto: 'fsm_inventario',
  action: d.action || 'reponer',
  warehouse_hint: d.warehouse_hint || 'principal',
  producto_buscado: d.producto_buscado || d.product_name || '',
  coincidencias: coincidenciasNombres.slice(0, 10),
  base_url: d.base_url,
  db: d.db,
  uid: d.uid,
  admin_password: d.admin_password || 'admin',
  quantity: Number(d.quantity || 1) || 1,
  timestamp: new Date().toISOString(),
};

return {
  chat_id: Number(d.chat_id) || 0,
  user_id: Number(d.user_id || src.user_id) || 0,
  user_name: (d.user_name || src.user_name || 'Usuario'),
  esperando_confirmacion,
  ultimo_mensaje_bot,
  datos_pendientes,
};

