// Si venimos de buscar proyecto
const datosNorm = $('Normalizar Parte+Materiales FSM').item.json;

let proyecto = null;

// ✅ LEER del HTTP real, no del payload
const httpProj = $('HTTP Buscar Proyecto FSM').item?.json;
if (httpProj && Array.isArray(httpProj.result) && httpProj.result[0]) {
  proyecto = httpProj.result[0];
}

// O bien (opcional) si se creó en otro flujo de confirmación
if (!proyecto) {
  const created = $('Payload Crear Proyecto FSM')?.tryjson;
  if (created && Number.isFinite(created.result)) {
    proyecto = { id: created.result, name: datosNorm.proyecto, is_fsm: true };
  }
}

if (!proyecto) {
  return { error: true, mensaje: 'No hay proyecto FSM', datos_verificacion: datosNorm };
}

// Fallbacks robustos para DB y UID
const DB  = datosNorm.db || 'lsanchezgoshawk-bot-main-27977821';
const UID = datosNorm.uid || ($('Autenticar Odoo')?.item?.json?.result);
if (!UID) throw new Error('Falta uid (Autenticar Odoo)');

// Normalizar nombre de tarea para búsqueda más flexible
const nombreTarea = (datosNorm.tarea || '').trim();

console.log('🔍 Buscando tarea FSM en TODAS las compañías:');
console.log('  Nombre a buscar:', nombreTarea);
console.log('  Proyecto ID:', proyecto.id);
console.log('  Proyecto:', proyecto.name);

// Buscar tarea por nombre dentro del proyecto FSM EN TODAS LAS COMPAÑÍAS
const body = {
  jsonrpc: '2.0',
  method: 'call',
  params: {
    service: 'object',
    method: 'execute_kw',
    args: [
      DB, UID, 'admin',
      'project.task', 'search_read',
      [[
        ['name', 'ilike', nombreTarea],
        ['project_id', '=', proyecto.id],
        '|',
        ['company_id', '=', false],  // Sin compañía
        ['company_id', '!=', false]  // O cualquier compañía
      ]],
      { 
        fields: ['id', 'name', 'project_id', 'company_id'], 
        limit: 1,
        context: { 'allowed_company_ids': [] }  // Buscar en todas las compañías
      },
    ],
  },
  id: Math.floor(Math.random() * 1e6),
};

console.log('📋 Buscando tarea en todas las compañías');
console.log('📋 Dominio de búsqueda:', JSON.stringify(body.params.args[4]));

return {
  payload: body,
  proyecto,
  datos_verificacion: {
    ...datosNorm,
