// Trae datos del item anterior
const ctx = $json;

// Fallbacks robustos
const DB  = ctx.db  || 'lsanchezgoshawk-bot-main-27977821';
const UID = ctx.uid || ($('Autenticar Odoo')?.item?.json?.result);

if (!DB)  throw new Error('DB no definida');
if (!UID) throw new Error('UID no definido');

// Nombre a buscar (por defecto "Servicio de campo")
const nombreProyecto = (ctx.proyecto || 'Servicio de campo').trim();

console.log('🔍 Buscando proyecto FSM en TODAS las compañías:', nombreProyecto);

const body = {
  jsonrpc: '2.0',
  method: 'call',
  params: {
    service: 'object',
    method: 'execute_kw',
    args: [
      DB, UID, 'admin',
      'project.project', 'search_read',
      [[ 
        ['is_fsm', '=', true], 
        ['name', 'ilike', nombreProyecto],
        '|',
        ['company_id', '=', false],  // Sin compañía
        ['company_id', '!=', false]  // O cualquier compañía
      ]],
      { 
        fields: ['id','name','is_fsm','company_id'], 
        limit: 1,
        context: { 'allowed_company_ids': [] }  // Buscar en todas las compañías
      },
    ],
  },
  id: Math.floor(Math.random()*1e6),
};

console.log('📋 Buscando en todas las compañías');

// Devuelve el payload + los datos ya normalizados con los fallbacks
return { payload: body, datos: { ...ctx, db: DB, uid: UID } };

// 1) Lee SIEMPRE del nodo "Normalizar Parte+Materiales FSM"
//
const norm =
  $('Normalizar Parte+Materiales FSM')?.first()?.json ||
  $('Normalizar Parte+Materiales FSM')?.item?.json ||
  {};

const db  = String(norm.db || 'lsanchezgoshawk-bot-main-27977821');
const uid = Number(norm.uid);
const clienteNombre = String(norm.cliente || '').trim();

//
// 2) Saca el task_id del item actual (HTTP Buscar Tarea FSM)
//
const http = $json;
const tareas = http.json?.result ?? http.result ?? http.body?.result ?? [];
const tarea = Array.isArray(tareas) && tareas[0] ? tareas[0] : null;
const taskId = Number(tarea?.id);

//
// 3) Si falta algo -> NO dispares el HTTP siguiente
//
if (!uid || !taskId || !clienteNombre) {
  return {
    skip_partner_lookup: true,
    motivo: `faltan_datos uid:${!!uid}, taskId:${!!taskId}, cliente:${!!clienteNombre}`,
    db, uid, task_id: taskId, cliente: clienteNombre,
  };
}

//
// 4) Payload para buscar el partner por nombre (res.partner)
//   - Usamos ilike + limit:1
//
const payload = {
  jsonrpc: '2.0',
  method: 'call',
  params: {
    service: 'object',
    method: 'execute_kw',
    args: [
      db, uid, 'admin',
      'res.partner', 'search_read',
      [[ ['name', 'ilike', clienteNombre] ]],
      { fields: ['id','name'], limit: 1 },
    ],
  },
  id: Math.floor(Math.random() * 1e6),
};

return {
  payload,
  db,
  uid,
  task_id: taskId,
  cliente: clienteNombre,
  skip_partner_lookup: false,
};

const ref = $json.datos_verificacion || $json;
const chatId = Number(ref.chat_id || 0);
const ctxKey = `memoria_${chatId}`;

let memoria = global[ctxKey] || {};
memoria.esperando_confirmacion = 'solicitar_cliente_fsm';
memoria.esperando_respuesta = true;
memoria.datos_pendientes = {
  accion_original: 'registrar_parte_y_stock',
