// PREPARAR BÚSQUEDA PRODUCTOS FSM (basado en la lógica de ventas)
const datos = $('Normalizar Parte+Materiales FSM').item.json;
const uid = datos.uid;
const productos = Array.isArray(datos.productos) ? datos.productos : [];

const STOP = new Set(['con','de','y','para','en','la','el','los','las','por','del','a','al']);

// Normalización ligera pensada para Odoo (NO tocamos ll/y ni h aquí)
const normalize = (s) =>
  (s || '')
    .toString()
    .normalize('NFD').replace(/[\u0300-\u036f]/g,'') // sin acentos
    .toLowerCase()
    .replace(/[^a-z0-9%\. ]+/g,' ')
    .replace(/\s+/g,' ')
    .trim();

// genera variantes útiles: singular/plural, número+unidad y variantes "fuzzy" (h, ll/y, prefijos/sufijos)
function variants(tok) {
  const out = new Set();
  const vowels = 'aeiou';

  if (!tok) return [];

  // token base
  out.add(tok);

  // singularización sencilla
  if (tok.endsWith('es')) out.add(tok.slice(0, -2));
  if (tok.endsWith('s'))  out.add(tok.slice(0, -1));

  // número+unidad: 5kg -> 5 , kg , 5kg
  const m = tok.match(/^(\d+(?:\.\d+)?)(kg|g|mg|l|ml|cm|mm|m|u|uds?)$/);
  if (m) {
    out.add(m[1]); // número
    out.add(m[2]); // unidad
  }

  // --- Prefijos y sufijos para tolerar faltas de ortografía ---
  // Ej.: torniyo -> "torni" (match con "tornillos"), "orño"/"iyo" etc.
  if (tok.length >= 4) {
    // prefijos
    out.add(tok.slice(0, 4));          // primeros 4
    if (tok.length >= 5) out.add(tok.slice(0, 5)); // primeros 5
    // sufijos
    out.add(tok.slice(-4));            // últimos 4
    if (tok.length >= 5) out.add(tok.slice(-5));   // últimos 5
  }

  // --- Varianten con/ sin h inicial ---
  // orno -> horno  (falta h)
  if (vowels.includes(tok[0])) {
    out.add('h' + tok);
  }
  // horno -> orno  (h de más)
  if (tok.startsWith('h') && tok.length > 1 && vowels.includes(tok[1])) {
    out.add(tok.slice(1));
  }

  // --- Variantes típicas ll / y ---
  // yave -> llave
  if (tok.startsWith('y') && tok.length > 1 && vowels.includes(tok[1])) {
    out.add('ll' + tok.slice(1));
  }

  // torniyo -> tornillo (yo final -> llo)
  if (tok.endsWith('yo') && tok.length > 2) {
    out.add(tok.slice(0, -2) + 'llo');
  }

  return [...out];
}

function tokens(nombre) {
  const base = normalize(nombre);
  if (!base) return [];
  const raw = base.split(' ').filter(t => t && !STOP.has(t));
  const uniq = new Set();
  for (const tk of raw) {
    for (const v of variants(tk)) {
      if (v.length >= 2) {
        uniq.add(v);
      }
    }
  }
  return [...uniq];
}

// 1) Construir hojas (leaves) para OR global: name ilike tk  OR  default_code ilike tk
let leaves = [];
for (const p of productos) {
  if (!p || typeof p.nombre !== 'string') continue;
  const tks = tokens(p.nombre);
  for (const tk of tks) {
    leaves.push(['name', 'ilike', tk]);
    leaves.push(['default_code', 'ilike', tk]);
  }
}

// deduplicar leaves
const seen = new Set();
leaves = leaves.filter(l => {
  const k = JSON.stringify(l);
  if (seen.has(k)) return false;
  seen.add(k);
  return true;
});

let domain;

if (leaves.length === 0) {
  // sin términos -> dominio imposible (evita resultados)
  domain = [['id', '=', -1]];
} else {
  // OR de todas las hojas:  [ '|', '|', ..., hoja1, hoja2, ..., hojaN ]
  const orTokens = (leaves.length === 1)
    ? [leaves[0]]
    : [...Array(leaves.length - 1).fill('|'), ...leaves];

  // AND con sale_ok:  ['&', ['sale_ok','=',true],  ...orTokens ]
  domain = ['&', ['sale_ok', '=', true], ...orTokens];
}

console.log('🔍 Dominio construido (plano):', JSON.stringify(domain));

// 2) Payload a Odoo
const payloadId = Math.floor(Math.random() * 1e6);
const jsonRpcPayload = {
  jsonrpc: '2.0',
  method: 'call',
  params: {
    service: 'object',
    method: 'execute_kw',
    args: [
      'lsanchezgoshawk-bot-main-27977821',
      uid,
      'admin',
      'product.product',
      'search_read',
      [domain],
      {
        fields: ['id', 'name', 'list_price', 'uom_id', 'default_code'],
        limit: 300
      }
    ]
  },
  id: payloadId
};

return {
  payload: jsonRpcPayload,
  productos_solicitados: productos,
  ...datos
};

// VALIDAR PRODUCTOS FSM (matching fuzzy entre productos solicitados y encontrados)

const httpResult = $json.result;
const datosBusqueda = $('Preparar Búsqueda Productos FSM').item?.json || {};
const productosSolicitados = Array.isArray(datosBusqueda.productos_solicitados) ? datosBusqueda.productos_solicitados : [];
const chatId = datosBusqueda.chat_id;
const uid = datosBusqueda.uid;

const pool = Array.isArray(httpResult) ? httpResult : [];

// Palabras vacías
const STOP = new Set(['con','de','y','para','en','la','el','los','las','por','del','a','al']);

// 🔤 Normalización básica + ortográfica/fonética en español
const normalize = (s) => {
  let out = (s || '')
    .toString()
    .normalize('NFD').replace(/[\u0300-\u036f]/g,'') // quitar acentos
    .toLowerCase()
    .replace(/\s+/g,' ')
    .trim();

  // Normalización fonética / ortográfica típica en español
  out = out
    .replace(/h/g, '')          // quitar h mudas (hacha ↔ acha)
    .replace(/ll/g, 'y')        // ll ↔ y (tornillo ↔ torniyo)
    .replace(/v/g, 'b')         // v ↔ b (tubo ↔ tuvo)
    .replace(/qu/g, 'k')        // qu ↔ k (quimico ↔ kimico)
    .replace(/c(?=[ei])/g, 's') // ce/ci ↔ se/si (cemento ↔ semento)
    .replace(/z/g, 's')         // z ↔ s (plaza ↔ plasa)
    .replace(/ge/g, 'je')       // ge/gi ↔ je/ji
    .replace(/gi/g, 'ji');

  return out;
};

function tokenVariants(tok) {
  const out = new Set([tok]);
  if (tok.endsWith('es')) out.add(tok.slice(0, -2));
  if (tok.endsWith('s'))  out.add(tok.slice(0, -1));

  // separar unidades tipo 5kg, 3m, etc.
  const m = tok.match(/^(\d+(?:\.\d+)?)(kg|g|mg|l|ml|cm|mm|m|u|uds?)$/);
  if (m) { out.add(m[1]); out.add(m[2]); }

  return [...out];
}

function tokens(s) {
  const base = normalize(s);
  if (!base) return [];
  const raw = base.split(/[ \-_/]+/).filter(t => t && !STOP.has(t));
  const uniq = new Set();
  for (const tk of raw) {
    for (const v of tokenVariants(tk)) {
      if (v.length >= 2 || /^\d+(?:\.\d+)?$/.test(v)) {
        uniq.add(v);
      }
    }
  }
  return [...uniq];
}

function levenshtein(a, b) {
  a = normalize(a); 
  b = normalize(b);
  const m = a.length, n = b.length;
  if (m === 0) return n;
  if (n === 0) return m;
  const dp = Array.from({ length: m + 1 }, () => Array(n + 1).fill(0));
  for (let i = 0; i <= m; i++) dp[i][0] = i;
  for (let j = 0; j <= n; j++) dp[0][j] = j;
  for (let i = 1; i <= m; i++) {
    for (let j = 1; j <= n; j++) {
      const cost = a[i - 1] === b[j - 1] ? 0 : 1;
      dp[i][j] = Math.min(
        dp[i - 1][j] + 1,
        dp[i][j - 1] + 1,
        dp[i - 1][j - 1] + cost
      );
    }
  }
  return dp[m][n];
}

function jaccardToken(a, b) {
  const A = new Set(tokens(a));
  const B = new Set(tokens(b));
  const inter = [...A].filter(x => B.has(x)).length;
  const uni = new Set([...A, ...B]).size;
  return uni === 0 ? 0 : inter / uni;
}

function similarity(a, b) {
  const la = normalize(a), lb = normalize(b);
  if (!la || !lb) return 0;
  if (la === lb) return 1;

  const maxLen = Math.max(la.length, lb.length);
  const ld = levenshtein(la, lb);
  const levScore = 1 - (ld / Math.max(1, maxLen));
  const jac = jaccardToken(a, b);

  // combinamos Levenshtein (pesado) + Jaccard de tokens
  return (levScore * 0.7) + (jac * 0.3);
}

// Umbrales
const THRESHOLD_HIGH = 0.88;  // match muy seguro → se valida directo
const MIN_SUGGESTION = 0.35;  // por debajo de esto no mostramos ni sugerencias

// Índice rápido por nombre normalizado
const byName = new Map(pool.map(p => [normalize(p.name), p]));

const productosValidados = [];
const productosAmbiguos = [];

for (const sol of productosSolicitados) {
  const nombreSolicitado = (sol?.nombre || '').toString();
  const cant = Number(sol?.cantidad || 1);
  if (!nombreSolicitado) continue;

  const qNorm = normalize(nombreSolicitado);
  const qTokens = new Set(tokens(nombreSolicitado));

  // 1) Exacto por nombre normalizado (ya con corrección ortográfica)
  if (byName.has(qNorm)) {
    const p = byName.get(qNorm);
    productosValidados.push({
      product_id: p.id,
      product_name: p.name,
      quantity: cant,
      price_unit: p.list_price
    });
    continue;
  }

  // 2) Prefiltro: solo candidatos que compartan tokens o tengan similitud suficiente
  const candidates = [];
  for (const p of pool) {
    const cName = p.name || '';
    const cTokens = new Set(tokens(cName));
    const overlap = [...qTokens].some(t => cTokens.has(t));
    const score = similarity(nombreSolicitado, cName);

    if (overlap || score >= MIN_SUGGESTION) {
      candidates.push({
        id: p.id,
        name: cName,
        price: p.list_price,
        score
      });
    }
  }

  candidates.sort((a, b) => b.score - a.score);

  const best = candidates[0];
  const top = candidates.slice(0, 7);

  if (best && best.score >= THRESHOLD_HIGH) {
    // Match muy claro → lo damos por bueno
    productosValidados.push({
      product_id: best.id,
      product_name: best.name,
      quantity: cant,
      price_unit: best.price
    });
  } else if (top.length > 0) {
    // Ambiguo → pedimos corrección al usuario
    productosAmbiguos.push({
      solicitado: nombreSolicitado,
      cantidad: cant,
      candidatos: top.map(x => ({
        id: x.id,
        name: x.name,
        price: x.price,
        score: Number(x.score.toFixed(2))
      }))
    });
  } else {
    // Nada parecido → sin sugerencias
    productosAmbiguos.push({
      solicitado: nombreSolicitado,
      cantidad: cant,
      candidatos: []
    });
  }
}

// Si hay ambigüedad → mensaje para corregir
if (productosAmbiguos.length > 0) {
  const instrucciones = [
    '🔎 Hemos detectado algunos **productos con errores** en la descripción.',
    '',
    'Por favor, envía **TODAS** las correcciones en **un solo** mensaje con el formato:',
    '`Nombre correcto x cantidad`,  `Nombre correcto x cantidad`... .',
    ''
  ];

  for (const amb of productosAmbiguos) {
    const sug = amb.candidatos.length
      ? amb.candidatos.map((c, i) => `${i + 1}. ${c.name}`).join('\n')
      : '— Sin sugerencias —';

    instrucciones.push(`Productos con errores (como lo has escrito): **${amb.solicitado}** x ${amb.cantidad}`);
    instrucciones.push('Sugerencias:');
    instrucciones.push(sug);
    instrucciones.push('');
  }

  instrucciones.push('Ejemplo: `Tornillo M6 x 12, Cable UTP Cat6 5m x 5`.');

  return {
    requiere_correccion: true,
    texto_correccion: instrucciones.join('\n'),
    productos_ambiguos: productosAmbiguos,
    productos_validados: productosValidados,
    ...datosBusqueda
  };
}

// Sin ambigüedad → seguir flujo
return {
  requiere_correccion: false,
  productos_validados: productosValidados,
  ...datosBusqueda
};

// Preparar memoria para corrección de productos FSM
const d = $('Validar Productos FSM').item.json;

return {
  chat_id: Number(d.chat_id) || 0,
  esperando_confirmacion: 'corregir_productos_fsm',
  ultimo_mensaje_bot: d.texto_correccion || 'Indica los productos correctos en un solo mensaje (nombre x cantidad, separados por comas).',
  datos_pendientes: {
    accion_original: 'registrar_parte_y_stock',
    proyecto: d.proyecto,
