const STOP = new Set(['con','de','y','para','en','la','el','los','las','por','del','a','al']);
const normalize = (s) =>
  (s || '').toString()
    .normalize('NFD').replace(/[\u0300-\u036f]/g,'')
    .toLowerCase()
    .replace(/[^a-z0-9%\. ]+/g,' ')
    .replace(/\s+/g,' ')
    .trim();

function variants(tok) {
  const out = new Set([tok]);
  if (tok.endsWith('es')) out.add(tok.slice(0, -2));
  if (tok.endsWith('s'))  out.add(tok.slice(0, -1));
  const m = tok.match(/^(\d+(?:\.\d+)?)(kg|g|mg|l|ml|cm|mm|m|u|uds?)$/);
  if (m) { out.add(m[1]); out.add(m[2]); }
  return [...out];
}

function tokens(nombre) {
  const base = normalize(nombre);
  if (!base) return [];
  const raw = base.split(' ').filter(t => t && !STOP.has(t));
  const uniq = new Set();
  for (const tk of raw) for (const v of variants(tk)) if (v.length >= 2) uniq.add(v);
  return [...uniq];
}

// Hojas OR para todos los productos solicitados
let leaves = [];
for (const p of productos) {
  const nombre = String(p?.nombre || p?.name || '').trim();
  const codigo = String(p?.codigo || p?.default_code || '').trim();
  if (codigo) {
    leaves.push(['default_code', '=', codigo]);
  }
  for (const tk of tokens(nombre)) {
    leaves.push(['name', 'ilike', tk]);
    leaves.push(['default_code', 'ilike', tk]);
  }
}

// dedupe
const seen = new Set();
leaves = leaves.filter(l => { const k = JSON.stringify(l); if (seen.has(k)) return false; seen.add(k); return true; });

let domain;
if (!leaves.length) {
  domain = [['id', '=', -1]]; // nada que buscar
} else {
  const orTokens = (leaves.length === 1) ? [leaves[0]] : [...Array(leaves.length - 1).fill('|'), ...leaves];
  domain = ['&', ['sale_ok', '=', true], ...orTokens];
}

const payload = {
