// Empareja productos solicitados con resultados de Odoo y devuelve product_id
const httpRes = $json.result || $json.body?.result || [];
const datos   = $('FSM ▸ Preparar Buscar Productos')?.first()?.json || {};
const solicitados = Array.isArray(datos.productos_solicitados) ? datos.productos_solicitados : [];
