const STOP = new Set(['con','de','y','para','en','la','el','los','las','por','del','a','al']);
const normalize = (s) =>
  (s || '').toString()
    .normalize('NFD').replace(/[\u0300-\u036f]/g,'')
    .replace(/\s+/g,' ')
    .toLowerCase()
    .trim();

function tokenVariants(tok) {
  const out = new Set([tok]);
  if (tok.endsWith('es')) out.add(tok.slice(0, -2));
  if (tok.endsWith('s'))  out.add(tok.slice(0, -1));
  const m = tok.match(/^(\d+(?:\.\d+)?)(kg|g|mg|l|ml|cm|mm|m|u|uds?)$/);
  if (m) { out.add(m[1]); out.add(m[2]); }
  return [...out];
}
function tokens(s) {
  const base = normalize(s);
  if (!base) return [];
  const raw = base.split(/[ \-_/]+/).filter(t => t && !STOP.has(t));
  const uniq = new Set();
  for (const tk of raw) for (const v of tokenVariants(tk)) if (v.length >= 2 || /^\d+(\.\d+)?$/.test(v)) uniq.add(v);
  return [...uniq];
}
function levenshtein(a,b){
  a=normalize(a); b=normalize(b);
  const m=a.length,n=b.length; if(!m) return n; if(!n) return m;
  const dp=Array.from({length:m+1},()=>Array(n+1).fill(0));
  for(let i=0;i<=m;i++) dp[i][0]=i;
  for(let j=0;j<=n;j++) dp[0][j]=j;
  for(let i=1;i<=m;i++) for(let j=1;j<=n;j++){
    const cost=a[i-1]===b[j-1]?0:1;
    dp[i][j]=Math.min(dp[i-1][j]+1, dp[i][j-1]+1, dp[i-1][j-1]+cost);
  }
  return dp[m][n];
}
function jaccardToken(a,b){
  const A=new Set(tokens(a)), B=new Set(tokens(b));
  const inter=[...A].filter(x=>B.has(x)).length;
  const uni=new Set([...A,...B]).size;
  return uni===0?0:inter/uni;
}
function sim(a,b){
  const la=normalize(a), lb=normalize(b);
  if(!la||!lb) return 0;
  if(la===lb)  return 1;
  const maxLen=Math.max(la.length,lb.length);
  const ld=levenshtein(la,lb);
  const levScore=1-(ld/Math.max(1,maxLen));
  const jac=jaccardToken(a,b);
  return (levScore*0.7)+(jac*0.3);
}

const THRESH=0.88, MIN_SUG=0.35;
const pool = Array.isArray(httpRes)? httpRes : [];
const byExactCode = new Map(pool.filter(p=>p.default_code).map(p=>[String(p.default_code).trim(), p]));
const out = [];
const ambiguos = [];

for (const s of solicitados) {
  const nombre = String(s?.nombre || s?.name || '').trim();
  const codigo = String(s?.codigo || s?.default_code || '').trim();
  const qty = Number(s?.cantidad || s?.qty || s?.quantity || 1) || 1;

  // 1) match exacto por código
  if (codigo && byExactCode.has(codigo)) {
    const p = byExactCode.get(codigo);
    out.push({ product_id: p.id, product_name: p.name, quantity: qty });
    continue;
  }

  // 2) mejor candidato por similitud de nombre
  let best = null;
  for (const p of pool) {
    const score = sim(nombre, p.name || '');
    if (!best || score > best.score) best = { p, score };
  }
  if (best && best.score >= THRESH) {
    out.push({ product_id: best.p.id, product_name: best.p.name, quantity: qty });
  } else {
    // guarda sugerencias (opcional)
    const ranked = pool
      .map(p => ({ id:p.id, name:p.name, score: Number(sim(nombre, p.name||'').toFixed(2)) }))
      .filter(x => x.score >= MIN_SUG)
      .sort((a,b)=>b.score-a.score)
      .slice(0,7);
    ambiguos.push({ solicitado: nombre, cantidad: qty, candidatos: ranked });
  }
}

const requiere_correccion = ambiguos.length > 0;
let texto_correccion = '';
if (requiere_correccion) {
