  ];
  for (const amb of ambiguos) {
    lines.push(`• **${amb.solicitado}** x ${amb.cantidad}`);
    const sug = amb.candidatos.length ? amb.candidatos.map((c,i)=>`${i+1}. ${c.name} (${c.id})`).join('\n') : '— Sin sugerencias —';
    lines.push(`  Sugerencias:\n${sug}\n`);
  }
  texto_correccion = lines.join('\n');
}

