const uid = datosAnteriores.uid || 2;

if (!resId) {
  throw new Error('No hay ID de registro (leave_id/gasto_id) para adjuntar.');
}

// 1) Intento: binario en el item actual
let bin = null;
let binKeys = Object.keys($binary || {});
if (binKeys.length) {
  bin = $binary[binKeys[0]];
}

// 2) Fallback: leer binario del nodo "Descargar Imagen HTTP"
if (!bin || !bin.data) {
  const httpItems = $items('Descargar Imagen HTTP Ausencia', 0, 0) || [];
  if (httpItems.length && httpItems[0].binary) {
    const httpBinKeys = Object.keys(httpItems[0].binary);
    if (httpBinKeys.length) {
      bin = httpItems[0].binary[httpBinKeys[0]];
    }
  }
}

// 3) Si aún no hay binario, error con debug
if (!bin || !bin.data) {
  const debug = {
    tieneBinaryActual: !!$binary,
    keysBinaryActual: Object.keys($binary || {}),
    tieneItemsHTTP: ($items('Descargar Imagen HTTP Ausencia', 0, 0) || []).length,
    keysBinaryHTTP: (() => {
      const it = $items('Descargar Imagen HTTP Ausencia', 0, 0) || [];
      return it.length && it[0].binary ? Object.keys(it[0].binary) : [];
    })(),
    download_url: datosAnteriores.download_url || null,
  };
  throw new Error('No se encontraron datos binarios del archivo descargado | DEBUG: ' + JSON.stringify(debug));
}

// 4) Convertir base64 → Buffer
const archivoBinario = Buffer.from(bin.data, 'base64');

// 5) Tipo/extension
let mimetype = bin.mimeType || 'image/jpeg';
let extension = bin.fileExtension || null;
if (!extension) {
  const sig = archivoBinario.slice(0, 8).toString('hex').toUpperCase();
  if (sig.startsWith('89504E47')) { mimetype = 'image/png'; extension = 'png'; }
  else if (sig.startsWith('FFD8FF')) { mimetype = 'image/jpeg'; extension = 'jpg'; }
  else if (sig.startsWith('47494638')) { mimetype = 'image/gif'; extension = 'gif'; }
  else if (sig.startsWith('52494646')) { mimetype = 'image/webp'; extension = 'webp'; }
  else { extension = mimetype.includes('png') ? 'png' : 'jpg'; }
}

// 6) Preparar adjunto para Odoo
