function score(name) {
  const n = (name || '').toLowerCase();
  let s = 0;
  for (const c of candidatos) if ((c||'') && n.includes(c.toLowerCase())) s += 2;
  if (n.includes('sick') || n.includes('medical') || n.includes('méd')) s += 1;
  if (n.includes('vac')) s += 1;
  if (n.includes('permiso') || n.includes('leave')) s += 1;
  return s;
}

