if (!extension) {
  const firstBytes = archivoBinario.slice(0, 8);
  const signature = firstBytes.toString('hex').toUpperCase();

  if (signature.startsWith('89504E47')) {
    mimetype = 'image/png';
    extension = 'png';
  } else if (signature.startsWith('FFD8FF')) {
    mimetype = 'image/jpeg';
    extension = 'jpg';
  } else if (signature.startsWith('47494638')) {
    mimetype = 'image/gif';
    extension = 'gif';
  } else if (signature.startsWith('52494646')) {
    mimetype = 'image/webp';
    extension = 'webp';
  } else {
    if (mimetype.includes('png')) extension = 'png';
    else if (mimetype.includes('webp')) extension = 'webp';
    else if (mimetype.includes('gif')) extension = 'gif';
    else extension = 'jpg';
