const normalize = (s) => {
  if (!s) return s;
  return s.normalize('NFD').replace(/\p{Diacritic}/gu, '').toLowerCase().trim().replace(/\s+/g, ' ');
};

const input = items[0].json || {};
let producto = input.producto || (input.datos_pendientes && input.datos_pendientes.producto) || null;
let cantidad = input.cantidad || (input.datos_pendientes && input.datos_pendientes.cantidad) || null;
