let almacen_destino = input.almacen_destino || (input.datos_pendientes && input.datos_pendientes.almacen_destino) || null;

const mensaje = input.mensaje_usuario || '';
if ((!producto || !cantidad) && mensaje) {
  // Patrón: "pasa 5 tornillos del almacen WH al almacen AA"
  const m1 = mensaje.match(/(?:pasa|pasar|transferir|mueve|envia|envía)\s+([0-9]+(?:[.,][0-9]+)?)\s+([^,\n]+?)\s+(?:del|desde|de)\s+almac[ée]n?\s+([A-Za-z0-9_-]+)\s+(?:al|a|hacia)\s+(?:almac[ée]n\s+)?([A-Za-z0-9_-]+)/i);
  if (m1) {
    cantidad = cantidad || parseFloat(m1[1].replace(',', '.'));
    producto = producto || m1[2].trim();
    almacen_origen = almacen_origen || m1[3].trim();
    almacen_destino = almacen_destino || m1[4].trim();
  }
}

producto = producto ? producto.toString().trim() : null;
const producto_norm = producto ? normalize(producto) : null;
const almacen_origen_norm = almacen_origen ? almacen_origen.toString().trim() : null;
const almacen_destino_norm = almacen_destino ? almacen_destino.toString().trim() : null;

// Mapeo de alias
const alias_almacenes = {
  'principal': 'WH',
  'secundario': 'AA',
  'almacen principal': 'WH',
  'almacen secundario': 'AA'
};
const mapAlias = (v) => {
  if (!v) return v;
  const low = v.toString().toLowerCase();
  return alias_almacenes[low] || v;
};

const out = {
  accion: input.accion || 'transferir_stock_almacen',
  producto: producto_norm || null,
  producto_raw: producto || null,
  cantidad: cantidad != null ? cantidad : null,
  unidad: unidad || null,
  almacen_origen: mapAlias(almacen_origen_norm) || null,
  almacen_destino: mapAlias(almacen_destino_norm) || null,
  datos_pendientes: input.datos_pendientes || null,
  mensaje_usuario: mensaje || null,
