  uid: input.uid
};

return [{ json: out }];

// Validar Completitud - Function
const item = items[0].json;
const faltantes = [];
if (!item.producto) faltantes.push('producto');
if (!item.cantidad && item.cantidad !== 0) faltantes.push('cantidad');
if (!item.almacen_origen) faltantes.push('almacen_origen');
if (!item.almacen_destino) faltantes.push('almacen_destino');

if (faltantes.length > 0) {
  return [{ json: {
    accion: 'transferir_stock_almacen_incompleto',
    producto: item.producto || item.producto_raw || null,
    cantidad: item.cantidad || null,
    almacen_origen: item.almacen_origen || null,
    almacen_destino: item.almacen_destino || null,
    datos_faltantes: faltantes,
    respuesta_usuario: `📦 Me faltan estos datos para la transferencia: ${faltantes.join(', ')}.`,
    chat_id: item.chat_id,
    uid: item.uid
  }}];
}

// Todo OK
return [{ json: Object.assign({}, item, { accion: 'transferir_stock_almacen' }) }];

