// Preparar Datos Transferencia - Function
const normalize = (s) => { if (!s) return s; return s.normalize('NFD').replace(/\p{Diacritic}/gu, '').toLowerCase().trim().replace(/\s+/g,' '); };
const input = items[0].json || {};
let producto = input.producto || (input.datos_pendientes && input.datos_pendientes.producto) || null;
let cantidad = input.cantidad || (input.datos_pendientes && input.datos_pendientes.cantidad) || null;
let almacen_origen = input.almacen_origen || (input.datos_pendientes && input.datos_pendientes.almacen_origen) || null;
let almacen_destino = input.almacen_destino || (input.datos_pendientes && input.datos_pendientes.almacen_destino) || null;
const mensaje = input.mensaje_usuario || '';
if((!producto || !cantidad) && mensaje){
  const m1 = mensaje.match(/(?:pasa|pasar|transferir|mueve|envia|env\u00eda)\s+([0-9]+(?:[.,][0-9]+)?)\s+([^,\n]+?)\s+(?:del|desde|de)\s+almac[ee]n?\s+([A-Za-z0-9_-]+)\s+(?:al|a|hacia)\s+(?:almac[ee]n\s+)?([A-Za-z0-9_-]+)/i);
  if(m1){ cantidad = cantidad || parseFloat(m1[1].replace(',','.')); producto = producto || m1[2].trim(); almacen_origen = almacen_origen || m1[3].trim(); almacen_destino = almacen_destino || m1[4].trim(); }
}
producto = producto ? producto.toString().trim() : null;
const producto_norm = producto ? normalize(producto) : null;
const alias_almacenes = { 'principal': 'WH', 'secundario': 'AA', 'almacen principal': 'WH', 'almacen secundario': 'AA' };
const mapAlias = (v) => { if(!v) return v; const low = v.toString().toLowerCase(); return alias_almacenes[low] || v; };
const out = { accion: input.accion || 'transferir_stock_almacen', producto: producto_norm || null, producto_raw: producto || null, cantidad: cantidad != null ? cantidad : null, almacen_origen: mapAlias(almacen_origen) || null, almacen_destino: mapAlias(almacen_destino) || null, chat_id: input.chat_id || null, uid: input.uid || null, datos_pendientes: input.datos_pendientes || null };
return [{ json: out }];

// Procesar respuesta Buscar Producto
const res = items[0].json;
// Adaptarse a search_read respuesta: puede venir como lista directa
const rows = Array.isArray(res) ? res : (res.result || res.records || []);
if(!rows || rows.length === 0) { return [{ json: { found: false } }]; }
const p = rows[0];
return [{ json: { found: true, product_id: p.id, product_name: p.name, uom_id: (p.uom_id && p.uom_id[0]) || null } }];

// Procesar creación de move
const res = items[0].json; // puede contener {result: id} o simplemente id
let moveId = null;
if (typeof res === 'object') { if (res.result) moveId = res.result; else if (Array.isArray(res)) moveId = res[0]; else if (res.id) moveId = res.id; } else if (typeof res === 'number') moveId = res;
if (!moveId) { return [{ json: { success: false, payload: res } }]; }
return [{ json: { success: true, move_id: moveId, respuesta_usuario: `📦 Transferencia creada (move id: ${moveId}).` } }];

parameters.jsCode:
const base = $('Normalizar Parte+Materiales FSM').item.json;

// Recuperar el registro (devuelto por el Code anterior a su HTTP)
const reg = $('Registrar Horas Directo FSM')?.item?.json?.registro;

// Fallback: si no hubiera reg, usar la tarea leída por HTTP
let taskId = reg?.task_id;
if (!taskId) {
  const ht = $('HTTP Buscar Tarea FSM')?.item?.json;
  const rr = ht?.json?.result ?? ht?.result ?? ht?.body?.result ?? [];
  if (Array.isArray(rr) && rr[0]) taskId = rr[0].id;
}
if (!taskId) return { skip_nota: true, motivo: 'Sin task_id' };

// Si no hay materiales, no publiques nada
const productos = Array.isArray(base.productos) ? base.productos : [];
if (!productos.length) return { skip_nota: true, motivo: 'Sin materiales' };

const lineas = (Array.isArray(productos) ? productos : [])
  .map(p => `• ${Number(p.cantidad ?? 1)} × ${String(p.nombre ?? '').trim()}`)
  .join('\n');

const texto = `Materiales usados:
${lineas}`;

// 🔎 NUEVO: obtener el partner del usuario Odoo (para que el autor sea el usuario correcto)
let authorId = null;
const respUsuario = $('Buscar Usuario Odoo')?.item?.json;
if (respUsuario) {
  const listaUsuarios = respUsuario.result || respUsuario;
  const usuario = Array.isArray(listaUsuarios) && listaUsuarios.length > 0 ? listaUsuarios[0] : null;
  if (usuario && Array.isArray(usuario.partner_id) && usuario.partner_id.length > 0) {
    authorId = usuario.partner_id[0];   // partner_id es [id, "Nombre"]
  }
}

// uid robusto (seguimos ejecutando como admin)
let uid = base.uid;
const auth = $('Autenticar Odoo')?.item;
if (auth?.json?.result) uid = auth.json.result;

// Construimos kwargs para message_post
const kwargs = { 
  body: texto, 
  message_type: 'comment', 
  subtype_xmlid: 'mail.mt_note',
};

// Si tenemos authorId, lo usamos para que la nota aparezca con ese usuario
if (authorId) {
  kwargs.author_id = authorId;    // 👈 clave para que el “autor” sea el usuario del perfil
}

const payload = {
  jsonrpc: '2.0',
  method: 'call',
  params: {
    service: 'object',
    method: 'execute_kw',
    args: [
      base.db, uid, 'admin',
      'project.task', 'message_post',
      [[taskId]],   // lista de ids
      kwargs,       // 👈 usamos el objeto con author_id (si existe)
    ],
  },
  id: Math.floor(Math.random() * 1e6),
};

return { payload };

// Leer la tarea desde ¿Existe Tarea?
const existeTarea = $('¿Existe Tarea?').item.json;
const r = existeTarea.result || [];
const tarea = Array.isArray(r) && r.length > 0 ? r[0] : null;

if (!tarea) {
  console.error('ERROR: No se encontro la tarea en nodo Existe Tarea');
  console.error('Resultado de Existe Tarea:', JSON.stringify(existeTarea, null, 2));
  throw new Error('No se encontro la tarea para registrar horas');
}

console.log('Tarea encontrada:', tarea);

const datosNorm = $('Normalizar Parte+Materiales FSM').item.json;
const preparaBusqueda = $('Preparar Buscar Tarea FSM').item.json;
const proyecto = preparaBusqueda.proyecto;

if (!proyecto) {
  throw new Error('No se encontro el proyecto');
}

// Fallbacks robustos
const DB  = datosNorm.db || preparaBusqueda.datos_verificacion?.db || 'lsanchezgoshawk-bot-main-27977821';
const UID = datosNorm.uid || preparaBusqueda.datos_verificacion?.uid || ($('Autenticar Odoo')?.item?.json?.result);

if (!UID) throw new Error('Falta uid (Autenticar Odoo)');

const hoy = new Date().toISOString().split('T')[0];

// 🔎 NUEVO: leer el usuario de Odoo buscado por email
const respUsuario = $('Buscar Usuario Odoo').item.json;
const listaUsuarios = respUsuario.result || respUsuario;
const usuario = Array.isArray(listaUsuarios) && listaUsuarios.length > 0 ? listaUsuarios[0] : null;

// id del res.users (user_id para el parte)
const userIdFromPerfil = usuario ? usuario.id : null;

// Construimos el diccionario de valores del parte
const valoresParte = {
  name: datosNorm.descripcion || `Trabajo en ${tarea.name}`,
  project_id: proyecto.id,
  task_id: tarea.id,
  unit_amount: Number(datosNorm.horas || 0),
  date: hoy,
};

// Si tenemos user_id de Odoo, lo añadimos
if (userIdFromPerfil) {
  valoresParte.user_id = userIdFromPerfil;  // <-- este será el usuario que se verá en Odoo
}

const payload = {
  jsonrpc: '2.0',
  method: 'call',
  params: {
    service: 'object',
    method: 'execute_kw',
    args: [
      DB, UID, 'admin',           // seguimos autenticando como admin
      'account.analytic.line', 'create',
      [[ valoresParte ]],         // 👈 aquí usamos valoresParte (NO cambia la lógica, solo el objeto)
    ],
  },
  id: Math.floor(Math.random() * 1e6),
};

return {
  payload,
  registro: {
    chat_id: datosNorm.chat_id,
    proyecto: proyecto.name,
    tarea: tarea.name,
    horas: Number(datosNorm.horas || 0),
    descripcion: datosNorm.descripcion || '',
    task_id: tarea.id,
    odoo_user: datosNorm.odoo_user || null,
    odoo_user_id: userIdFromPerfil || null,
  },
};

const datosNorm = $('Normalizar Parte+Materiales FSM').item.json;
const auth = $('Autenticar Odoo').item.json;

const DB   = datosNorm.db || 'lsanchezgoshawk-bot-main-27977821';
const UID  = auth.result;    // uid de admin (2)
const PASS = 'admin';        // contraseña / API key de admin

const email = datosNorm.odoo_user;   // aquí viene el email del usuario final

if (!email) {
  throw new Error('Falta odoo_user (email) en datos normalizados');
}

const payload = {
  jsonrpc: '2.0',
  method: 'call',
  params: {
    service: 'object',
    method: 'execute_kw',
    args: [
      DB, UID, PASS,
      'res.users', 'search_read',
      [[
        '|',
        ['login', '=', email],
        ['email', '=', email],
      ]],
      { fields: ['id', 'name', 'login', 'email', 'partner_id'], limit: 1 },
    ],
  },
  id: 1,
};

return { payload };

const base = $('Validar Productos FSM')?.item?.json || {};
const locs = $json;  // salida de "Fijar IDs de Ubicaciones"

// Productos que vienen del flujo FSM
const productosValidados = Array.isArray(base.productos_validados)
  ? base.productos_validados
  : [];

if (!productosValidados.length) {
  // Nada que entregar
  return {
    skip_entrega: true,
    chat_id: base.chat_id,
    total_productos: 0,
  };
}

/**
 * 1) OBTENER partner_id DESDE "HTTP Asignar Cliente a Tarea"
 */
let partner_id = null;
try {
  const clienteItems = $items('HTTP Asignar Cliente a Tarea', 0) || [];
  const clienteJson  = clienteItems[0]?.json || {};
  const rawResult    = clienteJson.result ?? clienteJson.json?.result ?? clienteJson.body?.result ?? [];

  const cliente = Array.isArray(rawResult) ? rawResult[0] : rawResult;
  if (cliente && typeof cliente.id !== 'undefined') {
    partner_id = cliente.id;          // <-- aquí cogemos el 141 de tu ejemplo
  }
} catch (e) {
  // no pasa nada, seguimos con los fallbacks de abajo
}

/**
 * 2) FALLBACKS por si en el futuro guardas también el cliente en el contexto base
 */
if (!partner_id) {
  partner_id =
    base.partner_id ||
    base.cliente_id ||
    base.customer_id ||
    null;
}

if (!partner_id) {
  // Si llegamos aquí es que no hemos encontrado el cliente
  return {
    error: true,
    motivo: 'Falta partner_id del cliente para crear la entrega',
    chat_id: base.chat_id,
  };
}

/**
 * 3) Construimos las líneas de material a entregar
 *    (ajusta nombres si en tu nodo las llamas lineas_desecho)
 */
const lineas_entrega = productosValidados.map((p) => ({
  product_id: p.product_id,
  product_name: p.product_name,
  qty: p.quantity,
  uom_id: p.uom_id || p.product_uom || null,
}));

return {
  lineas_entrega,
  productos_validados: productosValidados,
  skip_entrega: false,
  chat_id: base.chat_id,
  db: locs.db,
  uid: Number(locs.uid),
  company_id: locs.company_id,
  location_id: locs.location_id,                   // almacén (origen)
  customer_location_id: locs.customer_location_id, // Clientes (destino) → lo habrás montado en "Fijar IDs de Ubicaciones"
  picking_type_id: locs.picking_type_id,           // tipo de entrega
  partner_id,                                      // 🔹 YA VIENE DEL HTTP Asignar Cliente a Tarea
  total_productos: productosValidados.length,
};

const d = $json;

if (
  d.skip_entrega ||
  !Array.isArray(d.lineas_entrega) || !d.lineas_entrega.length ||
  !d.db || !Number.isFinite(Number(d.uid)) ||
  !d.location_id || !d.customer_location_id ||
  !d.picking_type_id || !d.partner_id
) {
  return [{
    json: {
      error: true,
      motivo: 'Faltan datos para crear la entrega',
      db: d.db,
      uid: d.uid,
      location_id: d.location_id,
      customer_location_id: d.customer_location_id,
      picking_type_id: d.picking_type_id,
      partner_id: d.partner_id,
    },
  }];
}

const items = [];
const fecha = new Date().toISOString().split('T')[0]; // fecha sin hora

for (const l of d.lineas_entrega) {
  if (!l.product_id || !l.qty) continue;

  const move = {
    name: l.product_name || 'Material',
    product_id: l.product_id,
    product_uom_qty: l.qty,
    location_id: d.location_id,
    location_dest_id: d.customer_location_id,
  };

  if (l.uom_id) {
    move.product_uom = Array.isArray(l.uom_id) ? l.uom_id[0] : l.uom_id;
  }

  const payload = {
    jsonrpc: '2.0',
    method: 'call',
    params: {
      service: 'object',
      method: 'execute_kw',
      args: [
        d.db,
        Number(d.uid),
        'admin',
        'stock.picking', 'create',
        [[{
          picking_type_id: d.picking_type_id,
          partner_id: d.partner_id,
          location_id: d.location_id,               // origen almacén
          location_dest_id: d.customer_location_id, // destino clientes
          scheduled_date: fecha,
          origin: `Parte FSM - ${fecha}`,
          move_ids_without_package: [[0, 0, move]],
        }]],
        { context: { allowed_company_ids: d.company_id ? [d.company_id] : [] } },
      ],
    },
    id: Math.floor(Math.random()*1e6),
  };

  items.push({
    json: {
      payload,
      db: d.db,
      uid: d.uid,
      chat_id: d.chat_id,
      total_productos: d.total_productos,
    },
  });
}

return items;

// Ejecutar en la rama "Done" del SplitInBatches
const reg = $items('Registrar Horas Directo FSM', 0)[0]?.json?.registro || {};
const horasIdRaw = $items('HTTP Registrar Horas Directo FSM', 0)[0]?.json;
const horasId = horasIdRaw?.result ?? horasIdRaw?.body?.result ?? null;

// total de entregas procesadas (los que generó "Crear Órdenes de Desecho")
const createdItems = $items('Crear Órdenes de Desecho', 0) || [];
const totalEntregas = createdItems.length;

// usa el chat_id del registro de horas, o el del primer item
const chatFromEntregas = createdItems[0]?.json?.chat_id;
const chatId = reg.chat_id || chatFromEntregas;

// mensaje
let texto = '✅ **¡Parte de horas registrado exitosamente!**\n\n';
texto += '📋 **Detalles del registro:**\n';
texto += `• **Proyecto:** ${reg.proyecto || '-'}\n`;
texto += `• **Tarea:** ${reg.tarea || '-'}\n`;
texto += `• **Horas:** ${reg.horas ?? 0}\n`;
texto += `• **Descripción:** ${reg.descripcion || '-'}\n`;
texto += `• **Fecha:** ${new Date().toLocaleDateString('es-ES')}\n`;
if (horasId) {
  texto += `• **ID del registro:** ${horasId}\n`;
  texto += `🔗 **Registro de horas:** [Abrir en Odoo](https://lsanchezgoshawk-n8npruebas.odoo.com/web#id=${horasId}&model=account.analytic.line&view_type=form)\n`;
}
texto += `\n📦 **Entregas de material creadas:** ${totalEntregas}\n`;
texto += '🎉 **¡Listo!** ¿Necesitas registrar más horas? 😊';

return { chat_id: chatId, texto };

// Preparar Validación Entrega (stock.picking.button_validate)

const httpCreateNode = 'HTTP Crear Desecho'; // mismo nombre de nodo HTTP

// Intentar extraer la respuesta exactamente del nodo HTTP Crear Desecho (mismo itemIndex)
const createResp = $items(httpCreateNode, 0)[$itemIndex]?.json ?? null;

let pickingId = null;
if (createResp) {
  // respuesta típica: { result: id } o { body: { result: id } }
  const candidate = createResp.result ?? createResp.body?.result ?? createResp.json?.result ?? createResp;
  if (candidate !== null && candidate !== undefined) {
    if (Array.isArray(candidate) && candidate.length) pickingId = Number(candidate[0]);
    else if (typeof candidate === 'number' || (typeof candidate === 'string' && /^\d+$/.test(candidate))) pickingId = Number(candidate);
    else if (typeof candidate === 'object' && typeof candidate.id !== 'undefined') pickingId = Number(candidate.id);
  }
}

// Fallback con el propio $json
if (!Number.isFinite(pickingId)) {
  const res = $json.json?.result ?? $json.result ?? $json.body?.result ?? null;
  if (res !== null && res !== undefined) {
    if (Array.isArray(res) && res.length) pickingId = Number(res[0]);
    else if (typeof res === 'number' || (typeof res === 'string' && /^\d+$/.test(res))) pickingId = Number(res);
    else if (typeof res === 'object' && typeof res.id !== 'undefined') pickingId = Number(res.id);
  }
}

if (!Number.isFinite(pickingId)) {
  return [{
    json: {
      error: true,
      motivo: 'No se pudo determinar pickingId desde HTTP Crear Entrega',
      createRespSnapshot: createResp ?? null,
      currentJson: $json,
    },
  }];
}

// Obtener db/uid desde el item generado por Crear Órdenes de Desecho o desde HTTP create
const originItem =
  $items('Crear Órdenes de Desecho', 0)[$itemIndex]?.json ||
  $items(httpCreateNode, 0)[$itemIndex]?.json ||
  $json ||
  {};

const db = originItem.db ?? originItem.json?.db ?? $json.db ?? null;
const uidCandidate = originItem.uid ?? originItem.json?.uid ?? $json.uid ?? null;
const uid = Number(uidCandidate);

if (!db || !Number.isFinite(uid)) {
  return [{
    json: {
      error: true,
      motivo: 'Faltan db/uid para validar',
      db,
      uid,
      pickingId,
    },
  }];
}

// Construir payload para button_validate
const payload = {
  jsonrpc: '2.0',
  method: 'call',
  params: {
    service: 'object',
    method: 'execute_kw',
    args: [
      db,
      uid,
      'admin',
      'stock.picking',
      'button_validate',
      [[ Number(pickingId) ]], // lista de ids
    ],
  },
  id: Math.floor(Math.random()*1e6),
};

return [{
  json: { payload, db, uid, picking_id: pickingId },
}];

// Preparar Message Post Entrega - construir payload para message_post (args y kwargs separados)
const res = $json.json?.result ?? $json.result ?? $json.body?.result;
const pickingId = Array.isArray(res) ? res[0] : res;

const prev = $items('Crear Órdenes de Desecho', 0)[$itemIndex] || {};
const db  = prev.json?.db || prev.db || $json.db;
const uid = Number(prev.json?.uid ?? prev.uid ?? $json.uid);

// obtener partner_id si está disponible (buscar en Buscar Usuario Odoo o en seed)
let partnerId = null;
try {
  const respUsuario = $('Buscar Usuario Odoo')?.first()?.json;
  if (respUsuario) {
    const listaUsuarios = respUsuario.result || respUsuario;
    const usuario = Array.isArray(listaUsuarios) && listaUsuarios.length > 0 ? listaUsuarios[0] : null;
    if (usuario && Array.isArray(usuario.partner_id) && usuario.partner_id.length > 0) {
      partnerId = usuario.partner_id[0];
    }
  }
} catch (e) {
  console.log('No se pudo leer Buscar Usuario Odoo:', e.message);
}

const usuarioNombre = prev.json?.usuarioNombre ?? prev.usuarioNombre ?? null;

if (!pickingId || !db || !Number.isFinite(uid)) {
  return [{
    json: {
      skip_post: true,
      motivo: 'Faltan pickingId/db/uid',
      pickingId,
      db,
      uid,
    },
  }];
}

const bodyText = usuarioNombre
  ? `Entrega registrada por: ${usuarioNombre}`
  : 'Entrega registrada';

const kwargs = {
  body: bodyText,
  message_type: 'comment',
  subtype_xmlid: 'mail.mt_note',
};
if (partnerId) kwargs.author_id = partnerId;

// args: list of ids as positional arg; kwargs separate
const payload = {
  jsonrpc: '2.0',
  method: 'call',
  params: {
    service: 'object',
    method: 'execute_kw',
    args: [
      db,
      uid,
      'admin',
      'stock.picking',
      'message_post',
      [[ Number(pickingId) ]], // positional args
      kwargs,                  // kwargs dict
    ],
  },
  id: Math.floor(Math.random() * 1e6),
};

return [{
  json: { payload, db, uid, pickingId, partnerId, usuarioNombre },
}];

const datosNorm = $('FSM ▸ Preparar Datos Completos').item.json;
const auth = $('Autenticar Odoo').item.json;

const DB   = datosNorm.db || 'lsanchezgoshawk-bot-main-27977821';
const UID  = auth.result;    // uid de admin (2)
const PASS = 'admin';        // contraseña / API key de admin

const email = datosNorm.odoo_user;   // aquí viene el email del usuario final

if (!email) {
  throw new Error('Falta odoo_user (email) en datos normalizados');
}

const payload = {
  jsonrpc: '2.0',
  method: 'call',
  params: {
    service: 'object',
    method: 'execute_kw',
    args: [
      DB, UID, PASS,
      'res.users', 'search_read',
      [[
        '|',
        ['login', '=', email],
        ['email', '=', email],
      ]],
      { fields: ['id', 'name', 'login', 'email', 'partner_id'], limit: 1 },
    ],
  },
  id: 1,
};

return { payload };

// Lee el resultado de Buscar Usuario Odoo1
