// Helper seguro para leer JSON de otro nodo (soporta .first() y .item)
function getNodeJson(name) {
  try {
    const h = $(name);
    if (typeof h.first === 'function') {
      const f = h.first();
      if (f && f.json) return f.json;
    }
    return h.item.json;
  } catch {
    return {};
  }
}

// 🔤 NUEVO: helper para normalizar texto (quita tildes, minúsculas, trim)
function normalizeText(raw) {
  if (raw === undefined || raw === null) return '';
  return String(raw)
    .normalize('NFD')                  // separa letra + tilde
    .replace(/[\u0300-\u036f]/g, '')   // elimina marcas diacríticas
    .replace(/´/g, '')                 // elimina acentos sueltos
    .toLowerCase()                     // minúsculas
    .trim();
}

